import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './ProfilePage.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft, 
  faEdit, 
  faPlus, 
  faUser, 
  faMapMarkerAlt, 
  faCalendar,
  faBaby,
  faEye,
  faEyeSlash,
  faArchive,
  faTrash,
  faPencilAlt,
  faMars,
  faVenus,
  faTimes
} from '@fortawesome/free-solid-svg-icons';
import AddChildModal from './AddChildModal';
import childrenData from '../../data/childrenData.json';

const ProfilePage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [posts, setPosts] = useState([]);
  const [drafts, setDrafts] = useState([]);
  const [archived, setArchived] = useState([]);
  const [children, setChildren] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('posts');
  const [showAddChildModal, setShowAddChildModal] = useState(false);

  useEffect(() => {
    fetchUserProfile();
    loadChildrenData();
  }, [userId]);

  const fetchUserProfile = async () => {
    try {
      setLoading(true);
      
      // Получаем данные пользователя
      const userResponse = await fetch(`http://localhost:8000/api/auth/${userId}/`);
      console.log('Ответ от сервера для пользователя:', userResponse.status);
      
      if (userResponse.ok) {
        const userData = await userResponse.json();
        console.log('Данные пользователя:', userData);
        setUser(userData.user || userData);
      } else {
        console.error('Ошибка получения пользователя:', userResponse.status);
        setError('Пользователь не найден');
      }

      // Получаем посты пользователя
      const postsResponse = await fetch(`http://localhost:8000/api/posts/user/${userId}/`);
      console.log('Ответ от сервера для постов:', postsResponse.status);
      
      if (postsResponse.ok) {
        const postsData = await postsResponse.json();
        console.log('Данные постов:', postsData);
        const allPosts = postsData.results || postsData;
        
        // Разделяем посты по статусам
        setPosts(allPosts.filter(post => post.status === 'published'));
        setDrafts(allPosts.filter(post => post.status === 'draft'));
        setArchived(allPosts.filter(post => post.status === 'archived'));
      } else {
        console.error('Ошибка получения постов:', postsResponse.status);
        setPosts([]);
      }
    } catch (error) {
      console.error('Ошибка загрузки профиля:', error);
      setError('Ошибка загрузки профиля пользователя');
    } finally {
      setLoading(false);
    }
  };

  const getAuthorInitials = (authorName) => {
    if (!authorName) return 'А';
    const names = authorName.split(' ');
    return `${names[0]?.charAt(0) || 'А'}${names[1]?.charAt(0) || ''}`.toUpperCase();
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Недавно';
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));
    
    if (diffInHours < 1) return 'Только что';
    if (diffInHours < 24) return `${diffInHours} ч назад`;
    
    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays < 7) return `${diffInDays} дн назад`;
    
    return date.toLocaleDateString('ru-RU');
  };

  const calculateAge = (birthDate) => {
    const today = new Date();
    const birth = new Date(birthDate);
    let age = today.getFullYear() - birth.getFullYear();
    const monthDiff = today.getMonth() - birth.getMonth();
    
    if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birth.getDate())) {
      age--;
    }
    
    if (age === 0) {
      const monthAge = monthDiff < 0 ? monthDiff + 12 : monthDiff;
      return `${monthAge} мес`;
    }
    
    return `${age} ${getAgeWord(age)}`;
  };

  const getAgeWord = (age) => {
    if (age >= 11 && age <= 19) return 'лет';
    const lastDigit = age % 10;
    if (lastDigit === 1) return 'год';
    if (lastDigit >= 2 && lastDigit <= 4) return 'года';
    return 'лет';
  };

  const handleAddChild = (childData) => {
    const newChild = {
      id: Date.now(),
      userId: parseInt(userId),
      name: childData.name,
      gender: childData.gender,
      birthDate: childData.birthDate,
      age: calculateAge(childData.birthDate),
      avatar: null
    };
    setChildren([...children, newChild]);
  };

  const loadChildrenData = () => {
    const userChildren = childrenData.children.filter(child => child.userId === parseInt(userId));
    setChildren(userChildren);
  };

  const handleDeleteChild = (childId) => {
    setChildren(children.filter(child => child.id !== childId));
  };

  const handleDeletePost = (postId) => {
    setPosts(posts.filter(post => post.id !== postId));
    setDrafts(drafts.filter(post => post.id !== postId));
    setArchived(archived.filter(post => post.id !== postId));
  };

  const renderPosts = (postsList, type) => {
    if (postsList.length === 0) {
      return (
        <div className="empty-state">
          <div className="empty-icon">
            <FontAwesomeIcon icon={type === 'drafts' ? faPencilAlt : type === 'archived' ? faArchive : faUser} />
          </div>
          <p className="empty-title">
            {type === 'drafts' ? 'Черновиков пока нет' : 
             type === 'archived' ? 'Архив пуст' : 'Постов пока нет'}
          </p>
          <p className="empty-description">
            {type === 'drafts' ? 'Создайте свой первый черновик' : 
             type === 'archived' ? 'Здесь будут ваши архивные записи' : 
             'Создайте свою первую запись'}
          </p>
        </div>
      );
    }

    return (
      <div className="posts-grid">
        {postsList.map((post) => (
          <div key={post.id} className="post-card">
            <div className="post-header">
              <div className="post-category">{post.category?.name || 'Общее'}</div>
              <div className="post-date">{formatDate(post.created_at)}</div>
            </div>
            <h3 className="post-title">{post.title}</h3>
            <p className="post-excerpt">
              {post.content?.length > 1000 ? `${post.content.substring(0, 1000)}...` : post.content}
            </p>
                         <div className="post-actions">
               <button className="btn-edit">
                 <FontAwesomeIcon icon={faEdit} />
                 Редактировать
               </button>
               {type === 'archived' && (
                 <button className="btn-restore">
                   <FontAwesomeIcon icon={faEye} />
                   Восстановить
                 </button>
               )}
               <button 
                 className="btn-delete"
                 onClick={() => handleDeletePost(post.id)}
               >
                 <FontAwesomeIcon icon={faTrash} />
                 Удалить
               </button>
             </div>
          </div>
        ))}
      </div>
    );
  };

  if (loading) {
    return (
      <div className="profile-loading">
        <div className="loading-spinner"></div>
        <p>Загрузка профиля...</p>
      </div>
    );
  }

  if (error || !user) {
    return (
      <div className="profile-error">
        <h2>Ошибка</h2>
        <p>{error || 'Пользователь не найден'}</p>
        <p>ID пользователя: {userId}</p>
        <button onClick={() => navigate('/')} className="btn-back">
          <FontAwesomeIcon icon={faArrowLeft} />
          Вернуться на главную
        </button>
      </div>
    );
  }

  return (
    <div className="profile-page">
      <div className="profile-container">
        {/* Кнопка назад */}
        <div className="profile-header">
          <button onClick={() => navigate('/')} className="btn-back">
            <FontAwesomeIcon icon={faArrowLeft} />
            Назад
          </button>
        </div>

        {/* Информация о пользователе */}
        <div className="user-profile-section">
          <div className="user-avatar">
            {user?.avatar ? (
              <img src={user.avatar} alt={user.first_name} />
            ) : (
              <div className="avatar-placeholder">
                {getAuthorInitials(user?.first_name || user?.username)}
              </div>
            )}
          </div>
          <div className="user-info">
            <h1 className="user-name">{user?.first_name || user?.username || 'Пользователь'}</h1>
            <div className="user-details">
              <span className="user-location">
                <FontAwesomeIcon icon={faMapMarkerAlt} />
                {user?.city || 'Не указан'}
              </span>
              <span className="user-join-date">
                <FontAwesomeIcon icon={faCalendar} />
                {user?.date_joined ? formatDate(user.date_joined) : 'Недавно'}
              </span>
            </div>
            <div className="user-username">@{user?.username}</div>
          </div>
                     <div className="user-actions">
             <button className="btn-edit-profile">
               <FontAwesomeIcon icon={faEdit} />
               Редактировать профиль
             </button>
             <button 
               className="btn-add-child"
               onClick={() => setShowAddChildModal(true)}
             >
               <FontAwesomeIcon icon={faBaby} />
               Добавить ребенка
             </button>
           </div>
        </div>

                 {/* Дети пользователя */}
         {children.length > 0 && (
           <div className="user-children-section">
             <h3 className="children-title">
               <FontAwesomeIcon icon={faBaby} />
               Мои дети
             </h3>
             <div className="children-grid">
                               {children.map((child) => (
                  <div key={child.id} className="child-card">
                    <div className="child-avatar">
                      <FontAwesomeIcon 
                        icon={child.gender === 'male' ? faMars : faVenus} 
                        className={`gender-icon ${child.gender}`}
                      />
                    </div>
                    <div className="child-info">
                      <div className="child-name">{child.name}</div>
                      <div className="child-age">{child.age}</div>
                    </div>
                    <button 
                      className="child-delete-btn"
                      onClick={() => handleDeleteChild(child.id)}
                      title="Удалить ребенка"
                    >
                      <FontAwesomeIcon icon={faTimes} />
                    </button>
                  </div>
                ))}
             </div>
           </div>
         )}

         {/* Статистика */}
         <div className="user-stats">
           <div className="stat-item">
             <div className="stat-number">{posts.length}</div>
             <div className="stat-label">постов</div>
           </div>
           <div className="stat-item">
             <div className="stat-number">{children.length}</div>
             <div className="stat-label">детей</div>
           </div>
           <div className="stat-item">
             <div className="stat-number">0</div>
             <div className="stat-label">подписчиков</div>
           </div>
           <div className="stat-item">
             <div className="stat-number">0</div>
             <div className="stat-label">подписок</div>
           </div>
         </div>

        {/* Вкладки */}
        <div className="profile-tabs">
          <button 
            className={`tab ${activeTab === 'posts' ? 'active' : ''}`}
            onClick={() => setActiveTab('posts')}
          >
            <FontAwesomeIcon icon={faUser} />
            Записи ({posts.length})
          </button>
          <button 
            className={`tab ${activeTab === 'drafts' ? 'active' : ''}`}
            onClick={() => setActiveTab('drafts')}
          >
            <FontAwesomeIcon icon={faPencilAlt} />
            Черновики ({drafts.length})
          </button>
          
          <button 
            className={`tab ${activeTab === 'archive' ? 'active' : ''}`}
            onClick={() => setActiveTab('archive')}
          >
            <FontAwesomeIcon icon={faArchive} />
            Архив ({archived.length})
          </button>
        </div>

        {/* Контент вкладок */}
        <div className="tab-content">
          {activeTab === 'posts' && renderPosts(posts, 'posts')}
          {activeTab === 'drafts' && renderPosts(drafts, 'drafts')}
          
          {activeTab === 'archive' && renderPosts(archived, 'archived')}
                 </div>
       </div>

       {/* Модальное окно добавления ребенка */}
       <AddChildModal
         isOpen={showAddChildModal}
         onClose={() => setShowAddChildModal(false)}
         onAddChild={handleAddChild}
       />
     </div>
   );
 };

export default ProfilePage;
