// Утилита для работы с пользователями

// Тестовая функция для получения моковых данных пользователя
export const getMockUserInfo = (userId) => {
  // Проверяем, что userId существует и является числом
  if (!userId || isNaN(Number(userId))) {
    userId = 1;
  }
  
  // Создаем более реалистичные моковые данные
  const mockNames = [
    'Анна', 'Мария', 'Елена', 'Ольга', 'Татьяна', 'Наталья', 'Ирина', 'Светлана',
    'Александр', 'Дмитрий', 'Сергей', 'Андрей', 'Алексей', 'Максим', 'Владимир', 'Иван'
  ];
  
  const mockCities = [
    'Москва', 'Санкт-Петербург', 'Новосибирск', 'Екатеринбург', 'Казань', 'Нижний Новгород',
    'Челябинск', 'Самара', 'Ростов-на-Дону', 'Уфа', 'Красноярск', 'Пермь'
  ];
  
  // Генерируем имя на основе ID для консистентности
  const nameIndex = Number(userId) % mockNames.length;
  const cityIndex = Number(userId) % mockCities.length;
  
  const mockUser = {
    id: userId,
    name: mockNames[nameIndex],
    username: `user_${userId}`,
    avatar: null,
    city: mockCities[cityIndex],
    isOnline: false
  };
  
  return mockUser;
};

// Функция для синхронизации данных между страницами
export const syncUserDataFromProfile = (userId, profileData) => {
  try {
    if (profileData && profileData.user) {
      const user = profileData.user;
      const userInfo = {
        id: userId,
        name: user.first_name || user.username || `Пользователь ${userId}`,
        avatar: user.avatar || null,
        city: user.city || 'Не указан'
      };
      
      // Сохраняем в localStorage
      saveUserInfoToStorage(userInfo);
      
      return userInfo;
    }
  } catch (error) {
    console.error('Ошибка при синхронизации данных пользователя:', error);
  }
  return null;
};

// Функция для получения информации о пользователе из localStorage (если есть)
export const getUserInfoFromStorage = (userId) => {
  try {
    // Пытаемся найти информацию о пользователе в localStorage
    const storedUsers = localStorage.getItem('userProfiles');
    if (storedUsers) {
      const users = JSON.parse(storedUsers);
      const user = users.find(u => u.id == userId);
      if (user) {
        return {
          id: user.id,
          name: user.first_name || user.username || `Пользователь ${userId}`,
          avatar: user.avatar || null,
          city: user.city || 'Не указан'
        };
      }
    }
  } catch (error) {
    console.error('Ошибка при получении информации из localStorage:', error);
  }
  return null;
};

// Функция для сохранения информации о пользователе в localStorage
export const saveUserInfoToStorage = (userInfo) => {
  try {
    const storedUsers = localStorage.getItem('userProfiles') || '{}';
    const users = JSON.parse(storedUsers);
    users[userInfo.id] = userInfo;
    localStorage.setItem('userProfiles', JSON.stringify(users));
  } catch (error) {
    console.error('Ошибка при сохранении в localStorage:', error);
  }
};

// Получить информацию о пользователе по ID
export const getUserInfo = async (userId) => {
  try {
    // Сначала проверяем localStorage
    const storedUserInfo = getUserInfoFromStorage(userId);
    if (storedUserInfo && storedUserInfo.name && storedUserInfo.name !== `Пользователь ${userId}`) {
      return storedUserInfo;
    }
    
    // Делаем API-запрос для получения реальных данных
    // Используем тот же endpoint, что и в UserProfilePage
    const response = await fetch(`http://localhost:8000/api/users/profile-with-posts/${userId}/`, {
      headers: {
        'Content-Type': 'application/json',
      }
    });

    if (response.ok) {
      const data = await response.json();
      
      if (data.success && data.user) {
        const user = data.user;
        const userInfo = {
          id: userId,
          name: user.first_name || user.username || `Пользователь ${userId}`,
          avatar: user.avatar || null,
          city: user.city || 'Не указан'
        };
        
        // Сохраняем полученную информацию в localStorage
        saveUserInfoToStorage(userInfo);
        
        return userInfo;
      } else if (data.user) {
        // Если нет поля success, но есть user
        const user = data.user;
        const userInfo = {
          id: userId,
          name: user.first_name || user.username || `Пользователь ${userId}`,
          avatar: user.avatar || null,
          city: user.city || 'Не указан'
        };
        
        saveUserInfoToStorage(userInfo);
        
        return userInfo;
      }
    }
    
    // Если API не сработал, возвращаем базовую информацию
    const basicUserInfo = {
      id: userId,
      name: `Пользователь ${userId}`,
      avatar: null,
      city: 'Не указан'
    };
    
    return basicUserInfo;
  } catch (error) {
    console.error('Ошибка при получении информации о пользователе:', error);
    const basicUserInfo = {
      id: userId,
      name: `Пользователь ${userId}`,
      avatar: null,
      city: 'Не указан'
    };
    return basicUserInfo;
  }
};

// Получить инициалы пользователя
export const getUserInitials = (userName) => {
  if (!userName) return 'А';
  const names = userName.split(' ');
  return `${names[0]?.charAt(0) || 'А'}${names[1]?.charAt(0) || ''}`.toUpperCase();
};

// Форматировать дату
export const formatDate = (dateString) => {
  if (!dateString) return 'Недавно';
  const date = new Date(dateString);
  const now = new Date();
  const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));
  
  if (diffInHours < 1) return 'Только что';
  if (diffInHours < 24) return `${diffInHours} ч назад`;
  
  const diffInDays = Math.floor(diffInHours / 24);
  if (diffInDays < 7) return `${diffInDays} дн назад`;
  
  return date.toLocaleDateString('ru-RU');
};
