import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faImage, faPaperPlane, faTimes } from '@fortawesome/free-solid-svg-icons';
import './ChatModal.css';

const ChatModal = ({ isOpen, onClose, userId, userName }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (isOpen && userId) {
      loadUserData();
      loadChatMessages();
    }
  }, [isOpen, userId]);

  const loadUserData = async () => {
    try {
      const token = localStorage.getItem('access_token');
      const response = await fetch(`http://localhost:8000/users/${userId}/`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success && data.user) {
          setUser(data.user);
        }
      }
    } catch (error) {
      console.error('Ошибка загрузки данных пользователя:', error);
    }
  };

  const loadChatMessages = async () => {
    try {
      const token = localStorage.getItem('access_token');
      const response = await fetch(`http://localhost:8000/users/async/chats/${userId}/messages/`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success && data.messages) {
          setMessages(data.messages);
        }
      }
    } catch (error) {
      console.error('Ошибка загрузки сообщений:', error);
    }
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;

    setLoading(true);
    try {
      const token = localStorage.getItem('access_token');
      const response = await fetch(`http://localhost:8000/users/chats/${userId}/messages/`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          content: newMessage,
          message_type: 'text'
        }),
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success && data.message) {
          setMessages(prev => [...prev, data.message]);
          setNewMessage('');
        }
      }
    } catch (error) {
      console.error('Ошибка отправки сообщения:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleFileSelect = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setLoading(true);
    try {
      const token = localStorage.getItem('access_token');
      const formData = new FormData();
      formData.append('file', file);
      formData.append('message_type', 'file');

      const response = await fetch(`http://localhost:8000/users/chats/${userId}/messages/`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
        body: formData,
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success && data.message) {
          setMessages(prev => [...prev, data.message]);
        }
      }
    } catch (error) {
      console.error('Ошибка отправки файла:', error);
    } finally {
      setLoading(false);
    }
  };

  if (!isOpen) return null;

  const displayName = user ? 
    (user.first_name && user.last_name ? `${user.first_name} ${user.last_name}` : 
     user.first_name || user.username || 'Пользователь') : 
    userName || 'Пользователь';

  return (
    <div className="chat-modal-overlay" onClick={onClose}>
      <div className="chat-modal" onClick={(e) => e.stopPropagation()}>
        {/* Заголовок модального окна */}
        <div className="chat-modal-header">
          <div className="chat-user-info">
            <h3 className="user-name">{displayName}</h3>
            <p className="user-status">Была онлайн: 3 м</p>
          </div>
          <button className="close-button" onClick={onClose}>
            <FontAwesomeIcon icon={faTimes} />
          </button>
        </div>

        {/* Контейнер сообщений */}
        <div className="chat-modal-messages">
          {messages.length === 0 ? (
            <div className="empty-messages">
              <p className="empty-title">Сообщений нет.</p>
              <p className="empty-subtitle">Собеседник не видит эту переписку.</p>
            </div>
          ) : (
            <div className="messages-list">
              {messages.map((message) => (
                <div key={message.id} className={`message ${message.sender === localStorage.getItem('user_id') ? 'my-message' : 'incoming-message'}`}>
                  <div className="message-content">
                    {message.message_type === 'file' && message.file ? (
                      <div className="file-message">
                        <img src={message.file} alt="Файл" className="message-file" />
                      </div>
                    ) : (
                      <p>{message.content}</p>
                    )}
                    <span className="message-time">
                      {new Date(message.created_at).toLocaleTimeString('ru-RU', {
                        hour: '2-digit',
                        minute: '2-digit'
                      })}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Поле ввода сообщения */}
        <div className="chat-modal-input">
          <div className="message-input-wrapper">
            <input
              type="file"
              accept="image/*"
              style={{ display: 'none' }}
              onChange={handleFileSelect}
              id="file-input"
            />
            <button className="attach-button" onClick={() => document.getElementById('file-input').click()}>
              <FontAwesomeIcon icon={faImage} />
            </button>
            <input
              type="text"
              className="message-input"
              placeholder="Ваше сообщение"
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSendMessage(e)}
            />
            <button 
              className="send-button" 
              onClick={handleSendMessage}
              disabled={!newMessage.trim() || loading}
            >
              <FontAwesomeIcon icon={faPaperPlane} />
              <span>Отправить</span>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChatModal;
