import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faComment, 
  faEye, 
  faCalendar,
  faTag,
  faArrowLeft,
  faMapMarkerAlt,
  faShare
} from '@fortawesome/free-solid-svg-icons';
import './PostDetail.css';

const PostDetail = () => {
  const { slug } = useParams();
  const navigate = useNavigate();
  const [post, setPost] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newComment, setNewComment] = useState('');
  const [replyTo, setReplyTo] = useState(null);
  const [replyText, setReplyText] = useState('');
  const [submitting, setSubmitting] = useState(false);


  useEffect(() => {
    fetchPost();
  }, [slug]);

    const fetchPost = async () => {
    try {
      setLoading(true);
      const response = await fetch(`http://localhost:8000/api/posts/${slug}/`);
      
      if (response.ok) {
        const data = await response.json();
        
        // Временно добавляем тестовые комментарии
        const testComments = [
          {
            id: 1,
            content: "Прикладывайте ребенка чаще, может, и придет, тут только один совет",
            author: {
              id: 1,
              first_name: "Мать",
              last_name: "булочки",
              username: "mother_buns",
              avatar: null,
              city: "Курск"
            },
            created_at: "2024-01-15T10:30:00Z",
            parent: null,
            is_approved: true
          },
          {
            id: 2,
            content: "Согласна с вами! Это действительно помогает",
            author: {
              id: 2,
              first_name: "Анна",
              last_name: "Петрова",
              username: "anna_p",
              avatar: null,
              city: "Москва"
            },
            created_at: "2024-01-15T11:15:00Z",
            parent: 1,
            is_approved: true
          },
          {
            id: 3,
            content: "Попробуйте разные позы для кормления, это тоже важно",
            author: {
              id: 3,
              first_name: "Елена",
              last_name: "Сидорова",
              username: "elena_s",
              avatar: null,
              city: "Санкт-Петербург"
            },
            created_at: "2024-01-15T09:45:00Z",
            parent: null,
            is_approved: true
          }
        ];
        
        data.comments = testComments;
        setPost(data);
      } else {
        setError('Пост не найден');
      }
    } catch (error) {
      console.error('Ошибка загрузки поста:', error);
      setError('Ошибка загрузки поста');
    } finally {
      setLoading(false);
    }
  };



  const formatDate = (dateString) => {
    try {
      if (!dateString) {
        return 'Недавно';
      }
      
      let date;
      
      // Проверяем, если дата в формате "DD.MM.YYYY"
      if (typeof dateString === 'string' && dateString.includes('.')) {
        const parts = dateString.split('.');
        if (parts.length === 3) {
          const [day, month, year] = parts;
          date = new Date(`${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`);
        } else {
          date = new Date(dateString);
        }
      } else {
        date = new Date(dateString);
      }
      
      if (isNaN(date.getTime())) {
        return 'Недавно';
      }
      
      const now = new Date();
      const diffInMinutes = Math.floor((now - date) / (1000 * 60));
      const diffInHours = Math.floor(diffInMinutes / 60);
      const diffInDays = Math.floor(diffInHours / 24);
      
      if (diffInMinutes < 1) {
        return 'Только что';
      } else if (diffInMinutes < 60) {
        return `${diffInMinutes} мин${diffInMinutes === 1 ? 'у' : diffInMinutes < 5 ? 'ы' : ''} назад`;
      } else if (diffInHours < 24) {
        return `${diffInHours} час${diffInHours === 1 ? '' : diffInHours < 5 ? 'а' : 'ов'} назад`;
      } else if (diffInDays < 7) {
        return `${diffInDays} дн${diffInDays === 1 ? 'ь' : diffInDays < 5 ? 'я' : 'ей'} назад`;
      } else {
        return date.toLocaleDateString('ru-RU');
      }
    } catch (error) {
      return 'Недавно';
    }
  };

  const getAuthorInitials = (author) => {
    if (!author) return 'А';
    const firstName = author.first_name || '';
    const lastName = author.last_name || '';
    return `${firstName.charAt(0)}${lastName.charAt(0)}`.toUpperCase() || 'А';
  };

  const getAuthorName = (author) => {
    if (!author) return 'Автор';
    const firstName = author.first_name || '';
    const lastName = author.last_name || '';
    return `${firstName} ${lastName}`.trim() || author.username || 'Автор';
  };

  const handleSubmitComment = async (e) => {
    e.preventDefault();
    if (!newComment.trim()) return;

    try {
      setSubmitting(true);
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      const response = await fetch(`http://localhost:8000/api/posts/${slug}/comments/`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          content: newComment,
          parent: null
        })
      });

      if (response.ok) {
        const comment = await response.json();
        setPost(prev => ({
          ...prev,
          comments: [...(prev.comments || []), comment]
        }));
        setNewComment('');
      }
    } catch (error) {
      console.error('Ошибка при отправке комментария:', error);
    } finally {
      setSubmitting(false);
    }
  };

  const handleSubmitReply = async (e, parentId) => {
    e.preventDefault();
    if (!replyText.trim()) return;

    try {
      setSubmitting(true);
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      const response = await fetch(`http://localhost:8000/api/posts/${slug}/comments/`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          content: replyText,
          parent: parentId
        })
      });

      if (response.ok) {
        const reply = await response.json();
        setPost(prev => ({
          ...prev,
          comments: [...(prev.comments || []), reply]
        }));
        setReplyText('');
        setReplyTo(null);
      }
    } catch (error) {
      console.error('Ошибка при отправке ответа:', error);
    } finally {
      setSubmitting(false);
    }
  };

  const handleReplyClick = (commentId) => {
    setReplyTo(replyTo === commentId ? null : commentId);
    setReplyText('');
  };

  const isLoggedIn = () => {
    return !!(localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken'));
  };

  if (loading) {
    return (
      <div className="post-detail-loading">
        <div className="loading-spinner"></div>
        <p>Загрузка поста...</p>
      </div>
    );
  }

  if (error || !post) {
    return (
      <div className="post-detail-error">
        <h2>Ошибка</h2>
        <p>{error || 'Пост не найден'}</p>
        <button onClick={() => navigate('/')} className="btn-back">
          <FontAwesomeIcon icon={faArrowLeft} />
          Вернуться на главную
        </button>
      </div>
    );
  }

  return (
    <div className="post-detail-container">
      {/* Кнопка назад */}
      <div className="post-detail-header">
        <button onClick={() => navigate('/')} className="btn-back">
          <FontAwesomeIcon icon={faArrowLeft} />
          Назад
        </button>
      </div>

      {/* Основной контент */}
      <div className="post-detail-content">
        <article className="post-article">
                     {/* Информация об авторе */}
           <header className="post-header">
             <div className="post-author-section">
              <div className="author-info">
                <div className="author-avatar">
                  {post.author?.avatar ? (
                    <img 
                      src={post.author.avatar} 
                      alt={getAuthorName(post.author)}
                      onError={(e) => {
                        e.target.style.display = 'none';
                        e.target.nextSibling.style.display = 'flex';
                      }}
                    />
                  ) : null}
                  <div className="author-initials" style={{ 
                    display: post.author?.avatar ? 'none' : 'flex' 
                  }}>
                    {getAuthorInitials(post.author)}
                  </div>
                </div>
                
                <div className="author-details">
                  <div className="author-name">{getAuthorName(post.author)}</div>
                  <div className="author-meta">
                    <span className="author-location">
                      <FontAwesomeIcon icon={faMapMarkerAlt} />
                      {post.author?.city || 'Москва'}
                    </span>
                    <span className="post-date">
                      <FontAwesomeIcon icon={faCalendar} />
                      {formatDate(post.published_at || post.created_at)}
                    </span>
                  </div>
                </div>
              </div>
              
                             {/* Действия */}
               <div className="post-actions">
                 <button className="action-btn share-btn">
                   <FontAwesomeIcon icon={faShare} />
                 </button>
               </div>
            </div>



                         {/* Статистика комментариев */}
             <div className="post-stats">
               <div className="stat-item">
                 <FontAwesomeIcon icon={faComment} />
                 <span>{post.comments_count || 0} комментариев</span>
               </div>
             </div>
          </header>

          

                     {/* Заголовок статьи */}
           <div className="post-title-section">
             <h1 className="post-title">{post.title}</h1>
           </div>

           {/* Основной контент */}
           <div className="post-body">
             <div className="post-content" dangerouslySetInnerHTML={{ __html: post.content }} />
           </div>

          {/* SEO информация (только для автора) */}
          {post.author?.id === (JSON.parse(localStorage.getItem('userData') || '{}').id) && (
            <div className="post-seo-info">
              <h3>SEO информация</h3>
              <div className="seo-fields">
                <div className="seo-field">
                  <label>Meta Title:</label>
                  <p>{post.meta_title}</p>
                </div>
                
                <div className="seo-field">
                  <label>Meta Description:</label>
                  <p>{post.meta_description}</p>
                </div>
                
                <div className="seo-field">
                  <label>Meta Keywords:</label>
                  <p>{post.meta_keywords}</p>
                </div>
              </div>
            </div>
          )}

                     {/* Комментарии */}
           {post.comments_enabled === 'enabled' && (
             <div className="post-comments">
               <h3>Комментарии ({post.comments?.length || 0})</h3>
               
               {/* Форма добавления комментария */}
               {isLoggedIn() && (
                 <form onSubmit={handleSubmitComment} className="comment-form">
                   <div className="comment-input-wrapper">
                     <textarea
                       value={newComment}
                       onChange={(e) => setNewComment(e.target.value)}
                       placeholder="Написать комментарий..."
                       className="comment-input"
                       rows="3"
                       disabled={submitting}
                     />
                   </div>
                   <div className="comment-form-actions">
                     <button 
                       type="submit" 
                       className="btn-submit-comment"
                       disabled={!newComment.trim() || submitting}
                     >
                       {submitting ? 'Отправка...' : 'Отправить'}
                     </button>
                   </div>
                 </form>
               )}

               {/* Список комментариев */}
               {post.comments && post.comments.length > 0 ? (
                 <div className="comments-list">
                   {post.comments
                     .filter(comment => !comment.parent) // Только основные комментарии
                     .map(comment => (
                       <div key={comment.id} className="comment">
                         <div className="comment-author">
                           <div className="comment-avatar">
                             {comment.author?.avatar ? (
                               <img 
                                 src={comment.author.avatar} 
                                 alt={`${comment.author.first_name} ${comment.author.last_name}`}
                               />
                             ) : (
                               <div className="comment-initials">
                                 {`${comment.author?.first_name?.charAt(0) || 'А'}${comment.author?.last_name?.charAt(0) || ''}`}
                               </div>
                             )}
                           </div>
                                                       <div className="comment-info">
                              <div className="comment-author-name">
                                {comment.author?.first_name} {comment.author?.last_name}
                              </div>
                              <div className="comment-author-location">
                                <FontAwesomeIcon icon={faMapMarkerAlt} />
                                {comment.author?.city || 'Москва'}
                              </div>
                              <div className="comment-date">
                                {formatDate(comment.created_at)}
                              </div>
                            </div>
                         </div>
                         <div className="comment-content">{comment.content}</div>
                         
                         {/* Кнопка ответа */}
                         {isLoggedIn() && (
                           <div className="comment-actions">
                             <button 
                               onClick={() => handleReplyClick(comment.id)}
                               className="btn-reply"
                             >
                               Ответить
                             </button>
                           </div>
                         )}

                         {/* Форма ответа */}
                         {replyTo === comment.id && (
                           <form onSubmit={(e) => handleSubmitReply(e, comment.id)} className="reply-form">
                             <div className="comment-input-wrapper">
                               <textarea
                                 value={replyText}
                                 onChange={(e) => setReplyText(e.target.value)}
                                 placeholder="Написать ответ..."
                                 className="comment-input"
                                 rows="2"
                                 disabled={submitting}
                               />
                             </div>
                             <div className="comment-form-actions">
                               <button 
                                 type="submit" 
                                 className="btn-submit-comment"
                                 disabled={!replyText.trim() || submitting}
                               >
                                 {submitting ? 'Отправка...' : 'Отправить'}
                               </button>
                               <button 
                                 type="button" 
                                 onClick={() => setReplyTo(null)}
                                 className="btn-cancel"
                               >
                                 Отмена
                               </button>
                             </div>
                           </form>
                         )}

                         {/* Ответы на комментарий */}
                         {post.comments
                           .filter(reply => reply.parent === comment.id)
                           .map(reply => (
                             <div key={reply.id} className="comment reply">
                               <div className="comment-author">
                                 <div className="comment-avatar">
                                   {reply.author?.avatar ? (
                                     <img 
                                       src={reply.author.avatar} 
                                       alt={`${reply.author.first_name} ${reply.author.last_name}`}
                                     />
                                   ) : (
                                     <div className="comment-initials">
                                       {`${reply.author?.first_name?.charAt(0) || 'А'}${reply.author?.last_name?.charAt(0) || ''}`}
                                     </div>
                                   )}
                                 </div>
                                                                   <div className="comment-info">
                                    <div className="comment-author-name">
                                      {reply.author?.first_name} {reply.author?.last_name}
                                    </div>
                                    <div className="comment-author-location">
                                      <FontAwesomeIcon icon={faMapMarkerAlt} />
                                      {reply.author?.city || 'Москва'}
                                    </div>
                                    <div className="comment-date">
                                      {formatDate(reply.created_at)}
                                    </div>
                                  </div>
                               </div>
                               <div className="comment-content">{reply.content}</div>
                             </div>
                           ))}
                       </div>
                     ))}
                 </div>
               ) : (
                 <p className="no-comments">Пока нет комментариев. Будьте первым!</p>
               )}
             </div>
           )}
        </article>

                 {/* Боковая панель */}
         <aside className="post-sidebar">
           <div className="sidebar-section">
             <h3>Информация о посте</h3>
             <div className="post-info">
               <div className="info-item">
                 <span className="info-label">Статус:</span>
                 <span className={`info-value status-${post.status}`}>
                   {post.status === 'published' ? 'Опубликован' : 'Черновик'}
                 </span>
               </div>
               <div className="info-item">
                 <span className="info-label">Комментарии:</span>
                 <span className="info-value">
                   {post.comments_enabled === 'enabled' ? 'Разрешены' : 'Запрещены'}
                 </span>
               </div>
               <div className="info-item">
                 <span className="info-label">Дата создания:</span>
                 <span className="info-value">
                   {new Date(post.created_at).toLocaleDateString('ru-RU')}
                 </span>
               </div>
               {post.published_at && (
                 <div className="info-item">
                   <span className="info-label">Дата публикации:</span>
                   <span className="info-value">
                     {new Date(post.published_at).toLocaleDateString('ru-RU')}
                   </span>
                 </div>
               )}
             </div>
           </div>
           
                       {/* Краткое описание */}
            {post.short_description && (
              <div className="sidebar-section">
                <h3>Краткое описание</h3>
                <div className="post-short-description">
                  <p>{post.short_description}</p>
                </div>
              </div>
            )}
            
            {/* Дополнительная информация */}
            <div className="sidebar-section">
              <div className="post-additional-info">
                {/* Категория */}
                <div className="post-category-badge">
                  {post.category?.name?.toUpperCase() || 'ОБЩЕЕ'}
                </div>
                
                {/* Просмотры */}
                <div className="post-views-info">
                  <FontAwesomeIcon icon={faEye} />
                  <span>{post.views_count || 0} просмотров</span>
                </div>
              </div>
            </div>
         </aside>
      </div>
    </div>
  );
};

export default PostDetail;
