import React, { useState, useRef } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronLeft, faChevronRight } from '@fortawesome/free-solid-svg-icons';
import './FeaturedCards.css';

const FeaturedCards = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const sliderRef = useRef(null);

  const featuredCards = [
    {
      id: 1,
      title: 'Слушаем и растём: аудиоконтент для малышей и школьников',
      color: 'purple',
      illustration: 'audio-content'
    },
    {
      id: 2,
      title: 'Секреты спокойного сна: как уложить малыша без слез',
      color: 'pink',
      illustration: 'sleeping-baby'
    },
    {
      id: 3,
      title: 'Первый год жизни: важные этапы развития ребенка',
      color: 'blue',
      illustration: 'baby-development'
    }
  ];

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % featuredCards.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + featuredCards.length) % featuredCards.length);
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  return (
    <div className="featured-content-wrapper">
      <div className="featured-content">
        <div className="slider-container" ref={sliderRef}>
          <div 
            className="slider-track"
            style={{ transform: `translateX(-${currentSlide * 100}%)` }}
          >
            {featuredCards.map(card => (
              <div key={card.id} className="slider-slide">
                <Link to={`/post/${card.id}`} className="featured-card-link">
                  <div className={`featured-card ${card.color}`}>
                    <div className="card-content">
                      <h3>{card.title}</h3>
                    </div>
                    <div className="card-image">
                      <div className={`illustration ${card.illustration}`}></div>
                    </div>
                  </div>
                </Link>
              </div>
            ))}
          </div>
        </div>

        {/* Навигационные кнопки */}
        <button className="slider-btn prev-btn" onClick={prevSlide}>
          <FontAwesomeIcon icon={faChevronLeft} />
        </button>
        <button className="slider-btn next-btn" onClick={nextSlide}>
          <FontAwesomeIcon icon={faChevronRight} />
        </button>

        {/* Индикаторы слайдов */}
        <div className="slider-indicators">
          {featuredCards.map((_, index) => (
            <button
              key={index}
              className={`indicator ${index === currentSlide ? 'active' : ''}`}
              onClick={() => goToSlide(index)}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default FeaturedCards;
