import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Header.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faPen, faUser, faUsers, faSignOutAlt, faChevronDown, faChevronRight, faMoon } from '@fortawesome/free-solid-svg-icons';
import { isAuthenticated, getUserData, removeTokens } from '../../utils/auth';


const Header = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userData, setUserData] = useState(null);
  const [showDropdown, setShowDropdown] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [showProfileModal, setShowProfileModal] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Проверяем авторизацию при загрузке компонента
    const checkAuth = () => {
      const authenticated = isAuthenticated();
      const user = getUserData();

      setIsLoggedIn(authenticated);
      setUserData(user);
    };

    checkAuth();

    // Слушаем изменения в localStorage
    const handleStorageChange = () => {
      checkAuth();
    };

    window.addEventListener('storage', handleStorageChange);

    // Также проверяем при фокусе на окне (когда пользователь возвращается на вкладку)
    const handleFocus = () => {
      checkAuth();
    };

    window.addEventListener('focus', handleFocus);

    return () => {
      window.removeEventListener('storage', handleStorageChange);
      window.removeEventListener('focus', handleFocus);
    };
  }, []);

  const handleLogout = () => {
    removeTokens();
    localStorage.removeItem('userData');
    setIsLoggedIn(false);
    setUserData(null);
    navigate('/');
  };

  const handleProfileClick = () => {
    setShowProfileModal(true);
    setShowDropdown(false);
  };

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
    // Здесь можно добавить логику для применения тёмной темы
    document.body.classList.toggle('dark-mode');
  };

  const handleClickOutside = (event) => {
    if (!event.target.closest('.user-info') && !event.target.closest('.dropdown-toggle')) {
      setShowDropdown(false);
    }
  };

  useEffect(() => {
    if (showDropdown) {
      document.addEventListener('click', handleClickOutside);
    }
    return () => {
      document.removeEventListener('click', handleClickOutside);
    };
  }, [showDropdown]);

  return (
    <header className="header">
      <div className="header-container">
        <div className="logo">
          <h1>MomsBlog</h1>
          <span className="logo-subtitle">Сообщество мам</span>
        </div>
        <div className="search-bar">
          <FontAwesomeIcon icon={faSearch} className="search-icon" />
          <input type="text" placeholder="Поиск постов, советов, обсуждений..." />
        </div>
        <div className="header-actions">
                                <button className="btn-write" onClick={() => navigate('/create-post')}>
                        <FontAwesomeIcon icon={faPen} />
                        Написать пост
                      </button>

          {isLoggedIn ? (
            <div className="user-info">
              <div className="user-profile" onClick={handleProfileClick}>
                <FontAwesomeIcon icon={faUser} />
                <span>{userData?.first_name || userData?.username || 'Профиль'}</span>
              </div>
              <div className="dropdown-toggle" onClick={toggleDropdown}>
                <FontAwesomeIcon icon={faChevronDown} className={`dropdown-arrow ${showDropdown ? 'rotated' : ''}`} />
              </div>

              {showDropdown && (
                <div className="dropdown-menu">
                  <div className="dropdown-header" onClick={handleProfileClick}>
                    <div className="dropdown-avatar">
                      <span>BB</span>
                    </div>
                    <div className="dropdown-user-info">
                      <div className="dropdown-label">Мой профиль</div>
                      <div className="dropdown-username">{userData?.first_name || userData?.username || 'Пользователь'}</div>
                    </div>
                    <FontAwesomeIcon icon={faChevronRight} className="dropdown-arrow-right" />
                  </div>

                  <div className="dropdown-item" onClick={() => navigate('/friends')}>
                    <FontAwesomeIcon icon={faUsers} />
                    <span>Друзья</span>
                  </div>

                  <div className="dropdown-item" onClick={toggleDarkMode}>
                    <FontAwesomeIcon icon={faMoon} />
                    <span>Тёмная тема</span>
                  </div>

                  <div className="dropdown-item" onClick={handleLogout}>
                    <FontAwesomeIcon icon={faSignOutAlt} />
                    <span>Выйти</span>
                  </div>
                </div>
              )}
            </div>
          ) : (
            <Link to="/login" className="login-link">
              <FontAwesomeIcon icon={faUser} />
              Войти
            </Link>
          )}
        </div>
      </div>

      {/* Модальное окно профиля */}
      <ProfileModal 
        isOpen={showProfileModal}
        onClose={() => setShowProfileModal(false)}
        userData={userData}
      />
    </header>
  );
};

export default Header;
