import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft, 
  faSearch, 
  faMapMarkerAlt,
  faCalendar,
  faUserPlus,
  faUserMinus,
  faBell,
  faComments,
  faUser
} from '@fortawesome/free-solid-svg-icons';
import './FriendsPage.css';

const FriendsPage = () => {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [searchLoading, setSearchLoading] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [notificationsLoading, setNotificationsLoading] = useState(false);



  const isLoggedIn = () => {
    return !!(localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken'));
  };

  const getAuthHeaders = () => {
    const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
    return {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${token}`
    };
  };

  const searchUsers = async () => {
    if (!searchQuery.trim()) return;
    
    setSearchLoading(true);
    try {
      const response = await fetch(`http://localhost:8000/api/users/search/?q=${encodeURIComponent(searchQuery)}`, {
        headers: getAuthHeaders()
      });
      
      if (response.ok) {
        const data = await response.json();
        setSearchResults(data.users || []);
      } else {
        console.error('Ошибка поиска пользователей');
        setSearchResults([]);
      }
    } catch (error) {
      console.error('Ошибка поиска:', error);
      setSearchResults([]);
    } finally {
      setSearchLoading(false);
    }
  };



  const handleFollow = async (userId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/users/follow/${userId}/`, {
        method: 'POST',
        headers: getAuthHeaders()
      });
      
      if (response.ok) {
        // Обновляем списки
        if (activeTab === 'search') {
          searchUsers();
        } else if (activeTab === 'followers') {
          loadFollowers();
        } else if (activeTab === 'following') {
          loadFollowing();
        }
      }
    } catch (error) {
      console.error('Ошибка подписки:', error);
    }
  };

  const handleUnfollow = async (userId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/users/unfollow/${userId}/`, {
        method: 'POST',
        headers: getAuthHeaders()
      });
      
      if (response.ok) {
        // Обновляем списки
        if (activeTab === 'search') {
          searchUsers();
        } else if (activeTab === 'followers') {
          loadFollowers();
        } else if (activeTab === 'following') {
          loadFollowing();
        }
      }
    } catch (error) {
      console.error('Ошибка отписки:', error);
    }
  };

  const getAuthorInitials = (authorName) => {
    if (!authorName) return 'А';
    const names = authorName.split(' ');
    return `${names[0]?.charAt(0) || 'А'}${names[1]?.charAt(0) || ''}`.toUpperCase();
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Недавно';
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));
    
    if (diffInHours < 1) return 'Только что';
    if (diffInHours < 24) return `${diffInHours} ч назад`;
    
    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays < 7) return `${diffInDays} дн назад`;
    
    return date.toLocaleDateString('ru-RU');
  };

  const renderUserCard = (user) => (
    <div key={user.id} className="user-card">
      <div className="user-header">
        <div className="user-avatar">
          {getAuthorInitials(user.full_name || user.username)}
        </div>
        <div className="user-info">
          <h3>{user.full_name || user.username}</h3>
          <p>
            <FontAwesomeIcon icon={faMapMarkerAlt} />
            {user.city || 'Не указан'}
          </p>
          <p>
            <FontAwesomeIcon icon={faCalendar} />
            {formatDate(user.date_joined)}
          </p>
        </div>
      </div>
      
      <div className="user-actions">
        {user.is_following ? (
          <button 
            className="unfollow-btn"
            onClick={() => handleUnfollow(user.id)}
          >
            <FontAwesomeIcon icon={faUserMinus} />
            Отписаться
          </button>
        ) : (
          <button 
            className="follow-btn"
            onClick={() => handleFollow(user.id)}
          >
            <FontAwesomeIcon icon={faUserPlus} />
            Подписаться
          </button>
        )}
      </div>
    </div>
  );

  if (!isLoggedIn()) {
    return (
      <div className="friends-page">
        <div className="friends-container">
          <div className="auth-required">
            <h2>Требуется авторизация</h2>
            <p>Для просмотра друзей и подписок необходимо войти в систему</p>
            <button className="auth-btn" onClick={() => navigate('/auth')}>
              Войти в систему
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="friends-page">
      <div className="friends-container">
        <div className="friends-header">
          <button onClick={() => navigate('/')} className="btn-back">
            <FontAwesomeIcon icon={faArrowLeft} />
            Назад
          </button>
          <h1 className="friends-title">Друзья</h1>
        </div>

        <div className="friends-content">


          <div className="search-section">
            <form className="search-form" onSubmit={(e) => { e.preventDefault(); searchUsers(); }}>
              <input
                type="text"
                className="search-input"
                placeholder="Поиск пользователей по имени или username..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
              <button 
                type="submit" 
                className="search-btn"
                disabled={searchLoading}
              >
                <FontAwesomeIcon icon={faSearch} />
                Найти
              </button>
            </form>

            {searchLoading ? (
              <div className="loading">
                <div className="loading-spinner"></div>
                <p>Поиск пользователей...</p>
              </div>
            ) : searchResults.length > 0 ? (
              <div className="users-grid">
                {searchResults.map(renderUserCard)}
              </div>
            ) : searchQuery && (
              <div className="empty-state">
                <div className="empty-icon">
                  <FontAwesomeIcon icon={faSearch} />
                </div>
                <h3>Пользователи не найдены</h3>
                <p>Попробуйте изменить поисковый запрос</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default FriendsPage;
