import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft, 
  faPaperPlane, 
  faEdit, 
  faTrash, 
  faCheck, 
  faTimes,
  faUser,
  faMessage,
  faReply,
  faImage,
  faVideo,
  faFile,
  faMicrophone,
  faPaperclip,
  faFilePdf,
  faFileWord,
  faFileText,
  faFileArchive
} from '@fortawesome/free-solid-svg-icons';
import { getChat, saveMessage, updateMessage, deleteMessage } from '../../utils/chatStorage';
import { getUserInfo } from '../../utils/userUtils';
import './ChatPage.css';

const ChatPage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const messagesEndRef = useRef(null);
  const fileInputRef = useRef(null);

  // Основные состояния
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isTyping, setIsTyping] = useState(false);
  const [typingTimeout, setTypingTimeout] = useState(null);

  // Редактирование сообщений
  const [editingMessageId, setEditingMessageId] = useState(null);
  const [editText, setEditText] = useState('');

  // Контекстное меню и модальные окна
  const [contextMenu, setContextMenu] = useState(null);
  const [modalMessage, setModalMessage] = useState(null);

  // Файлы и медиа
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [showMediaOptions, setShowMediaOptions] = useState(false);

  // Ответы на сообщения
  const [replyToMessage, setReplyToMessage] = useState(null);

  // Swipe для мобильных
  const [swipeStartX, setSwipeStartX] = useState(null);
  const [swipeStartY, setSwipeStartY] = useState(null);

  // Проверяем userId
  if (!userId) {
    console.error('userId не найден в параметрах');
    return (
      <div className="chat-loading">
        <p>Ошибка: пользователь не найден</p>
      </div>
    );
  }

  // Загрузка чата при монтировании компонента
  useEffect(() => {
    const loadChat = async () => {
      try {
        setIsLoading(true);

        // Загружаем информацию о пользователе
        const userInfo = await getUserInfo(userId);
        if (userInfo) {
          setUser(userInfo);
        } else {
          console.error('Пользователь не найден');
          navigate('/friends');
          return;
        }

        // Загружаем сообщения чата
        const chatData = getChat(userId);
        if (chatData && chatData.messages) {
          setMessages(chatData.messages);
        }

      } catch (error) {
        console.error('Ошибка загрузки чата:', error);
      } finally {
        setIsLoading(false);
      }
    };

    loadChat();
  }, [userId, navigate]);

  // Автоскролл к последнему сообщению
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Обработка клика вне контекстного меню
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (contextMenu && !event.target.closest('.context-menu')) {
        setContextMenu(null);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [contextMenu]);

  // Обработка клика вне модального окна
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (modalMessage && event.target.classList.contains('modal-overlay')) {
        setModalMessage(null);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [modalMessage]);

  // Функция скролла к низу
  const scrollToBottom = () => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  // Обработка отправки сообщения
  const handleSendMessage = async () => {
    if (!newMessage.trim() && selectedFiles.length === 0) return;

    const messageData = {
      id: Date.now().toString(),
      text: newMessage.trim(),
      timestamp: new Date().toISOString(),
      isOwn: true,
      files: selectedFiles.length > 0 ? selectedFiles : undefined,
      replyTo: replyToMessage ? {
        id: replyToMessage.id,
        text: replyToMessage.text,
        author: replyToMessage.isOwn ? 'Вы' : user?.name || 'Пользователь'
      } : undefined
    };

    // Добавляем сообщение в состояние
    setMessages(prev => [...prev, messageData]);

    // Сохраняем в localStorage
    saveMessage(userId, messageData);

    // Очищаем форму
    setNewMessage('');
    setSelectedFiles([]);
    setShowMediaOptions(false);
    setReplyToMessage(null);

    // Симуляция ответа собеседника
    setTimeout(() => {
      const replyMessage = {
        id: (Date.now() + 1).toString(),
        text: 'Спасибо за сообщение!',
        timestamp: new Date().toISOString(),
        isOwn: false
      };

      setMessages(prev => [...prev, replyMessage]);
      saveMessage(userId, replyMessage);
    }, 1000 + Math.random() * 2000);
  };

  // Обработка нажатия Enter
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  // Обработка изменения текста (индикатор набора)
  const handleTextChange = (e) => {
    setNewMessage(e.target.value);

    // Показываем индикатор набора текста
    if (!isTyping) {
      setIsTyping(true);
    }

    // Сбрасываем таймер
    if (typingTimeout) {
      clearTimeout(typingTimeout);
    }

    // Скрываем индикатор через 2 секунды
    const timeout = setTimeout(() => {
      setIsTyping(false);
    }, 2000);

    setTypingTimeout(timeout);
  };

  // Обработка выбора файлов
  const handleFileSelect = (e) => {
    const files = Array.from(e.target.files);
    const validFiles = files.filter(file => {
      const maxSize = 10 * 1024 * 1024; // 10MB
      if (file.size > maxSize) {
        alert(`Файл ${file.name} слишком большой. Максимальный размер: 10MB`);
        return false;
      }
      return true;
    });

    const fileObjects = validFiles.map(file => ({
      id: Date.now() + Math.random(),
      name: file.name,
      size: file.size,
      type: file.type,
      file: file
    }));

    setSelectedFiles(prev => [...prev, ...fileObjects]);
    setShowMediaOptions(false);

    // Очищаем input
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  // Удаление выбранного файла
  const removeFile = (fileId) => {
    setSelectedFiles(prev => prev.filter(file => file.id !== fileId));
  };

  // Получение иконки для типа файла
  const getFileIcon = (fileType) => {
    if (fileType.startsWith('image/')) return faImage;
    if (fileType.startsWith('video/')) return faVideo;
    if (fileType.includes('pdf')) return faFilePdf;
    if (fileType.includes('word') || fileType.includes('document')) return faFileWord;
    if (fileType.includes('text')) return faFileText;
    if (fileType.includes('zip') || fileType.includes('rar')) return faFileArchive;
    return faFile;
  };

  // Форматирование размера файла
  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  // Обработка контекстного меню
  const handleContextMenu = (e, message) => {
    e.preventDefault();
    setContextMenu({
      x: e.clientX,
      y: e.clientY,
      message: message
    });
  };

  // Начало редактирования сообщения
  const startEditing = (message) => {
    setEditingMessageId(message.id);
    setEditText(message.text);
    setContextMenu(null);
  };

  // Сохранение редактирования
  const saveEdit = () => {
    if (!editText.trim()) return;

    const updatedMessage = {
      ...messages.find(m => m.id === editingMessageId),
      text: editText.trim(),
      edited: true
    };

    setMessages(prev => prev.map(m => 
      m.id === editingMessageId ? updatedMessage : m
    ));

    updateMessage(userId, updatedMessage);
    setEditingMessageId(null);
    setEditText('');
  };

  // Отмена редактирования
  const cancelEdit = () => {
    setEditingMessageId(null);
    setEditText('');
  };

  // Удаление сообщения
  const handleDeleteMessage = (message) => {
    setModalMessage({
      type: 'delete',
      message: message
    });
    setContextMenu(null);
  };

  // Подтверждение удаления
  const confirmDelete = () => {
    if (modalMessage && modalMessage.message) {
      setMessages(prev => prev.filter(m => m.id !== modalMessage.message.id));
      deleteMessage(userId, modalMessage.message.id);
      setModalMessage(null);
    }
  };

  // Ответ на сообщение
  const handleReply = (message) => {
    setReplyToMessage(message);
    setContextMenu(null);
  };

  // Удаление ответа
  const removeReply = () => {
    setReplyToMessage(null);
  };

  // Обработка свайпа для мобильных
  const handleTouchStart = (e) => {
    setSwipeStartX(e.touches[0].clientX);
    setSwipeStartY(e.touches[0].clientY);
  };

  const handleTouchEnd = (e, message) => {
    if (!swipeStartX || !swipeStartY) return;

    const swipeEndX = e.changedTouches[0].clientX;
    const swipeEndY = e.changedTouches[0].clientY;
    const diffX = swipeStartX - swipeEndX;
    const diffY = swipeStartY - swipeEndY;

    // Если свайп влево больше чем вверх/вниз
    if (Math.abs(diffX) > Math.abs(diffY) && diffX > 50) {
      handleReply(message);
    }

    setSwipeStartX(null);
    setSwipeStartY(null);
  };

  // Форматирование времени
  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString('ru-RU', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  // Обработка клика по кнопке медиа
  const handleMediaClick = () => {
    setShowMediaOptions(!showMediaOptions);
  };

  // Обработка клика по опции медиа
  const handleMediaOptionClick = (type) => {
    if (fileInputRef.current) {
      fileInputRef.current.click();
    }
  };

  if (isLoading) {
    return (
      <div className="chat-page">
        <div className="chat-loading">
          <FontAwesomeIcon icon={faMessage} className="loading-icon" />
          <p>Загрузка чата...</p>
        </div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="chat-page">
        <div className="chat-error">
          <FontAwesomeIcon icon={faUser} className="error-icon" />
          <p>Пользователь не найден</p>
          <button onClick={() => navigate('/friends')} className="back-button">
            Вернуться к друзьям
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="chat-page">
      {/* Заголовок чата */}
      <div className="chat-header">
        <button className="back-button" onClick={() => navigate('/friends')}>
          <FontAwesomeIcon icon={faArrowLeft} />
        </button>
        <div className="chat-user-info">
          <div className="user-avatar">
            <img src={user.avatar || '/default-avatar.png'} alt={user.name} />
          </div>
          <div className="user-details">
            <h3 className="user-name">{user.name}</h3>
            <span className="user-city">📍 {user.city || 'Город не указан'}</span>
          </div>
        </div>
      </div>

      {/* Контейнер сообщений */}
      <div className="messages-container">
        {messages.length === 0 ? (
          <div className="empty-chat">
            <FontAwesomeIcon icon={faMessage} className="empty-chat-icon" />
            <div className="empty-chat-text">Начните общение</div>
            <div className="empty-chat-subtext">Отправьте первое сообщение</div>
          </div>
        ) : (
          <div className="messages-list">
            {messages.map((message) => (
              <div
                key={message.id}
                className={`message ${message.isOwn ? 'my-message' : 'incoming-message'}`}
                onContextMenu={(e) => handleContextMenu(e, message)}
                onTouchStart={handleTouchStart}
                onTouchEnd={(e) => handleTouchEnd(e, message)}
              >
                <div className="message-content">
                  {/* Ответ на сообщение */}
                  {message.replyTo && (
                    <div className="reply-to-message">
                      <div className="reply-author">{message.replyTo.author}</div>
                      <div className="reply-text">{message.replyTo.text}</div>
                    </div>
                  )}

                  {/* Редактирование сообщения */}
                  {editingMessageId === message.id ? (
                    <div className="edit-message-form">
                      <textarea
                        value={editText}
                        onChange={(e) => setEditText(e.target.value)}
                        onKeyPress={(e) => {
                          if (e.key === 'Enter' && !e.shiftKey) {
                            e.preventDefault();
                            saveEdit();
                          }
                        }}
                        autoFocus
                        rows={3}
                      />
                      <div className="edit-message-actions">
                        <button className="save-btn" onClick={saveEdit}>
                          <FontAwesomeIcon icon={faCheck} />
                        </button>
                        <button className="cancel-btn" onClick={cancelEdit}>
                          <FontAwesomeIcon icon={faTimes} />
                        </button>
                      </div>
                    </div>
                  ) : (
                    <>
                      {/* Файлы в сообщении */}
                      {message.files && message.files.length > 0 && (
                        <div className="message-files">
                          {message.files.map((file) => (
                            <div key={file.id} className="message-file">
                              <FontAwesomeIcon 
                                icon={getFileIcon(file.type)} 
                                className="file-icon" 
                              />
                              <div className="file-info">
                                <div className="file-name">{file.name}</div>
                                <div className="file-size">{formatFileSize(file.size)}</div>
                              </div>
                            </div>
                          ))}
                        </div>
                      )}

                      {/* Текст сообщения */}
                      {message.text && (
                        <p className="message-text">{message.text}</p>
                      )}

                      {/* Мета-информация */}
                      <div className="message-meta">
                        {message.edited && (
                          <span className="edited-indicator">(изменено)</span>
                        )}
                        <span className="message-time">{formatTime(message.timestamp)}</span>
                        {message.isOwn && (
                          <span className="message-status">✓</span>
                        )}
                      </div>
                    </>
                  )}
                </div>
              </div>
            ))}

            {/* Индикатор набора текста */}
            {isTyping && (
              <div className="typing-indicator">
                <span>{user.name} печатает</span>
                <div className="typing-dots">
                  <div className="typing-dot"></div>
                  <div className="typing-dot"></div>
                  <div className="typing-dot"></div>
                </div>
              </div>
            )}

            <div ref={messagesEndRef} />
          </div>
        )}
      </div>

      {/* Ответ на сообщение */}
      {replyToMessage && (
        <div className="reply-to-message">
          <div className="reply-author">
            {replyToMessage.isOwn ? 'Вы' : user.name}
          </div>
          <div className="reply-text">{replyToMessage.text}</div>
          <button className="reply-remove" onClick={removeReply}>
            <FontAwesomeIcon icon={faTimes} />
          </button>
        </div>
      )}

      {/* Выбранные файлы */}
      {selectedFiles.length > 0 && (
        <div className="selected-files">
          {selectedFiles.map((file) => (
            <div key={file.id} className="selected-file">
              <FontAwesomeIcon 
                icon={getFileIcon(file.type)} 
                className="file-icon" 
              />
              <span className="file-name">{file.name}</span>
              <button 
                className="remove-file-btn"
                onClick={() => removeFile(file.id)}
              >
                <FontAwesomeIcon icon={faTimes} />
              </button>
            </div>
          ))}
        </div>
      )}

      {/* Опции медиа */}
      {showMediaOptions && (
        <div className="media-options">
          <button 
            className="media-option"
            onClick={() => handleMediaOptionClick('photo')}
          >
            <FontAwesomeIcon icon={faImage} />
            Фото/Видео
          </button>
          <button 
            className="media-option"
            onClick={() => handleMediaOptionClick('document')}
          >
            <FontAwesomeIcon icon={faFile} />
            Документ
          </button>
        </div>
      )}

      {/* Форма ввода */}
      <div className="message-input-wrapper">
        <button 
          className="media-button"
          onClick={handleMediaClick}
          title="Прикрепить файл"
        >
          <FontAwesomeIcon icon={faPaperclip} />
        </button>

        <textarea
          className="message-input"
          placeholder="Сообщение"
          value={newMessage}
          onChange={handleTextChange}
          onKeyPress={handleKeyPress}
          rows={1}
        />

        <button 
          className="send-button"
          onClick={handleSendMessage}
          disabled={!newMessage.trim() && selectedFiles.length === 0}
          title="Отправить"
        >
          <FontAwesomeIcon icon={faPaperPlane} />
        </button>
      </div>

      {/* Скрытый input для файлов */}
      <input
        ref={fileInputRef}
        type="file"
        multiple
        accept="image/*,video/*,.pdf,.doc,.docx,.txt,.zip,.rar"
        onChange={handleFileSelect}
        className="file-input"
      />

      {/* Контекстное меню */}
      {contextMenu && (
        <div
          className="context-menu"
          style={{
            left: contextMenu.x,
            top: contextMenu.y
          }}
        >
          <div 
            className="context-menu-item"
            onClick={() => handleReply(contextMenu.message)}
          >
            <FontAwesomeIcon icon={faReply} className="icon" />
            Ответить
          </div>
          {contextMenu.message.isOwn && (
            <>
              <div 
                className="context-menu-item"
                onClick={() => startEditing(contextMenu.message)}
              >
                <FontAwesomeIcon icon={faEdit} className="icon" />
                Редактировать
              </div>
              <div 
                className="context-menu-item"
                onClick={() => handleDeleteMessage(contextMenu.message)}
              >
                <FontAwesomeIcon icon={faTrash} className="icon" />
                Удалить
              </div>
            </>
          )}
        </div>
      )}

      {/* Модальное окно подтверждения */}
      {modalMessage && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h3 className="modal-title">Удалить сообщение?</h3>
              <button 
                className="modal-close"
                onClick={() => setModalMessage(null)}
              >
                <FontAwesomeIcon icon={faTimes} />
              </button>
            </div>
            <div className="modal-content">
              <p>Это действие нельзя отменить.</p>
            </div>
            <div className="modal-actions">
              <button 
                className="modal-button secondary"
                onClick={() => setModalMessage(null)}
              >
                Отмена
              </button>
              <button 
                className="modal-button primary"
                onClick={confirmDelete}
              >
                Удалить
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ChatPage;
