import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEye, faHeart, faComment, faCalendar, faUser, faPen, faMapMarkerAlt } from '@fortawesome/free-solid-svg-icons';
import './PostsList.css';

const PostsList = ({ category = null, limit = null }) => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchPosts();
  }, [category]);

  const fetchPosts = async () => {
    try {
      setLoading(true);
      let url = 'http://localhost:8000/api/posts/';
      
      if (category) {
        url += `?category=${category}`;
      }
      
      if (limit) {
        url += `${category ? '&' : '?'}page_size=${limit}`;
      }
      
      const response = await fetch(url);
      if (response.ok) {
        const data = await response.json();
        setPosts(data.results || data);
      } else {
        setError('Ошибка загрузки постов');
      }
    } catch (error) {
      console.error('Ошибка загрузки постов:', error);
      setError('Ошибка загрузки постов');
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    try {
      const date = new Date(dateString);
      if (isNaN(date.getTime())) {
        return 'Недавно';
      }
      return date.toLocaleDateString('ru-RU', {
        day: 'numeric',
        month: 'long',
        year: 'numeric'
      });
    } catch (error) {
      return 'Недавно';
    }
  };

  const getAuthorInitials = (author) => {
    if (!author) return 'А';
    const firstName = author.first_name || '';
    const lastName = author.last_name || '';
    return `${firstName.charAt(0)}${lastName.charAt(0)}`.toUpperCase() || 'А';
  };

  const getAuthorName = (author) => {
    if (!author) return 'Автор';
    const firstName = author.first_name || '';
    const lastName = author.last_name || '';
    return `${firstName} ${lastName}`.trim() || author.username || 'Автор';
  };

  if (loading) {
    return (
      <div className="posts-loading">
        <div className="loading-spinner"></div>
        <p>Загрузка постов...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="posts-error">
        <p>{error}</p>
        <button onClick={fetchPosts} className="btn-retry">
          Попробовать снова
        </button>
      </div>
    );
  }

  if (posts.length === 0) {
    return (
      <div className="posts-empty">
        <div className="empty-icon">
          <FontAwesomeIcon icon={faEye} />
        </div>
        <h3>Постов пока нет</h3>
        <p>Будьте первым, кто поделится своими мыслями и опытом!</p>
        <Link to="/create-post" className="btn-create-post">
          <FontAwesomeIcon icon={faPen} />
          Написать первый пост
        </Link>
      </div>
    );
  }

  return (
    <div className="posts-grid">
      {posts.map(post => (
        <article key={post.id} className="post-card">
          {/* Изображение поста */}
          <div className="post-image">
            <div className="post-image-placeholder">
              <FontAwesomeIcon icon={faEye} />
            </div>
          </div>
          
          {/* Содержимое поста */}
          <div className="post-content">
            {/* Категория */}
            <div className="post-category">
              {post.category?.name || 'Общее'}
            </div>
            
            {/* Заголовок */}
            <h3 className="post-title">
              <Link to={`/post-detail/${post.slug}`}>
                {post.title}
              </Link>
            </h3>
            
            {/* Краткое описание */}
            <p className="post-excerpt">
              {post.short_description || (post.content.length > 120 
                ? `${post.content.substring(0, 120)}...` 
                : post.content)}
            </p>
            
            {/* Мета-информация */}
            <div className="post-meta">
              <div className="post-author">
                <div className="author-avatar">
                  {post.author?.avatar ? (
                    <img 
                      src={post.author.avatar} 
                      alt={getAuthorName(post.author)}
                      onError={(e) => {
                        e.target.style.display = 'none';
                        e.target.nextSibling.style.display = 'flex';
                      }}
                    />
                  ) : null}
                  <div className="author-initials" style={{ 
                    display: post.author?.avatar ? 'none' : 'flex' 
                  }}>
                    {getAuthorInitials(post.author)}
                  </div>
                </div>
                <div className="author-info">
                  <span className="author-name">{getAuthorName(post.author)}</span>
                  <span className="author-location">
                    <FontAwesomeIcon icon={faMapMarkerAlt} />
                    {post.author?.city || 'Москва'}
                  </span>
                </div>
              </div>
              <div className="post-date">
                <FontAwesomeIcon icon={faCalendar} />
                <span>{formatDate(post.published_at || post.created_at)}</span>
              </div>
            </div>
            
            {/* Статистика */}
            <div className="post-stats">
              <span className="post-stat">
                <FontAwesomeIcon icon={faEye} />
                {post.views_count || 0}
              </span>
              <span className="post-stat">
                <FontAwesomeIcon icon={faHeart} />
                {post.likes_count || 0}
              </span>
              <span className="post-stat">
                <FontAwesomeIcon icon={faComment} />
                {post.comments_count || 0}
              </span>
            </div>
            
            {/* Кнопка "Читать дальше" */}
            <div className="post-actions">
              <Link to={`/post-detail/${post.slug}`} className="read-more-btn">
                Читать дальше
              </Link>
            </div>
          </div>
        </article>
      ))}
    </div>
  );
};

export default PostsList;
