const API_BASE_URL = 'http://localhost:8000/api';

class ApiClient {
  constructor() {
    this.baseURL = API_BASE_URL;
    this.token = localStorage.getItem('access_token');
  }

  setToken(token) {
    this.token = token;
    localStorage.setItem('access_token', token);
  }

  getHeaders(includeContentType = true) {
    const headers = {};
    
    if (includeContentType) {
      headers['Content-Type'] = 'application/json';
    }
    
    if (this.token) {
      headers['Authorization'] = `Bearer ${this.token}`;
    }
    
    return headers;
  }

  async request(endpoint, options = {}) {
    const url = `${this.baseURL}${endpoint}`;
    
    // Определяем, нужно ли включать Content-Type
    const isFormData = options.body instanceof FormData;
    const config = {
      headers: this.getHeaders(!isFormData),
      ...options,
    };

    try {
      const response = await fetch(url, config);
      
      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.detail || `HTTP error! status: ${response.status}`);
      }
      
      return await response.json();
    } catch (error) {
      console.error('API request failed:', error);
      throw error;
    }
  }

  // Аутентификация
  async register(userData) {
    return this.request('/auth/register', {
      method: 'POST',
      body: JSON.stringify(userData),
    });
  }

  async login(email, password) {
    const formData = new FormData();
    formData.append('email', email);
    formData.append('password', password);
    
    return this.request('/auth/login', {
      method: 'POST',
      headers: {
        'Authorization': this.token ? `Bearer ${this.token}` : undefined,
      },
      body: formData,
    });
  }

  async getCurrentUser() {
    return this.request('/users/me');
  }

  // Пользователи
  async getUsers() {
    return this.request('/users');
  }

  // Чаты
  async getChats() {
    return this.request('/chats');
  }

  async createChat(user2Id) {
    return this.request('/chats', {
      method: 'POST',
      body: JSON.stringify({ user2_id: user2Id }),
    });
  }

  // Сообщения
  async getMessages(chatId) {
    return this.request(`/chats/${chatId}/messages`);
  }

  async sendMessage(chatId, text) {
    return this.request(`/chats/${chatId}/messages`, {
      method: 'POST',
      body: JSON.stringify({ text }),
    });
  }

  async updateMessage(messageId, text) {
    return this.request(`/messages/${messageId}`, {
      method: 'PUT',
      body: JSON.stringify({ text }),
    });
  }

  async deleteMessage(messageId) {
    return this.request(`/messages/${messageId}`, {
      method: 'DELETE',
    });
  }

  async uploadFile(messageId, file) {
    const formData = new FormData();
    formData.append('file', file);
    
    return this.request(`/messages/${messageId}/files`, {
      method: 'POST',
      body: formData,
    });
  }

  // Методы для постов (совместимость со старым API)
  async createPost(postData) {
    const formData = new FormData();
    
    // Добавляем все поля в FormData
    Object.keys(postData).forEach(key => {
      if (postData[key] !== null && postData[key] !== undefined) {
        formData.append(key, postData[key]);
      }
    });
    
    return this.request('/posts/create/', {
      method: 'POST',
      body: formData,
    });
  }

  async updatePost(postId, postData) {
    const formData = new FormData();
    
    Object.keys(postData).forEach(key => {
      if (postData[key] !== null && postData[key] !== undefined) {
        formData.append(key, postData[key]);
      }
    });
    
    return this.request(`/posts/${postId}/update/`, {
      method: 'PUT',
      body: formData,
    });
  }

  async getPosts() {
    return this.request('/posts/');
  }

  async getPost(postId) {
    return this.request(`/posts/${postId}/`);
  }

  async deletePost(postId) {
    return this.request(`/posts/${postId}/delete/`, {
      method: 'DELETE',
    });
  }

  async getCategories() {
    return this.request('/categories/');
  }
}

export const apiClient = new ApiClient();
