import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft,
  faMapMarkerAlt,
  faCalendar,
  faPen
} from '@fortawesome/free-solid-svg-icons';
import './ProfilePage.css';

const ProfilePage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchUserProfile();
  }, [userId]);

  const fetchUserProfile = async () => {
    try {
      setLoading(true);

      // Получаем данные пользователя
      const userResponse = await fetch(`http://localhost:8000/api/auth/${userId}/`);
      console.log('Ответ от сервера для пользователя:', userResponse.status);

      if (userResponse.ok) {
        const userData = await userResponse.json();
        console.log('Данные пользователя:', userData);
        setUser(userData.user || userData);
      } else {
        console.error('Ошибка получения пользователя:', userResponse.status);
        setError('Пользователь не найден');
      }

      // Получаем посты пользователя
      const postsResponse = await fetch(`http://localhost:8000/api/posts/user/${userId}/`);
      console.log('Ответ от сервера для постов:', postsResponse.status);

      if (postsResponse.ok) {
        const postsData = await postsResponse.json();
        console.log('Данные постов:', postsData);
        setPosts(postsData.results || postsData);
      } else {
        console.error('Ошибка получения постов:', postsResponse.status);
        setPosts([]);
      }
    } catch (error) {
      console.error('Ошибка загрузки профиля:', error);
      setError('Ошибка загрузки профиля пользователя');
    } finally {
      setLoading(false);
    }
  };

  const getAuthorInitials = (author) => {
    if (!author) return 'А';
    const firstName = author.first_name || '';
    const lastName = author.last_name || '';
    return `${firstName.charAt(0)}${lastName.charAt(0)}`.toUpperCase() || 'А';
  };

  const getAuthorName = (author) => {
    if (!author) return 'Автор';
    const firstName = author.first_name || '';
    const lastName = author.last_name || '';
    const fullName = `${firstName} ${lastName}`.trim();
    return fullName || author.username || 'Автор';
  };

  const formatDate = (dateString) => {
    try {
      const date = new Date(dateString);
      if (isNaN(date.getTime())) {
        return 'Недавно';
      }
      return date.toLocaleDateString('ru-RU', {
        day: 'numeric',
        month: 'long',
        year: 'numeric'
      });
    } catch (error) {
      return 'Недавно';
    }
  };

  if (loading) {
    return (
      <div className="profile-loading">
        <div className="loading-spinner"></div>
        <p>Загрузка профиля...</p>
      </div>
    );
  }

  if (error || !user) {
    return (
      <div className="profile-error">
        <h2>Ошибка</h2>
        <p>{error || 'Пользователь не найден'}</p>
        <button onClick={() => navigate('/')} className="btn-back">
          <FontAwesomeIcon icon={faArrowLeft} />
          Вернуться на главную
        </button>
      </div>
    );
  }

  return (
    <div className="profile-container">
      {/* Кнопка назад */}
      <div className="profile-header">
        <button onClick={() => navigate('/')} className="btn-back">
          <FontAwesomeIcon icon={faArrowLeft} />
          Назад
        </button>
      </div>

      {/* Информация о пользователе */}
      <div className="profile-info">
        <div className="profile-avatar">
          {user.avatar_url ? (
            <img 
              src={user.avatar_url} 
              alt={getAuthorName(user)}
              onError={(e) => {
                e.target.style.display = 'none';
                e.target.nextSibling.style.display = 'flex';
              }}
            />
          ) : null}
          <div className="profile-initials" style={{ 
            display: user.avatar_url ? 'none' : 'flex' 
          }}>
            {getAuthorInitials(user)}
          </div>
        </div>

        <div className="profile-details">
          <h1 className="profile-name">{getAuthorName(user)}</h1>
          <div className="profile-meta">
            <span className="profile-location">
              <FontAwesomeIcon icon={faMapMarkerAlt} />
              {user.city || 'Не указан'}
            </span>
            <span className="profile-username">
              @{user.username}
            </span>
          </div>
        </div>
      </div>

      {/* Статистика */}
      <div className="profile-stats">
        <div className="stat-item">
          <span className="stat-number">{posts.length}</span>
          <span className="stat-label">Постов</span>
        </div>
      </div>

      {/* Посты пользователя */}
      <div className="profile-posts">
        <h2>Посты пользователя</h2>

        {posts.length === 0 ? (
          <div className="no-posts">
            <div className="no-posts-icon">
              <FontAwesomeIcon icon={faPen} />
            </div>
            <h3>Постов пока нет</h3>
            <p>Этот пользователь еще не опубликовал ни одного поста</p>
          </div>
        ) : (
          <div className="posts-grid">
            {posts.map(post => (
              <div key={post.id} className="post-card">
                <div className="post-content">
                  <h3 className="post-title">
                    <a href={`/post-detail/${post.slug}`}>
                      {post.title}
                    </a>
                  </h3>

                  {post.short_description && (
                    <p className="post-excerpt">
                      {post.short_description}
                    </p>
                  )}

                  <div className="post-meta">
                    <span className="post-category">
                      {post.category?.name || 'Общее'}
                    </span>
                    <span className="post-date">
                      <FontAwesomeIcon icon={faCalendar} />
                      {formatDate(post.published_at || post.created_at)}
                    </span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ProfilePage;
