import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faBookmark, 
  faMapMarkerAlt, 
  faCalendar,
  faArrowLeft,
  faEye
} from '@fortawesome/free-solid-svg-icons';
import './ArchivePage.css';

const ArchivePage = () => {
  const [archivedPosts, setArchivedPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchArchivedPosts();
  }, []);

  const fetchArchivedPosts = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');

      if (!token) {
        setError('Необходима авторизация');
        setLoading(false);
        return;
      }

      const response = await fetch('http://localhost:8000/api/users/archive/', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        setArchivedPosts(data.results || []);
      } else {
        setError('Ошибка загрузки архива');
      }
    } catch (error) {
      setError('Ошибка загрузки архива');
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    try {
      const date = new Date(dateString);
      const now = new Date();
      const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));
      
      if (diffInHours < 1) return 'Только что';
      if (diffInHours < 24) return `${diffInHours} ч назад`;
      
      const diffInDays = Math.floor(diffInHours / 24);
      if (diffInDays < 7) return `${diffInDays} дн назад`;
      
      return date.toLocaleDateString('ru-RU');
    } catch (error) {
      return 'Недавно';
    }
  };

  const getAuthorInitials = (author) => {
    if (!author) return 'А';
    const firstName = author.first_name || '';
    const lastName = author.last_name || '';
    return `${firstName.charAt(0)}${lastName.charAt(0)}`.toUpperCase() || 'А';
  };

  if (loading) {
    return (
      <div className="archive-loading">
        <div className="loading-spinner"></div>
        <p>Загрузка архива...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="archive-error">
        <h2>Ошибка</h2>
        <p>{error}</p>
        <button onClick={fetchArchivedPosts} className="btn-retry">
          Попробовать снова
        </button>
      </div>
    );
  }

  return (
    <div className="archive-page">
      {/* Хлебные крошки */}
      <div className="breadcrumbs-container">
        <nav className="breadcrumbs">
          <Link to="/" className="breadcrumb-link">Главная</Link>
          <FontAwesomeIcon icon={faArrowLeft} className="breadcrumb-separator" />
          <span className="breadcrumb-link active">Архив</span>
        </nav>
      </div>

      {/* Заголовок */}
      <div className="archive-header">
        <div className="archive-title">
          <FontAwesomeIcon icon={faBookmark} className="archive-icon" />
          <h1>Мой архив</h1>
        </div>
        <p className="archive-description">
          Здесь собраны все посты, которые вы сохранили для себя
        </p>
      </div>

      {/* Список постов */}
      <div className="archive-content">
        {archivedPosts.length === 0 ? (
          <div className="archive-empty">
            <FontAwesomeIcon icon={faBookmark} className="empty-icon" />
            <h3>Архив пуст</h3>
            <p>Вы пока не сохранили ни одного поста в архив</p>
            <Link to="/posts" className="btn-browse-posts">
              Просмотреть посты
            </Link>
          </div>
        ) : (
          <div className="posts-grid">
            {archivedPosts.map((post) => (
              <div key={post.id} className="post-item">
                {/* Заголовок */}
                <div 
                  className="post-title clickable-title"
                  onClick={() => {
                    const postSlug = post.slug && post.slug !== 'post' ? post.slug : post.id;
                    navigate(`/post-detail/${postSlug}`);
                  }}
                >
                  {post.title}
                </div>
                
                {/* Тег */}
                <div className="post-tag">
                  {post.category?.name || 'Общее'}
                </div>
                
                {/* Информация об авторе */}
                <div className="post-author">
                  <div className="author-avatar">
                    {post.author?.avatar ? (
                      <img 
                        src={post.author.avatar} 
                        alt={post.author.first_name}
                        onError={(e) => {
                          e.target.style.display = 'none';
                          e.target.nextSibling.style.display = 'flex';
                        }}
                      />
                    ) : null}
                    <div className="author-initials" style={{ 
                      display: post.author?.avatar ? 'none' : 'flex' 
                    }}>
                      {getAuthorInitials(post.author)}
                    </div>
                  </div>
                  <div className="author-info">
                    <span className="author-name">
                      {post.author?.first_name || post.author?.username || 'Пользователь'}
                    </span>
                    <span className="author-location">
                      <FontAwesomeIcon icon={faMapMarkerAlt} />
                      {post.author?.city || 'Не указан'}
                    </span>
                    <span className="post-date">
                      <FontAwesomeIcon icon={faCalendar} />
                      {formatDate(post.created_at)}
                    </span>
                  </div>
                </div>
                
                {/* Отрывок текста статьи */}
                {post.content && (
                  <div className="post-excerpt">
                    <p>{post.content.length > 67 ? `${post.content.substring(0, 67)}...` : post.content}</p>
                  </div>
                )}
                
                {/* Кнопка "Читать дальше" */}
                <div className="post-read-more">
                  <button 
                    className="read-more-btn"
                    onClick={() => {
                      const postSlug = post.slug && post.slug !== 'post' ? post.slug : post.id;
                      navigate(`/post-detail/${postSlug}`);
                    }}
                  >
                    <FontAwesomeIcon icon={faEye} />
                    Читать дальше
                  </button>
                </div>
                
                {/* Дата добавления в архив */}
                <div className="archived-date">
                  <FontAwesomeIcon icon={faBookmark} />
                  Сохранено: {formatDate(post.archived_at)}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ArchivePage;
