import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft, 
  faPaperPlane, 
  faEdit, 
  faTrash, 
  faCheck, 
  faTimes,
  faUser,
  faMessage,
  faReply,
  faImage,
  faFile,
  faPaperclip,
  faFilePdf,
  faFileWord,
  faFileText,
  faFileArchive
} from '@fortawesome/free-solid-svg-icons';
import './ChatPage.css';

const ChatPageTest = () => {
  const { chatId } = useParams();
  const navigate = useNavigate();
  const messagesEndRef = useRef(null);

  const [messages, setMessages] = useState([
    {
      id: 1,
      text: '–ü—Ä–∏–≤–µ—Ç! –ö–∞–∫ –¥–µ–ª–∞?',
      sender_id: 2,
      created_at: new Date().toISOString(),
      files: []
    },
    {
      id: 2,
      text: '–ü—Ä–∏–≤–µ—Ç! –í—Å—ë –æ—Ç–ª–∏—á–Ω–æ, —Å–ø–∞—Å–∏–±–æ!',
      sender_id: 1,
      created_at: new Date().toISOString(),
      files: []
    }
  ]);
  const [newMessage, setNewMessage] = useState('');
  const [editingMessageId, setEditingMessageId] = useState(null);
  const [editText, setEditText] = useState('');
  const [currentUser] = useState({ id: 1, name: '–í—ã' });
  const [chatUser] = useState({ id: 2, name: '–¢–µ—Å—Ç–æ–≤—ã–π –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å', city: '–ú–æ—Å–∫–≤–∞' });
  const [isLoading, setIsLoading] = useState(false);
  const [isTyping, setIsTyping] = useState(false);
  const [typingUsers, setTypingUsers] = useState(new Set());
  const [contextMenu, setContextMenu] = useState(null);
  const [modalMessage, setModalMessage] = useState(null);
  const [swipeStartX, setSwipeStartX] = useState(null);
  const [swipeStartY, setSwipeStartY] = useState(null);
  const [replyToMessage, setReplyToMessage] = useState(null);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [showMediaOptions, setShowMediaOptions] = useState(false);
  const [sendingMessage, setSendingMessage] = useState(false);

  // –ê–≤—Ç–æ—Å–∫—Ä–æ–ª–ª –∫ –ø–æ—Å–ª–µ–¥–Ω–µ–º—É —Å–æ–æ–±—â–µ–Ω–∏—é
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleSendMessage = async () => {
    if ((!newMessage.trim() && selectedFiles.length === 0) || sendingMessage) return;

    try {
      setSendingMessage(true);
      
      // –°–æ–∑–¥–∞–µ–º –Ω–æ–≤–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ
      const newMsg = {
        id: Date.now(),
        text: newMessage.trim(),
        sender_id: currentUser.id,
        created_at: new Date().toISOString(),
        files: selectedFiles.map((file, index) => ({
          id: index + 1,
          filename: file.name,
          file_size: file.size,
          file_type: file.type
        }))
      };
      
      setMessages(prev => [...prev, newMsg]);
      setNewMessage('');
      setSelectedFiles([]);
      setShowMediaOptions(false);
      
      // –°–∏–º—É–ª—è—Ü–∏—è –æ—Ç–≤–µ—Ç–∞ —Å–æ–±–µ—Å–µ–¥–Ω–∏–∫–∞
      setTimeout(() => {
        const replyMsg = {
          id: Date.now() + 1,
          text: '–°–ø–∞—Å–∏–±–æ –∑–∞ —Å–æ–æ–±—â–µ–Ω–∏–µ!',
          sender_id: chatUser.id,
          created_at: new Date().toISOString(),
          files: []
        };
        setMessages(prev => [...prev, replyMsg]);
      }, 1000 + Math.random() * 2000);
      
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –æ—Ç–ø—Ä–∞–≤–∫–∏ —Å–æ–æ–±—â–µ–Ω–∏—è:', error);
    } finally {
      setSendingMessage(false);
    }
  };

  const handleEditMessage = async () => {
    if (!editText.trim()) return;

    try {
      setMessages(prev => 
        prev.map(msg => 
          msg.id === editingMessageId 
            ? { ...msg, text: editText.trim(), edited_at: new Date().toISOString() }
            : msg
        )
      );
      setEditingMessageId(null);
      setEditText('');
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏—è —Å–æ–æ–±—â–µ–Ω–∏—è:', error);
    }
  };

  const handleDeleteMessage = async (messageId) => {
    try {
      setMessages(prev => prev.filter(msg => msg.id !== messageId));
      setContextMenu(null);
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ —É–¥–∞–ª–µ–Ω–∏—è —Å–æ–æ–±—â–µ–Ω–∏—è:', error);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleInputChange = (e) => {
    setNewMessage(e.target.value);
  };

  const handleFileSelect = (e) => {
    const files = Array.from(e.target.files);
    setSelectedFiles(prev => [...prev, ...files]);
    setShowMediaOptions(false);
  };

  const removeFile = (index) => {
    setSelectedFiles(prev => prev.filter((_, i) => i !== index));
  };

  const handleMediaOptionClick = (type) => {
    if (type === 'photo') {
      fileInputRef.current.accept = 'image/*';
    } else if (type === 'document') {
      fileInputRef.current.accept = '.pdf,.doc,.docx,.txt,.zip,.rar';
    }
    fileInputRef.current.click();
  };

  const getFileIcon = (file) => {
    const extension = file.name.split('.').pop().toLowerCase();
    switch (extension) {
      case 'pdf':
        return faFilePdf;
      case 'doc':
      case 'docx':
        return faFileWord;
      case 'txt':
        return faFileText;
      case 'zip':
      case 'rar':
        return faFileArchive;
      default:
        return faFile;
    }
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const handleContextMenu = (e, message) => {
    e.preventDefault();
    if (message.sender_id === currentUser.id) {
      setContextMenu({
        x: e.clientX,
        y: e.clientY,
        message: message
      });
    }
  };

  const closeContextMenu = () => {
    setContextMenu(null);
  };

  const formatTime = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleTimeString('ru-RU', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  if (isLoading) {
    return (
      <div className="chat-page">
        <div className="loading">–ó–∞–≥—Ä—É–∑–∫–∞...</div>
      </div>
    );
  }

  return (
    <div className="chat-page" onClick={closeContextMenu}>
      {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ —á–∞—Ç–∞ */}
      <div className="chat-header">
        <button className="back-button" onClick={() => navigate('/friends')}>
          <FontAwesomeIcon icon={faArrowLeft} />
        </button>
        <div className="chat-user-info">
          <div className="user-avatar">
            <img 
              src="/default-avatar.png" 
              alt={chatUser?.name || '–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å'} 
            />
          </div>
          <div className="user-details">
            <h3 className="user-name">{chatUser?.name || '–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å'}</h3>
            <span className="user-city">üìç {chatUser?.city || '–ì–æ—Ä–æ–¥ –Ω–µ —É–∫–∞–∑–∞–Ω'}</span>
          </div>
        </div>
      </div>

      {/* –ö–æ–Ω—Ç–µ–π–Ω–µ—Ä —Å–æ–æ–±—â–µ–Ω–∏–π */}
      <div className="messages-container">
        <div className="messages-list">
          {messages.map((message) => (
            <div
              key={message.id}
              className={`message ${message.sender_id === currentUser.id ? 'my-message' : 'incoming-message'}`}
              onContextMenu={(e) => handleContextMenu(e, message)}
            >
              <div className="message-content">
                {message.text && (
                  <p className="message-text">{message.text}</p>
                )}
                
                {/* –§–∞–π–ª—ã —Å–æ–æ–±—â–µ–Ω–∏—è */}
                {message.files && message.files.length > 0 && (
                  <div className="message-files">
                    {message.files.map((file) => (
                      <div key={file.id} className="message-file">
                        <FontAwesomeIcon icon={getFileIcon(file)} />
                        <span className="file-name">{file.filename}</span>
                        <span className="file-size">({formatFileSize(file.file_size)})</span>
                      </div>
                    ))}
                  </div>
                )}
                
                <div className="message-meta">
                  <span className="message-time">{formatTime(message.created_at)}</span>
                  {message.edited_at && (
                    <span className="edited-indicator">(—Ä–µ–¥.)</span>
                  )}
                  {message.sender_id === currentUser.id && (
                    <span className="message-status">‚úì</span>
                  )}
                </div>
              </div>
            </div>
          ))}
          
          {/* –ò–Ω–¥–∏–∫–∞—Ç–æ—Ä –Ω–∞–±–æ—Ä–∞ —Ç–µ–∫—Å—Ç–∞ */}
          {typingUsers.size > 0 && (
            <div className="typing-indicator">
              <span>–ü–µ—á–∞—Ç–∞–µ—Ç...</span>
            </div>
          )}
          
          <div ref={messagesEndRef} />
        </div>
      </div>

      {/* –í—ã–±—Ä–∞–Ω–Ω—ã–µ —Ñ–∞–π–ª—ã */}
      {selectedFiles.length > 0 && (
        <div className="selected-files">
          {selectedFiles.map((file, index) => (
            <div key={index} className="selected-file">
              <FontAwesomeIcon icon={getFileIcon(file)} />
              <span className="file-name">{file.name}</span>
              <span className="file-size">({formatFileSize(file.size)})</span>
              <button 
                className="remove-file-btn"
                onClick={() => removeFile(index)}
              >
                <FontAwesomeIcon icon={faTimes} />
              </button>
            </div>
          ))}
        </div>
      )}

      {/* –û–ø—Ü–∏–∏ –º–µ–¥–∏–∞ */}
      {showMediaOptions && (
        <div className="media-options">
          <button 
            className="media-option"
            onClick={() => handleMediaOptionClick('photo')}
          >
            <FontAwesomeIcon icon={faImage} />
            –§–æ—Ç–æ
          </button>
          <button 
            className="media-option"
            onClick={() => handleMediaOptionClick('document')}
          >
            <FontAwesomeIcon icon={faFile} />
            –î–æ–∫—É–º–µ–Ω—Ç
          </button>
        </div>
      )}

      {/* –§–æ—Ä–º–∞ –≤–≤–æ–¥–∞ */}
      <div className="message-input-container">
        <div className="message-input-wrapper">
          <button 
            className="media-button"
            onClick={() => setShowMediaOptions(!showMediaOptions)}
            title="–ü—Ä–∏–∫—Ä–µ–ø–∏—Ç—å —Ñ–∞–π–ª"
          >
            <FontAwesomeIcon icon={faPaperclip} />
          </button>
          
          <textarea
            className="message-input"
            placeholder="–°–æ–æ–±—â–µ–Ω–∏–µ"
            value={newMessage}
            onChange={handleInputChange}
            onKeyPress={handleKeyPress}
            rows={1}
          />
          
          <button 
            className="send-button"
            onClick={handleSendMessage}
            disabled={(!newMessage.trim() && selectedFiles.length === 0) || sendingMessage}
            title="–û—Ç–ø—Ä–∞–≤–∏—Ç—å"
          >
            <FontAwesomeIcon icon={faPaperPlane} />
          </button>
        </div>

        {/* –°–∫—Ä—ã—Ç—ã–π input –¥–ª—è —Ñ–∞–π–ª–æ–≤ */}
        <input
          ref={fileInputRef}
          type="file"
          multiple
          accept="image/*,.pdf,.doc,.docx,.txt,.zip,.rar"
          onChange={handleFileSelect}
          className="file-input"
        />
      </div>

      {/* –ö–æ–Ω—Ç–µ–∫—Å—Ç–Ω–æ–µ –º–µ–Ω—é */}
      {contextMenu && (
        <div
          className="context-menu"
          style={{
            position: 'fixed',
            left: contextMenu.x,
            top: contextMenu.y,
            zIndex: 1000
          }}
        >
          <button
            className="context-menu-item"
            onClick={() => {
              setEditingMessageId(contextMenu.message.id);
              setEditText(contextMenu.message.text || '');
              setContextMenu(null);
            }}
          >
            <FontAwesomeIcon icon={faEdit} />
            –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å
          </button>
          <button
            className="context-menu-item delete"
            onClick={() => handleDeleteMessage(contextMenu.message.id)}
          >
            <FontAwesomeIcon icon={faTrash} />
            –£–¥–∞–ª–∏—Ç—å
          </button>
        </div>
      )}

      {/* –ú–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏—è */}
      {editingMessageId && (
        <div className="modal-overlay" onClick={() => setEditingMessageId(null)}>
          <div className="edit-modal" onClick={(e) => e.stopPropagation()}>
            <h3>–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å —Å–æ–æ–±—â–µ–Ω–∏–µ</h3>
            <textarea
              value={editText}
              onChange={(e) => setEditText(e.target.value)}
              className="edit-textarea"
              rows={3}
            />
            <div className="modal-buttons">
              <button
                className="cancel-button"
                onClick={() => setEditingMessageId(null)}
              >
                <FontAwesomeIcon icon={faTimes} />
                –û—Ç–º–µ–Ω–∞
              </button>
              <button
                className="save-button"
                onClick={handleEditMessage}
                disabled={!editText.trim()}
              >
                <FontAwesomeIcon icon={faCheck} />
                –°–æ—Ö—Ä–∞–Ω–∏—Ç—å
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ChatPageTest;
