import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faSearch, 
  faPen, 
  faUser, 
  faUsers, 
  faSignOutAlt, 
  faChevronDown, 
  faChevronRight, 
  faMoon, 
  faUserFriends,
  faBell
} from '@fortawesome/free-solid-svg-icons';
import './Header.css';

const Header = () => {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userData, setUserData] = useState(null);
  const [showDropdown, setShowDropdown] = useState(false);
  const [showWriteModal, setShowWriteModal] = useState(false);
  const [unreadNotifications, setUnreadNotifications] = useState(0);

  useEffect(() => {
    checkAuthStatus();
    window.addEventListener('authUpdate', checkAuthStatus);
    return () => window.removeEventListener('authUpdate', checkAuthStatus);
  }, []);

  useEffect(() => {
    if (isLoggedIn) {
      loadNotifications();
      const interval = setInterval(loadNotifications, 30000); // Обновляем каждые 30 секунд
      return () => clearInterval(interval);
    }
  }, [isLoggedIn]);

  const checkAuthStatus = () => {
    const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
    const user = JSON.parse(localStorage.getItem('userData') || sessionStorage.getItem('userData') || 'null');

    setIsLoggedIn(!!token);
    setUserData(user);
  };

  const loadNotifications = async () => {
    try {
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      if (!token) return;

      const response = await fetch('http://localhost:8000/api/users/notifications/', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        setUnreadNotifications(data.unread_count || 0);
      }
    } catch (error) {
      console.error('Ошибка загрузки уведомлений:', error);
    }
  };

  const handleWritePostClick = () => {
    if (!isLoggedIn) {
      setShowWriteModal(true);
    } else {
      navigate('/create-post');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('accessToken');
    localStorage.removeItem('refreshToken');
    localStorage.removeItem('userData');
    sessionStorage.removeItem('accessToken');
    sessionStorage.removeItem('refreshToken');
    sessionStorage.removeItem('userData');

    setIsLoggedIn(false);
    setUserData(null);
    setShowDropdown(false);

    // Обновляем состояние авторизации
    window.dispatchEvent(new CustomEvent('authUpdate'));

    navigate('/');
  };

  const getProfileUrl = () => {
    if (userData && userData.id) {
      return `/profile`;
    }
    return '/auth';
  };

  const getAuthorInitials = (authorName) => {
    if (!authorName) return 'А';
    const names = authorName.split(' ');
    return `${names[0]?.charAt(0) || 'А'}${names[1]?.charAt(0) || ''}`.toUpperCase();
  };

  return (
    <header className="header">
      <div className="header-container">
        <div className="header-left">
          <div className="logo" onClick={() => navigate('/')}>
            <h1>BabyBlog</h1>
          </div>
        </div>

        <div className="header-center">
          <div className="search-container">
            <FontAwesomeIcon icon={faSearch} className="search-icon" />
            <input 
              type="text" 
              placeholder="Поиск постов..." 
              className="search-input"
            />
          </div>
        </div>

        <div className="header-right">
          <div className="header-actions">
            <button className="btn-write" onClick={handleWritePostClick}>
              <FontAwesomeIcon icon={faPen} />
              Написать пост
            </button>

            {isLoggedIn && (
              <button className="btn-notifications" onClick={() => navigate('/notifications')}>
                <FontAwesomeIcon icon={faBell} />
                {unreadNotifications > 0 && (
                  <span className="notification-badge">{unreadNotifications}</span>
                )}
              </button>
            )}

            {isLoggedIn ? (
              <div className="user-menu">
                <div 
                  className="user-avatar"
                  onClick={() => setShowDropdown(!showDropdown)}
                >
                  {userData?.avatar ? (
                    <img 
                      src={userData.avatar} 
                      alt={userData.first_name || userData.username}
                      onError={(e) => {
                        e.target.style.display = 'none';
                        e.target.nextSibling.style.display = 'flex';
                      }}
                    />
                  ) : null}
                  <div className="avatar-placeholder" style={{ 
                    display: userData?.avatar ? 'none' : 'flex' 
                  }}>
                    {getAuthorInitials(userData?.first_name || userData?.username)}
                  </div>
                  <FontAwesomeIcon icon={faChevronDown} className="dropdown-arrow" />
                </div>

                {showDropdown && (
                  <div className="dropdown-menu">
                    <div className="dropdown-header">
                      <div className="dropdown-user-info">
                        <div className="dropdown-avatar">
                          {userData?.avatar ? (
                            <img 
                              src={userData.avatar} 
                              alt={userData.first_name || userData.username}
                              onError={(e) => {
                                e.target.style.display = 'none';
                                e.target.nextSibling.style.display = 'flex';
                              }}
                            />
                          ) : null}
                          <div className="dropdown-avatar-placeholder" style={{ 
                            display: userData?.avatar ? 'none' : 'flex' 
                          }}>
                            {getAuthorInitials(userData?.first_name || userData?.username)}
                          </div>
                        </div>
                        <div className="dropdown-user-details">
                          <div className="dropdown-user-name">
                            {userData?.first_name || userData?.username}
                          </div>
                          <div className="dropdown-user-email">
                            {userData?.email}
                          </div>
                        </div>
                      </div>
                    </div>

                    <div className="dropdown-items">
                      <div className="dropdown-item" onClick={() => { navigate(getProfileUrl()); setShowDropdown(false); }}>
                        <FontAwesomeIcon icon={faUser} />
                        <span>Профиль</span>
                        <FontAwesomeIcon icon={faChevronRight} className="item-arrow" />
                      </div>

                      <div className="dropdown-item" onClick={() => { navigate('/friends'); setShowDropdown(false); }}>
                        <FontAwesomeIcon icon={faUsers} />
                        <span>Друзья</span>
                        <FontAwesomeIcon icon={faChevronRight} className="item-arrow" />
                      </div>

                      <div className="dropdown-divider"></div>

                      <div className="dropdown-item" onClick={handleLogout}>
                        <FontAwesomeIcon icon={faSignOutAlt} />
                        <span>Выйти</span>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            ) : (
              <button className="btn-login" onClick={() => navigate('/auth')}>
                Войти
              </button>
            )}
          </div>
        </div>
      </div>

      {/* Модальное окно для неавторизованных пользователей */}
      {showWriteModal && (
        <div className="modal-overlay" onClick={() => setShowWriteModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h3>Требуется авторизация</h3>
            <p>Для создания постов необходимо войти в систему</p>
            <div className="modal-actions">
              <button 
                className="btn-primary" 
                onClick={() => { setShowWriteModal(false); navigate('/auth'); }}
              >
                Войти
              </button>
              <button 
                className="btn-secondary" 
                onClick={() => setShowWriteModal(false)}
              >
                Отмена
              </button>
            </div>
          </div>
        </div>
      )}
    </header>
  );
};

export default Header;
