import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft, 
  faPaperPlane, 
  faEdit, 
  faTrash, 
  faCheck, 
  faTimes,
  faUser,
  faMessage
} from '@fortawesome/free-solid-svg-icons';
import { getChat, saveMessage, updateMessage, deleteMessage } from '../../utils/chatStorage';
import { getUserInfo } from '../../utils/userUtils';
import './ChatPage.css';

const ChatPage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const messagesEndRef = useRef(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [editingMessageId, setEditingMessageId] = useState(null);
  const [editText, setEditText] = useState('');
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isTyping, setIsTyping] = useState(false);
  const [typingTimeout, setTypingTimeout] = useState(null);
  const [contextMenu, setContextMenu] = useState(null);
  const [modalMessage, setModalMessage] = useState(null);
  const [swipeStartX, setSwipeStartX] = useState(null);
  const [swipeStartY, setSwipeStartY] = useState(null);
  const [replyToMessage, setReplyToMessage] = useState(null);

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º userId —Å—Ä–∞–∑—É –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ

  if (!userId) {
    console.error('userId –Ω–µ –Ω–∞–π–¥–µ–Ω –≤ –ø–∞—Ä–∞–º–µ—Ç—Ä–∞—Ö');
    return (
      <div className="chat-loading">
        <p>–û—à–∏–±–∫–∞: ID –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –Ω–µ –Ω–∞–π–¥–µ–Ω</p>
        <button onClick={() => navigate(-1)}>–í–µ—Ä–Ω—É—Ç—å—Å—è –Ω–∞–∑–∞–¥</button>
      </div>
    );
  }

  // –ó–∞–≥—Ä—É–∂–∞–µ–º –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –∏ —Å–æ–æ–±—â–µ–Ω–∏—è
  useEffect(() => {
    const loadChat = async () => {
      try {
        // –ó–∞–≥—Ä—É–∂–∞–µ–º —Å—É—â–µ—Å—Ç–≤—É—é—â–∏–µ —Å–æ–æ–±—â–µ–Ω–∏—è
        const existingMessages = getChat(userId);
        setMessages(existingMessages);

        // –ü–æ–ª—É—á–∞–µ–º –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ
        // –°–Ω–∞—á–∞–ª–∞ –ø—Ä–æ–≤–µ—Ä—è–µ–º localStorage –Ω–∞ –Ω–∞–ª–∏—á–∏–µ —Ä–µ–∞–ª—å–Ω—ã—Ö –¥–∞–Ω–Ω—ã—Ö
        const userInfo = await getUserInfo(userId);

        if (userInfo && userInfo.name && userInfo.name !== `–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å ${userId}`) {
          setUser(userInfo);
        } else {
          const mockUserInfo = {
            id: userId,
            name: `–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å ${userId}`,
            avatar: '',
            city: '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω—ã–π –≥–æ—Ä–æ–¥',
            lastSeen: new Date().toISOString()
          };
          setUser(mockUserInfo);
        }

        setIsLoading(false);
      } catch (error) {
        // Fallback –Ω–∞ –º–æ–∫–æ–≤—ã–µ –¥–∞–Ω–Ω—ã–µ
        const fallbackUser = {
          id: userId,
          name: `–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å ${userId}`,
          avatar: '',
          city: '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω—ã–π –≥–æ—Ä–æ–¥',
          lastSeen: new Date().toISOString()
        };
        setUser(fallbackUser);
        setIsLoading(false);
      }
    };

    loadChat();
  }, [userId]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // –û—á–∏—Å—Ç–∫–∞ —Ç–∞–π–º–∞—É—Ç–∞ –ø—Ä–∏ —Ä–∞–∑–º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏
  useEffect(() => {
    return () => {
      if (typingTimeout) {
        clearTimeout(typingTimeout);
      }
    };
  }, [typingTimeout]);

  const handleSendMessage = () => {
    if (newMessage.trim()) {
      const messageData = {
        text: newMessage.trim(),
        sender: 'user'
      };

      // –°–æ—Ö—Ä–∞–Ω—è–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –≤ localStorage
      const savedMessage = saveMessage(userId, messageData);

      if (savedMessage) {
        // –î–æ–±–∞–≤–ª—è–µ–º –∫–ª–∞—Å—Å –¥–ª—è –∞–Ω–∏–º–∞—Ü–∏–∏ –Ω–æ–≤–æ–≥–æ —Å–æ–æ–±—â–µ–Ω–∏—è
        const messageWithAnimation = { ...savedMessage, isNew: true };
        setMessages(prev => [...prev, messageWithAnimation]);
        setNewMessage('');

        // –£–±–∏—Ä–∞–µ–º –∞–Ω–∏–º–∞—Ü–∏—é —á–µ—Ä–µ–∑ –Ω–µ–∫–æ—Ç–æ—Ä–æ–µ –≤—Ä–µ–º—è
        setTimeout(() => {
          setMessages(prev => prev.map(msg => 
            msg.id === savedMessage.id ? { ...msg, isNew: false } : msg
          ));
        }, 500);
      }

      // –û—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –∏–Ω–¥–∏–∫–∞—Ç–æ—Ä –ø–µ—á–∞—Ç–∏
      setIsTyping(false);
      if (typingTimeout) {
        clearTimeout(typingTimeout);
        setTypingTimeout(null);
      }
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleInputChange = (e) => {
    setNewMessage(e.target.value);

    // –ü–æ–∫–∞–∑—ã–≤–∞–µ–º –∏–Ω–¥–∏–∫–∞—Ç–æ—Ä –ø–µ—á–∞—Ç–∏
    if (!isTyping) {
      setIsTyping(true);
    }

    // –û—á–∏—â–∞–µ–º –ø—Ä–µ–¥—ã–¥—É—â–∏–π —Ç–∞–π–º–∞—É—Ç
    if (typingTimeout) {
      clearTimeout(typingTimeout);
    }

    // –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –Ω–æ–≤—ã–π —Ç–∞–π–º–∞—É—Ç –¥–ª—è —Å–∫—Ä—ã—Ç–∏—è –∏–Ω–¥–∏–∫–∞—Ç–æ—Ä–∞ –ø–µ—á–∞—Ç–∏
    const timeout = setTimeout(() => {
      setIsTyping(false);
    }, 1000);

    setTypingTimeout(timeout);
  };

  const handleEditMessage = (messageId) => {
    const message = messages.find(msg => msg.id === messageId);
    if (message) {
      setEditingMessageId(messageId);
      setEditText(message.text);
    }
  };

  const handleSaveEdit = () => {
    if (editText.trim() && editingMessageId) {
      // –û–±–Ω–æ–≤–ª—è–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –≤ localStorage
      const success = updateMessage(userId, editingMessageId, editText.trim());

      if (success) {
        setMessages(prev => prev.map(msg => 
          msg.id === editingMessageId 
            ? { ...msg, text: editText.trim(), isEdited: true, editedAt: new Date().toISOString() }
            : msg
        ));
        setEditingMessageId(null);
        setEditText('');
      }
    }
  };

  const handleCancelEdit = () => {
    setEditingMessageId(null);
    setEditText('');
  };

  const handleDeleteMessage = (messageId) => {
    if (window.confirm('–í—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ —É–¥–∞–ª–∏—Ç—å —ç—Ç–æ —Å–æ–æ–±—â–µ–Ω–∏–µ?')) {
      // –£–¥–∞–ª—è–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –∏–∑ localStorage
      const success = deleteMessage(userId, messageId);

      if (success) {
        setMessages(prev => prev.filter(msg => msg.id !== messageId));
      }
    }
  };

  // –§—É–Ω–∫—Ü–∏–∏ –¥–ª—è –∫–æ–Ω—Ç–µ–∫—Å—Ç–Ω–æ–≥–æ –º–µ–Ω—é
  const handleMessageContextMenu = (e, messageId) => {
    e.preventDefault();
    e.stopPropagation();

    const message = messages.find(msg => msg.id === messageId);
    if (message && message.sender === 'user') {
      const rect = e.currentTarget.getBoundingClientRect();
      setContextMenu({
        messageId,
        x: e.clientX,
        y: e.clientY,
        messageRect: rect
      });
    }
  };

  const handleContextMenuAction = (action, messageId) => {
    setContextMenu(null);

    if (action === 'edit') {
      handleEditMessage(messageId);
    } else if (action === 'delete') {
      handleDeleteMessage(messageId);
    }
  };

  const closeContextMenu = () => {
    setContextMenu(null);
  };

  // –§—É–Ω–∫—Ü–∏–∏ –¥–ª—è –º–æ–¥–∞–ª—å–Ω–æ–≥–æ –æ–∫–Ω–∞
  const handleMessageClick = (messageId) => {
    const message = messages.find(msg => msg.id === messageId);
    if (message && message.sender === 'user') {
      setModalMessage(message);
    }
  };

  const closeModal = () => {
    setModalMessage(null);
  };

  const handleModalEdit = () => {
    if (modalMessage) {
      setEditingMessageId(modalMessage.id);
      setEditText(modalMessage.text);
      setModalMessage(null);
    }
  };

  const handleModalDelete = () => {
    if (modalMessage) {
      handleDeleteMessage(modalMessage.id);
      setModalMessage(null);
    }
  };

  const handleModalReply = () => {
    if (modalMessage) {
      setReplyToMessage(modalMessage);
      setModalMessage(null);
    }
  };

  // –§—É–Ω–∫—Ü–∏–∏ –¥–ª—è —Å–≤–∞–π–ø–∞
  const handleTouchStart = (e, messageId) => {
    const message = messages.find(msg => msg.id === messageId);
    if (message && message.sender !== 'user') {
      setSwipeStartX(e.touches[0].clientX);
      setSwipeStartY(e.touches[0].clientY);
    }
  };

  const handleTouchEnd = (e, messageId) => {
    if (swipeStartX === null) return;

    const swipeEndX = e.changedTouches[0].clientX;
    const swipeEndY = e.changedTouches[0].clientY;
    const deltaX = swipeStartX - swipeEndX;
    const deltaY = Math.abs(swipeStartY - swipeEndY);

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ —ç—Ç–æ —Å–≤–∞–π–ø –≤–ª–µ–≤–æ (deltaX > 50) –∏ –Ω–µ –≤–µ—Ä—Ç–∏–∫–∞–ª—å–Ω—ã–π (deltaY < 100)
    if (deltaX > 50 && deltaY < 100) {
      const message = messages.find(msg => msg.id === messageId);
      if (message) {
        setReplyToMessage(message);
      }
    }

    setSwipeStartX(null);
    setSwipeStartY(null);
  };

  // –ó–∞–∫—Ä—ã—Ç–∏–µ –∫–æ–Ω—Ç–µ–∫—Å—Ç–Ω–æ–≥–æ –º–µ–Ω—é –ø—Ä–∏ –∫–ª–∏–∫–µ –≤–Ω–µ –µ–≥–æ
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (contextMenu && !e.target.closest('.context-menu')) {
        closeContextMenu();
      }
    };

    if (contextMenu) {
      document.addEventListener('click', handleClickOutside);
      document.addEventListener('contextmenu', handleClickOutside);
    }

    return () => {
      document.removeEventListener('click', handleClickOutside);
      document.removeEventListener('contextmenu', handleClickOutside);
    };
  }, [contextMenu]);

  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString('ru-RU', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const formatDate = (timestamp) => {
    const date = new Date(timestamp);
    const today = new Date();
    const yesterday = new Date(today);
    yesterday.setDate(yesterday.getDate() - 1);

    if (date.toDateString() === today.toDateString()) {
      return '–°–µ–≥–æ–¥–Ω—è';
    } else if (date.toDateString() === yesterday.toDateString()) {
      return '–í—á–µ—Ä–∞';
    } else {
      return date.toLocaleDateString('ru-RU');
    }
  };

  if (isLoading) {
    return (
      <div className="chat-loading">
        <div className="loading-spinner"></div>
        <p>–ó–∞–≥—Ä—É–∑–∫–∞ —á–∞—Ç–∞...</p>
      </div>
    );
  }

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ —É –Ω–∞—Å –µ—Å—Ç—å –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ
  if (!user) {
    // Fallback –Ω–∞ –º–æ–∫–æ–≤—ã–µ –¥–∞–Ω–Ω—ã–µ –ø—Ä—è–º–æ –≤ —Ä–µ–Ω–¥–µ—Ä–µ
    const fallbackUser = {
      id: userId,
      name: `–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å ${userId}`,
      avatar: '',
      city: '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω—ã–π –≥–æ—Ä–æ–¥',
      lastSeen: new Date().toISOString()
    };
    return (
      <div className="chat-loading">
        <div className="loading-spinner"></div>
        <p>–ó–∞–≥—Ä—É–∑–∫–∞ –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–∏ –æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ...</p>
        <button 
          onClick={() => setUser(fallbackUser)}
          style={{ 
            marginTop: '20px', 
            padding: '10px 20px', 
            background: '#667eea', 
            color: 'white', 
            border: 'none', 
            borderRadius: '8px',
            cursor: 'pointer'
          }}
        >
          –ü—Ä–∏–Ω—É–¥–∏—Ç–µ–ª—å–Ω–æ –∑–∞–≥—Ä—É–∑–∏—Ç—å –¥–∞–Ω–Ω—ã–µ
        </button>
      </div>
    );
  }

  return (
    <div className="chat-page">
      {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ —á–∞—Ç–∞ */}
      <div className="chat-header">
        <button className="back-button" onClick={() => navigate(-1)}>
          <FontAwesomeIcon icon={faArrowLeft} />
        </button>

        <div className="chat-user-info">
          <div className="user-avatar">
            {user?.avatar ? (
              <img src={user.avatar} alt={user.name} />
            ) : (
              <div className="avatar-placeholder">
                <FontAwesomeIcon icon={faUser} />
              </div>
            )}
          </div>

          <div className="user-details">
            <h3 className="user-name">
              {user?.name || '–ó–∞–≥—Ä—É–∑–∫–∞...'}
            </h3>
            {user?.city && (
              <span className="user-city">
                üìç {user.city}
              </span>
            )}
          </div>
        </div>

        {/* –£–±–∏—Ä–∞–µ–º —Å—Ç–∞—Ç—É—Å –æ–Ω–ª–∞–π–Ω */}
      </div>

      {/* –ö–æ–Ω—Ç–µ–π–Ω–µ—Ä —Å–æ–æ–±—â–µ–Ω–∏–π */}
      <div className="messages-container">
        {messages.length === 0 ? (
          <div className="empty-chat">
            <div className="empty-chat-icon">
              <FontAwesomeIcon icon={faMessage} />
            </div>
            <h3>üí¨ –ß–∞—Ç –ø—É—Å—Ç</h3>
            <p>–ù–∞—á–Ω–∏—Ç–µ —Ä–∞–∑–≥–æ–≤–æ—Ä, –æ—Ç–ø—Ä–∞–≤–∏–≤ –ø–µ—Ä–≤–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ</p>
            <div className="empty-chat-features">
              <span>‚ú® –û—Ç–ø—Ä–∞–≤–ª—è–π—Ç–µ —Å–æ–æ–±—â–µ–Ω–∏—è</span>
              <span>‚úèÔ∏è –†–µ–¥–∞–∫—Ç–∏—Ä—É–π—Ç–µ –∏—Ö</span>
              <span>üóëÔ∏è –£–¥–∞–ª—è–π—Ç–µ –ø—Ä–∏ –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ—Å—Ç–∏</span>
            </div>
          </div>
        ) : (
          <div className="messages-list">
            {messages.map((message) => {
              const messageClass = message.sender === 'user' ? 'my-message' : 'incoming-message';
              return (
              <div 
                key={message.id} 
                className={`message ${messageClass} ${message.isNew ? 'new-message' : ''}`}
                onContextMenu={(e) => handleMessageContextMenu(e, message.id)}
                onClick={(e) => {
                  // –ù–∞ –º–æ–±–∏–ª—å–Ω—ã—Ö —É—Å—Ç—Ä–æ–π—Å—Ç–≤–∞—Ö –ø–æ–∫–∞–∑—ã–≤–∞–µ–º –∫–æ–Ω—Ç–µ–∫—Å—Ç–Ω–æ–µ –º–µ–Ω—é –ø–æ –æ–±—ã—á–Ω–æ–º—É –∫–ª–∏–∫—É
                  if (window.innerWidth <= 768 && message.sender === 'user') {
                    e.preventDefault();
                    handleMessageContextMenu(e, message.id);
                  }
                }}
              >
                {editingMessageId === message.id ? (
                  <div className="edit-message-form">
                    <textarea
                      className="edit-textarea"
                      value={editText}
                      onChange={(e) => setEditText(e.target.value)}
                      placeholder="–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å —Å–æ–æ–±—â–µ–Ω–∏–µ..."
                      autoFocus
                    />
                    <div className="edit-actions">
                      <button 
                        className="edit-btn save-btn" 
                        onClick={handleSaveEdit}
                      >
                        <FontAwesomeIcon icon={faCheck} />
                      </button>
                      <button 
                        className="edit-btn cancel-btn" 
                        onClick={handleCancelEdit}
                      >
                        <FontAwesomeIcon icon={faTimes} />
                      </button>
                    </div>
                  </div>
                ) : (
                  <>
                    <div className="message-content">
                      <p className="message-text">{message.text}</p>
                      {message.isEdited && (
                        <span className="edited-indicator">(–∏–∑–º–µ–Ω–µ–Ω–æ)</span>
                      )}
                      <div className="message-meta">
                        <span className="message-time">{formatTime(message.timestamp)}</span>
                      </div>
                    </div>
                  </>
                )}
              </div>
              );
            })}
            <div ref={messagesEndRef} />
          </div>
        )}

        {/* –ò–Ω–¥–∏–∫–∞—Ç–æ—Ä –ø–µ—á–∞—Ç–∏ */}
        {isTyping && (
          <div className="typing-indicator">
            <div className="typing-dot"></div>
            <div className="typing-dot"></div>
            <div className="typing-dot"></div>
          </div>
        )}
      </div>

      {/* –§–æ—Ä–º–∞ –≤–≤–æ–¥–∞ —Å–æ–æ–±—â–µ–Ω–∏—è */}
      <div className="message-input-container">
        <div className="message-input-wrapper">
          <textarea
            className="message-input"
            value={newMessage}
            onChange={handleInputChange}
            onKeyPress={handleKeyPress}
            placeholder="üí¨ –ù–∞—á–Ω–∏—Ç–µ —Ä–∞–∑–≥–æ–≤–æ—Ä..."
            rows="1"
          />
          <button 
            className="send-button" 
            onClick={handleSendMessage}
            disabled={!newMessage.trim()}
          >
            <FontAwesomeIcon icon={faPaperPlane} />
          </button>
        </div>
      </div>

      {/* –ö–æ–Ω—Ç–µ–∫—Å—Ç–Ω–æ–µ –º–µ–Ω—é */}
      {contextMenu && (
        <>
          <div className="context-menu-overlay" onClick={closeContextMenu} />
          <div 
            className="context-menu"
            style={{
              left: contextMenu.x,
              top: contextMenu.y
            }}
          >
            <button 
              className="context-menu-item edit-item"
              onClick={() => handleContextMenuAction('edit', contextMenu.messageId)}
            >
              <FontAwesomeIcon icon={faEdit} className="icon" />
              –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å
            </button>
            <button 
              className="context-menu-item delete-item"
              onClick={() => handleContextMenuAction('delete', contextMenu.messageId)}
            >
              <FontAwesomeIcon icon={faTrash} className="icon" />
              –£–¥–∞–ª–∏—Ç—å
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default ChatPage;
