import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft, 
  faPaperPlane, 
  faEdit, 
  faTrash, 
  faCheck, 
  faTimes,
  faUser,
  faMessage,
  faReply,
  faImage,
  faFile,
  faMicrophone,
  faPaperclip,
  faSmile,
  faEllipsisV,
  faPhone,
  faVideo,
  faSearch,
  faDownload,
  faShare,
  faCopy,
  faForward,
  faClock,
  faCheckDouble
} from '@fortawesome/free-solid-svg-icons';
import { apiClient } from '../../utils/api';
import './TelegramChat.css';

const TelegramChat = () => {
  const { chatId } = useParams();
  const navigate = useNavigate();
  const messagesEndRef = useRef(null);
  const fileInputRef = useRef(null);
  const textareaRef = useRef(null);
  
  // ÐžÑÐ½Ð¾Ð²Ð½Ñ‹Ðµ ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸Ñ
  const [chat, setChat] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [isTyping, setIsTyping] = useState(false);
  const [typingTimeout, setTypingTimeout] = useState(null);
  
  // Ð ÐµÐ´Ð°ÐºÑ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ðµ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ð¹
  const [editingMessageId, setEditingMessageId] = useState(null);
  const [editText, setEditText] = useState('');
  
  // ÐšÐ¾Ð½Ñ‚ÐµÐºÑÑ‚Ð½Ð¾Ðµ Ð¼ÐµÐ½ÑŽ Ð¸ Ð¼Ð¾Ð´Ð°Ð»ÑŒÐ½Ñ‹Ðµ Ð¾ÐºÐ½Ð°
  const [contextMenu, setContextMenu] = useState(null);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [showAttachmentMenu, setShowAttachmentMenu] = useState(false);
  
  // Ð¤Ð°Ð¹Ð»Ñ‹ Ð¸ Ð¼ÐµÐ´Ð¸Ð°
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [uploadingFiles, setUploadingFiles] = useState(false);
  
  // ÐžÑ‚Ð²ÐµÑ‚Ñ‹ Ð½Ð° ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ñ
  const [replyToMessage, setReplyToMessage] = useState(null);
  
  // ÐŸÐ°Ð³Ð¸Ð½Ð°Ñ†Ð¸Ñ
  const [currentPage, setCurrentPage] = useState(1);
  const [hasMoreMessages, setHasMoreMessages] = useState(true);
  const [loadingMore, setLoadingMore] = useState(false);

  // Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° Ñ‡Ð°Ñ‚Ð° Ð¿Ñ€Ð¸ Ð¼Ð¾Ð½Ñ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ð¸ ÐºÐ¾Ð¼Ð¿Ð¾Ð½ÐµÐ½Ñ‚Ð°
  useEffect(() => {
    if (chatId) {
      loadChat();
    }
  }, [chatId]);

  // ÐÐ²Ñ‚Ð¾ÑÐºÑ€Ð¾Ð»Ð» Ðº Ð¿Ð¾ÑÐ»ÐµÐ´Ð½ÐµÐ¼Ñƒ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸ÑŽ
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ° ÐºÐ»Ð¸ÐºÐ° Ð²Ð½Ðµ ÐºÐ¾Ð½Ñ‚ÐµÐºÑÑ‚Ð½Ð¾Ð³Ð¾ Ð¼ÐµÐ½ÑŽ
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (contextMenu && !event.target.closest('.context-menu')) {
        setContextMenu(null);
      }
      if (showEmojiPicker && !event.target.closest('.emoji-picker')) {
        setShowEmojiPicker(false);
      }
      if (showAttachmentMenu && !event.target.closest('.attachment-menu')) {
        setShowAttachmentMenu(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [contextMenu, showEmojiPicker, showAttachmentMenu]);

  // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ñ ÑÐºÑ€Ð¾Ð»Ð»Ð° Ðº Ð½Ð¸Ð·Ñƒ
  const scrollToBottom = () => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  // Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° Ñ‡Ð°Ñ‚Ð°
  const loadChat = async () => {
    try {
      setIsLoading(true);
      const response = await apiClient.request(`/users/chats/${chatId}/`);
      
      if (response.success) {
        setChat(response.chat);
        setMessages(response.messages); // Ð¡Ð¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ñ ÑƒÐ¶Ðµ Ð² Ð¿Ñ€Ð°Ð²Ð¸Ð»ÑŒÐ½Ð¾Ð¼ Ð¿Ð¾Ñ€ÑÐ´ÐºÐµ
        setHasMoreMessages(response.pagination.has_next);
      } else {
        console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸ Ñ‡Ð°Ñ‚Ð°:', response.message);
        navigate('/chats');
      }
    } catch (error) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸ Ñ‡Ð°Ñ‚Ð°:', error);
      navigate('/chats');
    } finally {
      setIsLoading(false);
    }
  };

  // Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° Ð´Ð¾Ð¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ñ‹Ñ… ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ð¹
  const loadMoreMessages = async () => {
    if (loadingMore || !hasMoreMessages) return;
    
    try {
      setLoadingMore(true);
      const nextPage = currentPage + 1;
      const response = await apiClient.request(`/users/chats/${chatId}/?page=${nextPage}`);
      
      if (response.success) {
        const newMessages = response.messages;
        setMessages(prev => [...newMessages, ...prev]);
        setCurrentPage(nextPage);
        setHasMoreMessages(response.pagination.has_next);
      }
    } catch (error) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸ Ð´Ð¾Ð¿Ð¾Ð»Ð½Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ñ‹Ñ… ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ð¹:', error);
    } finally {
      setLoadingMore(false);
    }
  };

  // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ° Ð¾Ñ‚Ð¿Ñ€Ð°Ð²ÐºÐ¸ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ñ
  const handleSendMessage = async () => {
    if (!newMessage.trim() && selectedFiles.length === 0) return;

    const formData = new FormData();
    formData.append('content', newMessage.trim());
    
    if (replyToMessage) {
      formData.append('reply_to', replyToMessage.id);
    }

    // Ð”Ð¾Ð±Ð°Ð²Ð»ÑÐµÐ¼ Ñ„Ð°Ð¹Ð»Ñ‹
    selectedFiles.forEach((file, index) => {
      formData.append('file', file.file);
    });

    try {
      const response = await apiClient.request(`/users/chats/${chatId}/messages/`, {
        method: 'POST',
        body: formData,
        headers: {
          'Authorization': `Bearer ${apiClient.token}`
        }
      });

      if (response.success) {
        setMessages(prev => [...prev, response.message]);
        setNewMessage('');
        setSelectedFiles([]);
        setReplyToMessage(null);
        setShowAttachmentMenu(false);
        
        // ÐžÐ±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸ÑŽ Ð¾ Ñ‡Ð°Ñ‚Ðµ
        if (chat) {
          setChat(prev => ({
            ...prev,
            last_message: response.message,
            updated_at: new Date().toISOString()
          }));
        }
      }
    } catch (error) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¾Ñ‚Ð¿Ñ€Ð°Ð²ÐºÐ¸ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ñ:', error);
    }
  };

  // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ° Ð½Ð°Ð¶Ð°Ñ‚Ð¸Ñ Enter
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ° Ð¸Ð·Ð¼ÐµÐ½ÐµÐ½Ð¸Ñ Ñ‚ÐµÐºÑÑ‚Ð° (Ð¸Ð½Ð´Ð¸ÐºÐ°Ñ‚Ð¾Ñ€ Ð½Ð°Ð±Ð¾Ñ€Ð°)
  const handleTextChange = (e) => {
    setNewMessage(e.target.value);
    
    // ÐÐ²Ñ‚Ð¾Ð¼Ð°Ñ‚Ð¸Ñ‡ÐµÑÐºÐ¾Ðµ Ð¸Ð·Ð¼ÐµÐ½ÐµÐ½Ð¸Ðµ Ð²Ñ‹ÑÐ¾Ñ‚Ñ‹ textarea
    if (textareaRef.current) {
      textareaRef.current.style.height = 'auto';
      textareaRef.current.style.height = textareaRef.current.scrollHeight + 'px';
    }
    
    // ÐŸÐ¾ÐºÐ°Ð·Ñ‹Ð²Ð°ÐµÐ¼ Ð¸Ð½Ð´Ð¸ÐºÐ°Ñ‚Ð¾Ñ€ Ð½Ð°Ð±Ð¾Ñ€Ð° Ñ‚ÐµÐºÑÑ‚Ð°
    if (!isTyping) {
      setIsTyping(true);
    }
    
    // Ð¡Ð±Ñ€Ð°ÑÑ‹Ð²Ð°ÐµÐ¼ Ñ‚Ð°Ð¹Ð¼ÐµÑ€
    if (typingTimeout) {
      clearTimeout(typingTimeout);
    }
    
    // Ð¡ÐºÑ€Ñ‹Ð²Ð°ÐµÐ¼ Ð¸Ð½Ð´Ð¸ÐºÐ°Ñ‚Ð¾Ñ€ Ñ‡ÐµÑ€ÐµÐ· 2 ÑÐµÐºÑƒÐ½Ð´Ñ‹
    const timeout = setTimeout(() => {
      setIsTyping(false);
    }, 2000);
    
    setTypingTimeout(timeout);
  };

  // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ° Ð²Ñ‹Ð±Ð¾Ñ€Ð° Ñ„Ð°Ð¹Ð»Ð¾Ð²
  const handleFileSelect = (e) => {
    const files = Array.from(e.target.files);
    const validFiles = files.filter(file => {
      const maxSize = 10 * 1024 * 1024; // 10MB
      if (file.size > maxSize) {
        alert(`Ð¤Ð°Ð¹Ð» ${file.name} ÑÐ»Ð¸ÑˆÐºÐ¾Ð¼ Ð±Ð¾Ð»ÑŒÑˆÐ¾Ð¹. ÐœÐ°ÐºÑÐ¸Ð¼Ð°Ð»ÑŒÐ½Ñ‹Ð¹ Ñ€Ð°Ð·Ð¼ÐµÑ€: 10MB`);
        return false;
      }
      return true;
    });

    const fileObjects = validFiles.map(file => ({
      id: Date.now() + Math.random(),
      name: file.name,
      size: file.size,
      type: file.type,
      file: file
    }));

    setSelectedFiles(prev => [...prev, ...fileObjects]);
    setShowAttachmentMenu(false);
    
    // ÐžÑ‡Ð¸Ñ‰Ð°ÐµÐ¼ input
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  // Ð£Ð´Ð°Ð»ÐµÐ½Ð¸Ðµ Ð²Ñ‹Ð±Ñ€Ð°Ð½Ð½Ð¾Ð³Ð¾ Ñ„Ð°Ð¹Ð»Ð°
  const removeFile = (fileId) => {
    setSelectedFiles(prev => prev.filter(file => file.id !== fileId));
  };

  // ÐŸÐ¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ðµ Ð¸ÐºÐ¾Ð½ÐºÐ¸ Ð´Ð»Ñ Ñ‚Ð¸Ð¿Ð° Ñ„Ð°Ð¹Ð»Ð°
  const getFileIcon = (fileType) => {
    if (fileType.startsWith('image/')) return faImage;
    return faFile;
  };

  // Ð¤Ð¾Ñ€Ð¼Ð°Ñ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ðµ Ñ€Ð°Ð·Ð¼ÐµÑ€Ð° Ñ„Ð°Ð¹Ð»Ð°
  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ° ÐºÐ¾Ð½Ñ‚ÐµÐºÑÑ‚Ð½Ð¾Ð³Ð¾ Ð¼ÐµÐ½ÑŽ
  const handleContextMenu = (e, message) => {
    e.preventDefault();
    setContextMenu({
      x: e.clientX,
      y: e.clientY,
      message: message
    });
  };

  // ÐÐ°Ñ‡Ð°Ð»Ð¾ Ñ€ÐµÐ´Ð°ÐºÑ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ñ
  const startEditing = (message) => {
    setEditingMessageId(message.id);
    setEditText(message.content);
    setContextMenu(null);
  };

  // Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð¸Ðµ Ñ€ÐµÐ´Ð°ÐºÑ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ
  const saveEdit = async () => {
    if (!editText.trim()) return;

    try {
      const response = await apiClient.request(`/users/chats/${chatId}/messages/${editingMessageId}/`, {
        method: 'PUT',
        body: JSON.stringify({ content: editText.trim() })
      });

      if (response.success) {
        setMessages(prev => prev.map(m => 
          m.id === editingMessageId ? response.message : m
        ));
        setEditingMessageId(null);
        setEditText('');
      }
    } catch (error) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ñ€ÐµÐ´Ð°ÐºÑ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ñ:', error);
    }
  };

  // ÐžÑ‚Ð¼ÐµÐ½Ð° Ñ€ÐµÐ´Ð°ÐºÑ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ
  const cancelEdit = () => {
    setEditingMessageId(null);
    setEditText('');
  };

  // Ð£Ð´Ð°Ð»ÐµÐ½Ð¸Ðµ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ñ
  const handleDeleteMessage = async (message) => {
    try {
      const response = await apiClient.request(`/users/chats/${chatId}/messages/${message.id}/delete/`, {
        method: 'DELETE'
      });

      if (response.success) {
        setMessages(prev => prev.filter(m => m.id !== message.id));
      }
    } catch (error) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° ÑƒÐ´Ð°Ð»ÐµÐ½Ð¸Ñ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ñ:', error);
    }
    setContextMenu(null);
  };

  // ÐžÑ‚Ð²ÐµÑ‚ Ð½Ð° ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ðµ
  const handleReply = (message) => {
    setReplyToMessage(message);
    setContextMenu(null);
  };

  // Ð£Ð´Ð°Ð»ÐµÐ½Ð¸Ðµ Ð¾Ñ‚Ð²ÐµÑ‚Ð°
  const removeReply = () => {
    setReplyToMessage(null);
  };

  // Ð¤Ð¾Ñ€Ð¼Ð°Ñ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ðµ Ð²Ñ€ÐµÐ¼ÐµÐ½Ð¸
  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString('ru-RU', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  // Ð¤Ð¾Ñ€Ð¼Ð°Ñ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ðµ Ð´Ð°Ñ‚Ñ‹
  const formatDate = (timestamp) => {
    const date = new Date(timestamp);
    const today = new Date();
    const yesterday = new Date(today);
    yesterday.setDate(yesterday.getDate() - 1);

    if (date.toDateString() === today.toDateString()) {
      return 'Ð¡ÐµÐ³Ð¾Ð´Ð½Ñ';
    } else if (date.toDateString() === yesterday.toDateString()) {
      return 'Ð’Ñ‡ÐµÑ€Ð°';
    } else {
      return date.toLocaleDateString('ru-RU', { 
        day: 'numeric', 
        month: 'long' 
      });
    }
  };

  // Ð”Ð¾Ð±Ð°Ð²Ð»ÐµÐ½Ð¸Ðµ ÑÐ¼Ð¾Ð´Ð·Ð¸
  const addEmoji = (emoji) => {
    setNewMessage(prev => prev + emoji);
    setShowEmojiPicker(false);
  };

  // Ð­Ð¼Ð¾Ð´Ð·Ð¸ Ð´Ð»Ñ Ð²Ñ‹Ð±Ð¾Ñ€Ð°
  const emojis = ['ðŸ˜€', 'ðŸ˜ƒ', 'ðŸ˜„', 'ðŸ˜', 'ðŸ˜†', 'ðŸ˜…', 'ðŸ˜‚', 'ðŸ¤£', 'ðŸ˜Š', 'ðŸ˜‡', 'ðŸ™‚', 'ðŸ™ƒ', 'ðŸ˜‰', 'ðŸ˜Œ', 'ðŸ˜', 'ðŸ¥°', 'ðŸ˜˜', 'ðŸ˜—', 'ðŸ˜™', 'ðŸ˜š', 'ðŸ˜‹', 'ðŸ˜›', 'ðŸ˜', 'ðŸ˜œ', 'ðŸ¤ª', 'ðŸ¤¨', 'ðŸ§', 'ðŸ¤“', 'ðŸ˜Ž', 'ðŸ¤©', 'ðŸ¥³', 'ðŸ˜', 'ðŸ˜’', 'ðŸ˜ž', 'ðŸ˜”', 'ðŸ˜Ÿ', 'ðŸ˜•', 'ðŸ™', 'â˜¹ï¸', 'ðŸ˜£', 'ðŸ˜–', 'ðŸ˜«', 'ðŸ˜©', 'ðŸ¥º', 'ðŸ˜¢', 'ðŸ˜­', 'ðŸ˜¤', 'ðŸ˜ ', 'ðŸ˜¡', 'ðŸ¤¬', 'ðŸ¤¯', 'ðŸ˜³', 'ðŸ¥µ', 'ðŸ¥¶', 'ðŸ˜±', 'ðŸ˜¨', 'ðŸ˜°', 'ðŸ˜¥', 'ðŸ˜“', 'ðŸ¤—', 'ðŸ¤”', 'ðŸ¤­', 'ðŸ¤«', 'ðŸ¤¥', 'ðŸ˜¶', 'ðŸ˜', 'ðŸ˜‘', 'ðŸ˜¬', 'ðŸ™„', 'ðŸ˜¯', 'ðŸ˜¦', 'ðŸ˜§', 'ðŸ˜®', 'ðŸ˜²', 'ðŸ¥±', 'ðŸ˜´', 'ðŸ¤¤', 'ðŸ˜ª', 'ðŸ˜µ', 'ðŸ¤', 'ðŸ¥´', 'ðŸ¤¢', 'ðŸ¤®', 'ðŸ¤§', 'ðŸ˜·', 'ðŸ¤’', 'ðŸ¤•', 'ðŸ¤‘', 'ðŸ¤ ', 'ðŸ˜ˆ', 'ðŸ‘¿', 'ðŸ‘¹', 'ðŸ‘º', 'ðŸ¤¡', 'ðŸ’©', 'ðŸ‘»', 'ðŸ’€', 'â˜ ï¸', 'ðŸ‘½', 'ðŸ‘¾', 'ðŸ¤–', 'ðŸŽƒ', 'ðŸ˜º', 'ðŸ˜¸', 'ðŸ˜¹', 'ðŸ˜»', 'ðŸ˜¼', 'ðŸ˜½', 'ðŸ™€', 'ðŸ˜¿', 'ðŸ˜¾'];

  if (isLoading) {
    return (
      <div className="telegram-chat">
        <div className="chat-loading">
          <div className="loading-spinner"></div>
          <p>Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° Ñ‡Ð°Ñ‚Ð°...</p>
        </div>
      </div>
    );
  }

  if (!chat) {
    return (
      <div className="telegram-chat">
        <div className="chat-error">
          <FontAwesomeIcon icon={faMessage} className="error-icon" />
          <p>Ð§Ð°Ñ‚ Ð½Ðµ Ð½Ð°Ð¹Ð´ÐµÐ½</p>
          <button onClick={() => navigate('/chats')} className="back-button">
            Ð’ÐµÑ€Ð½ÑƒÑ‚ÑŒÑÑ Ðº Ñ‡Ð°Ñ‚Ð°Ð¼
          </button>
        </div>
      </div>
    );
  }

  const otherUser = chat.other_participant;

  return (
    <div className="telegram-chat">
      {/* Ð—Ð°Ð³Ð¾Ð»Ð¾Ð²Ð¾Ðº Ñ‡Ð°Ñ‚Ð° */}
      <div className="chat-header">
        <div className="chat-header-left">
          <button className="back-button" onClick={() => navigate('/chats')}>
            <FontAwesomeIcon icon={faArrowLeft} />
          </button>
          <div className="chat-user-info">
            <div className="user-avatar">
              {otherUser?.avatar ? (
                <img src={otherUser.avatar} alt={otherUser.first_name} />
              ) : (
                <div className="avatar-placeholder">
                  <FontAwesomeIcon icon={faUser} />
                </div>
              )}
            </div>
            <div className="user-details">
              <h3 className="user-name">{otherUser?.first_name || 'ÐŸÐ¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒ'}</h3>
              <span className="user-status">Ð² ÑÐµÑ‚Ð¸</span>
            </div>
          </div>
        </div>
        <div className="chat-header-right">
          <button className="header-button" title="ÐŸÐ¾Ð¸ÑÐº">
            <FontAwesomeIcon icon={faSearch} />
          </button>
          <button className="header-button" title="Ð—Ð²Ð¾Ð½Ð¾Ðº">
            <FontAwesomeIcon icon={faPhone} />
          </button>
          <button className="header-button" title="Ð’Ð¸Ð´ÐµÐ¾Ð·Ð²Ð¾Ð½Ð¾Ðº">
            <FontAwesomeIcon icon={faVideo} />
          </button>
          <button className="header-button" title="ÐœÐµÐ½ÑŽ">
            <FontAwesomeIcon icon={faEllipsisV} />
          </button>
        </div>
      </div>

      {/* ÐšÐ¾Ð½Ñ‚ÐµÐ¹Ð½ÐµÑ€ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ð¹ */}
      <div className="messages-container" onScroll={(e) => {
        if (e.target.scrollTop === 0 && hasMoreMessages && !loadingMore) {
          loadMoreMessages();
        }
      }}>
        {loadingMore && (
          <div className="loading-more">
            <div className="loading-spinner"></div>
          </div>
        )}
        
        {messages.length === 0 ? (
          <div className="empty-chat">
            <FontAwesomeIcon icon={faMessage} className="empty-chat-icon" />
            <div className="empty-chat-text">ÐÐ°Ñ‡Ð½Ð¸Ñ‚Ðµ Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ðµ</div>
            <div className="empty-chat-subtext">ÐžÑ‚Ð¿Ñ€Ð°Ð²ÑŒÑ‚Ðµ Ð¿ÐµÑ€Ð²Ð¾Ðµ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ðµ</div>
          </div>
        ) : (
          <div className="messages-list">
            {messages.map((message, index) => {
              const prevMessage = index > 0 ? messages[index - 1] : null;
              const showDate = !prevMessage || 
                formatDate(prevMessage.created_at) !== formatDate(message.created_at);
              
              return (
                <React.Fragment key={message.id}>
                  {showDate && (
                    <div className="date-separator">
                      {formatDate(message.created_at)}
                    </div>
                  )}
                  <div
                    className={`message ${message.sender_name === (chat.other_participant?.first_name || 'ÐŸÐ¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒ') ? 'incoming-message' : 'my-message'}`}
                    onContextMenu={(e) => handleContextMenu(e, message)}
                  >
                    <div className="message-content">
                      {/* ÐžÑ‚Ð²ÐµÑ‚ Ð½Ð° ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ðµ */}
                      {message.reply_to_message && (
                        <div className="reply-to-message">
                          <div className="reply-line"></div>
                          <div className="reply-content">
                            <div className="reply-author">{message.reply_to_message.sender_name}</div>
                            <div className="reply-text">{message.reply_to_message.content}</div>
                          </div>
                        </div>
                      )}

                      {/* Ð ÐµÐ´Ð°ÐºÑ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ðµ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ñ */}
                      {editingMessageId === message.id ? (
                        <div className="edit-message-form">
                          <textarea
                            value={editText}
                            onChange={(e) => setEditText(e.target.value)}
                            onKeyPress={(e) => {
                              if (e.key === 'Enter' && !e.shiftKey) {
                                e.preventDefault();
                                saveEdit();
                              }
                            }}
                            autoFocus
                            rows={3}
                          />
                          <div className="edit-message-actions">
                            <button className="save-btn" onClick={saveEdit}>
                              <FontAwesomeIcon icon={faCheck} />
                            </button>
                            <button className="cancel-btn" onClick={cancelEdit}>
                              <FontAwesomeIcon icon={faTimes} />
                            </button>
                          </div>
                        </div>
                      ) : (
                        <>
                          {/* Ð¤Ð°Ð¹Ð»Ñ‹ Ð² ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ð¸ */}
                          {message.file_url && (
                            <div className="message-file">
                              {message.message_type === 'image' ? (
                                <img src={message.file_url} alt="Ð˜Ð·Ð¾Ð±Ñ€Ð°Ð¶ÐµÐ½Ð¸Ðµ" className="message-image" />
                              ) : (
                                <div className="file-message">
                                  <FontAwesomeIcon 
                                    icon={getFileIcon(message.message_type)} 
                                    className="file-icon" 
                                  />
                                  <div className="file-info">
                                    <div className="file-name">{message.file_url.split('/').pop()}</div>
                                    <div className="file-size">{message.file_size}</div>
                                  </div>
                                  <button className="download-btn" title="Ð¡ÐºÐ°Ñ‡Ð°Ñ‚ÑŒ">
                                    <FontAwesomeIcon icon={faDownload} />
                                  </button>
                                </div>
                              )}
                            </div>
                          )}

                          {/* Ð¢ÐµÐºÑÑ‚ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ñ */}
                          {message.content && (
                            <p className="message-text">{message.content}</p>
                          )}

                          {/* ÐœÐµÑ‚Ð°-Ð¸Ð½Ñ„Ð¾Ñ€Ð¼Ð°Ñ†Ð¸Ñ */}
                          <div className="message-meta">
                            <span className="message-time">{formatTime(message.created_at)}</span>
                            {message.is_edited && (
                              <span className="edited-indicator">(Ð¸Ð·Ð¼ÐµÐ½ÐµÐ½Ð¾)</span>
                            )}
                            {message.sender_name !== (chat.other_participant?.first_name || 'ÐŸÐ¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒ') && (
                              <span className="message-status">
                                {message.is_read ? (
                                  <FontAwesomeIcon icon={faCheckDouble} className="read" />
                                ) : (
                                  <FontAwesomeIcon icon={faCheck} />
                                )}
                              </span>
                            )}
                          </div>
                        </>
                      )}
                    </div>
                  </div>
                </React.Fragment>
              );
            })}

            {/* Ð˜Ð½Ð´Ð¸ÐºÐ°Ñ‚Ð¾Ñ€ Ð½Ð°Ð±Ð¾Ñ€Ð° Ñ‚ÐµÐºÑÑ‚Ð° */}
            {isTyping && (
              <div className="typing-indicator">
                <div className="typing-avatar">
                  {otherUser?.avatar ? (
                    <img src={otherUser.avatar} alt={otherUser.first_name} />
                  ) : (
                    <div className="avatar-placeholder">
                      <FontAwesomeIcon icon={faUser} />
                    </div>
                  )}
                </div>
                <div className="typing-content">
                  <span className="typing-text">{otherUser?.first_name} Ð¿ÐµÑ‡Ð°Ñ‚Ð°ÐµÑ‚</span>
                  <div className="typing-dots">
                    <div className="typing-dot"></div>
                    <div className="typing-dot"></div>
                    <div className="typing-dot"></div>
                  </div>
                </div>
              </div>
            )}

            <div ref={messagesEndRef} />
          </div>
        )}
      </div>

      {/* ÐšÐ¾Ð½Ñ‚ÐµÐ¹Ð½ÐµÑ€ Ð²Ð²Ð¾Ð´Ð° ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ñ */}
      <div className="message-input-container">
        {/* ÐžÑ‚Ð²ÐµÑ‚ Ð½Ð° ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ðµ */}
        {replyToMessage && (
          <div className="reply-to-message">
            <div className="reply-line"></div>
            <div className="reply-content">
              <div className="reply-author">
                {replyToMessage.sender_name === (otherUser?.first_name || 'ÐŸÐ¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒ') ? otherUser?.first_name : 'Ð’Ñ‹'}
              </div>
              <div className="reply-text">{replyToMessage.content}</div>
            </div>
            <button className="reply-remove" onClick={removeReply}>
              <FontAwesomeIcon icon={faTimes} />
            </button>
          </div>
        )}

        {/* Ð’Ñ‹Ð±Ñ€Ð°Ð½Ð½Ñ‹Ðµ Ñ„Ð°Ð¹Ð»Ñ‹ */}
        {selectedFiles.length > 0 && (
          <div className="selected-files">
            {selectedFiles.map((file) => (
              <div key={file.id} className="selected-file">
                <FontAwesomeIcon 
                  icon={getFileIcon(file.type)} 
                  className="file-icon" 
                />
                <span className="file-name">{file.name}</span>
                <button 
                  className="remove-file-btn"
                  onClick={() => removeFile(file.id)}
                >
                  <FontAwesomeIcon icon={faTimes} />
                </button>
              </div>
            ))}
          </div>
        )}

        {/* Ð¤Ð¾Ñ€Ð¼Ð° Ð²Ð²Ð¾Ð´Ð° */}
        <div className="message-input-wrapper">
          <button 
            className="attachment-button"
            onClick={() => setShowAttachmentMenu(!showAttachmentMenu)}
            title="ÐŸÑ€Ð¸ÐºÑ€ÐµÐ¿Ð¸Ñ‚ÑŒ Ñ„Ð°Ð¹Ð»"
          >
            <FontAwesomeIcon icon={faPaperclip} />
          </button>
          
          <div className="input-container">
            <textarea
              ref={textareaRef}
              className="message-input"
              placeholder="Ð¡Ð¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ðµ"
              value={newMessage}
              onChange={handleTextChange}
              onKeyPress={handleKeyPress}
              rows={1}
            />
            
            <button 
              className="emoji-button"
              onClick={() => setShowEmojiPicker(!showEmojiPicker)}
              title="Ð­Ð¼Ð¾Ð´Ð·Ð¸"
            >
              <FontAwesomeIcon icon={faSmile} />
            </button>
          </div>
          
          <button 
            className="send-button"
            onClick={handleSendMessage}
            disabled={!newMessage.trim() && selectedFiles.length === 0}
            title="ÐžÑ‚Ð¿Ñ€Ð°Ð²Ð¸Ñ‚ÑŒ"
          >
            <FontAwesomeIcon icon={faPaperPlane} />
          </button>
        </div>

        {/* Ð¡ÐºÑ€Ñ‹Ñ‚Ñ‹Ð¹ input Ð´Ð»Ñ Ñ„Ð°Ð¹Ð»Ð¾Ð² */}
        <input
          ref={fileInputRef}
          type="file"
          multiple
          accept="image/*,.pdf,.doc,.docx,.txt,.zip,.rar"
          onChange={handleFileSelect}
          className="file-input"
        />
      </div>

      {/* ÐœÐµÐ½ÑŽ Ð¿Ñ€Ð¸ÐºÑ€ÐµÐ¿Ð»ÐµÐ½Ð¸Ñ Ñ„Ð°Ð¹Ð»Ð¾Ð² */}
      {showAttachmentMenu && (
        <div className="attachment-menu">
          <button 
            className="attachment-option"
            onClick={() => {
              fileInputRef.current?.click();
              setShowAttachmentMenu(false);
            }}
          >
            <FontAwesomeIcon icon={faImage} />
            <span>Ð¤Ð¾Ñ‚Ð¾</span>
          </button>
          <button 
            className="attachment-option"
            onClick={() => {
              fileInputRef.current?.click();
              setShowAttachmentMenu(false);
            }}
          >
            <FontAwesomeIcon icon={faFile} />
            <span>Ð”Ð¾ÐºÑƒÐ¼ÐµÐ½Ñ‚</span>
          </button>
        </div>
      )}

      {/* Ð­Ð¼Ð¾Ð´Ð·Ð¸ Ð¿Ð¸ÐºÐµÑ€ */}
      {showEmojiPicker && (
        <div className="emoji-picker">
          <div className="emoji-grid">
            {emojis.map((emoji, index) => (
              <button
                key={index}
                className="emoji-button"
                onClick={() => addEmoji(emoji)}
              >
                {emoji}
              </button>
            ))}
          </div>
        </div>
      )}

      {/* ÐšÐ¾Ð½Ñ‚ÐµÐºÑÑ‚Ð½Ð¾Ðµ Ð¼ÐµÐ½ÑŽ */}
      {contextMenu && (
        <div
          className="context-menu"
          style={{
            left: contextMenu.x,
            top: contextMenu.y
          }}
        >
          <div 
            className="context-menu-item"
            onClick={() => handleReply(contextMenu.message)}
          >
            <FontAwesomeIcon icon={faReply} className="icon" />
            ÐžÑ‚Ð²ÐµÑ‚Ð¸Ñ‚ÑŒ
          </div>
          <div 
            className="context-menu-item"
            onClick={() => {
              navigator.clipboard.writeText(contextMenu.message.content);
              setContextMenu(null);
            }}
          >
            <FontAwesomeIcon icon={faCopy} className="icon" />
            ÐšÐ¾Ð¿Ð¸Ñ€Ð¾Ð²Ð°Ñ‚ÑŒ
          </div>
          {contextMenu.message.sender_name !== (chat.other_participant?.first_name || 'ÐŸÐ¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒ') && (
            <>
              <div 
                className="context-menu-item"
                onClick={() => startEditing(contextMenu.message)}
              >
                <FontAwesomeIcon icon={faEdit} className="icon" />
                Ð ÐµÐ´Ð°ÐºÑ‚Ð¸Ñ€Ð¾Ð²Ð°Ñ‚ÑŒ
              </div>
              <div 
                className="context-menu-item"
                onClick={() => handleDeleteMessage(contextMenu.message)}
              >
                <FontAwesomeIcon icon={faTrash} className="icon" />
                Ð£Ð´Ð°Ð»Ð¸Ñ‚ÑŒ
              </div>
            </>
          )}
        </div>
      )}
    </div>
  );
};

export default TelegramChat;
