import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import ChatModal from '../ChatModal/ChatModal';
import './UserProfilePage.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft, 
  faMapMarkerAlt, 
  faCalendar,
  faBaby,
  faEye,
  faEyeSlash,
  faArchive,
  faMars,
  faVenus,
  faUserPlus,
  faComments
} from '@fortawesome/free-solid-svg-icons';

const UserProfilePage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [posts, setPosts] = useState([]);
  const [children, setChildren] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('posts');
  const [isSubscribed, setIsSubscribed] = useState(false);
  const [isSubscribing, setIsSubscribing] = useState(false);
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);

  useEffect(() => {
    fetchUserProfile();
    loadChildrenData();
  }, [userId]);

  const fetchUserProfile = async () => {
    try {
      setLoading(true);

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –µ—Å—Ç—å –ª–∏ —Ç–æ–∫–µ–Ω
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');

      // –ü–æ–ª—É—á–∞–µ–º —Ç–æ–∫–µ–Ω –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏–∏
      const headers = token ? { 'Authorization': `Bearer ${token}` } : {};

      // –ü–æ–ª—É—á–∞–µ–º –¥–∞–Ω–Ω—ã–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è —Å –ø–æ—Å—Ç–∞–º–∏ –∏ –¥–µ—Ç—å–º–∏
      const userUrl = `http://localhost:8000/api/users/profile-with-posts/${userId}/`;

      const userResponse = await fetch(userUrl, {
        headers: {
          'Content-Type': 'application/json',
          ...headers
        }
      });

      if (userResponse.ok) {
        const userData = await userResponse.json();

        // –ü—Ä–æ–≤–µ—Ä—è–µ–º —Å—Ç—Ä—É–∫—Ç—É—Ä—É –æ—Ç–≤–µ—Ç–∞
        if (userData.success && userData.user) {
          const user = userData.user;
          setUser(user);

          // –°–æ—Ö—Ä–∞–Ω—è–µ–º –¥–∞–Ω–Ω—ã–µ –æ –¥–µ—Ç—è—Ö
          if (user.children) {
            setChildren(user.children);
          }
        } else if (userData.user) {
          // –ï—Å–ª–∏ –Ω–µ—Ç –ø–æ–ª—è success, –Ω–æ –µ—Å—Ç—å user
          const user = userData.user;
          setUser(user);

          if (user.children) {
            setChildren(user.children);
          }
        } else {
          // –ï—Å–ª–∏ —Å—Ç—Ä—É–∫—Ç—É—Ä–∞ –Ω–µ–æ–∂–∏–¥–∞–Ω–Ω–∞—è
          setError('–û—à–∏–±–∫–∞ –≤ —Å—Ç—Ä—É–∫—Ç—É—Ä–µ –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è');
        }
      } else {
        if (userResponse.status === 401) {
          setError('–î–ª—è –ø—Ä–æ—Å–º–æ—Ç—Ä–∞ –ø—Ä–æ—Ñ–∏–ª—è –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ –≤–æ–π—Ç–∏ –≤ —Å–∏—Å—Ç–µ–º—É');
        } else if (userResponse.status === 404) {
          setError(`–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å —Å ID ${userId} –Ω–µ –Ω–∞–π–¥–µ–Ω`);
        } else {
          setError(`–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –ø—Ä–æ—Ñ–∏–ª—è: ${userResponse.status} ${userResponse.statusText}`);
        }
      }

      // –ü–æ–ª—É—á–∞–µ–º —Ç–æ–ª—å–∫–æ –æ–ø—É–±–ª–∏–∫–æ–≤–∞–Ω–Ω—ã–µ –ø–æ—Å—Ç—ã –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
      const postsUrl = `http://localhost:8000/api/posts/user/${userId}/published/`;

      const postsResponse = await fetch(postsUrl, {
        headers: {
          'Content-Type': 'application/json',
          ...headers
        }
      });

      if (postsResponse.ok) {
        const postsData = await postsResponse.json();

        // –ü—Ä–æ–≤–µ—Ä—è–µ–º —Å—Ç—Ä—É–∫—Ç—É—Ä—É –æ—Ç–≤–µ—Ç–∞
        if (postsData.success && postsData.posts) {
          setPosts(postsData.posts);
        } else if (postsData.posts) {
          setPosts(postsData.posts);
        } else if (Array.isArray(postsData)) {
          setPosts(postsData);
        } else {
          setPosts([]);
        }
      } else {
        setPosts([]);
      }

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º —Å—Ç–∞—Ç—É—Å –ø–æ–¥–ø–∏—Å–∫–∏
      if (token) {
        try {
          const subscriptionResponse = await fetch(`http://localhost:8000/api/users/check-subscription/${userId}/`, {
            headers: {
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json',
            }
          });

          if (subscriptionResponse.ok) {
            const subscriptionData = await subscriptionResponse.json();
            setIsSubscribed(subscriptionData.is_subscribed || false);
          } else {
            setIsSubscribed(false);
          }
        } catch (error) {
          setIsSubscribed(false);
        }
      }
    } catch (error) {
      setError('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –ø—Ä–æ—Ñ–∏–ª—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è');
      setPosts([]);
    } finally {
      setLoading(false);
    }
  };

  const loadChildrenData = async () => {
    try {
      // –ü–æ–ª—É—á–∞–µ–º –¥–µ—Ç–µ–π —á–µ—Ä–µ–∑ API –ø—Ä–æ—Ñ–∏–ª—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
      const response = await fetch(`http://localhost:8000/api/users/profile-with-posts/${userId}/`, {
        headers: {
          'Content-Type': 'application/json',
        }
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success && data.user && data.user.children) {
          setChildren(data.user.children);
        }
      }
    } catch (error) {
      // –û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–µ—Ç–µ–π
    }
  };

  const handleSubscribe = async () => {
    if (!isLoggedIn()) {
      alert('–î–ª—è –ø–æ–¥–ø–∏—Å–∫–∏ –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ –≤–æ–π—Ç–∏ –≤ —Å–∏—Å—Ç–µ–º—É');
      return;
    }

    setIsSubscribing(true);
    try {
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');

      if (isSubscribed) {
        // –û—Ç–ø–∏—Å–∫–∞
        const response = await fetch(`http://localhost:8000/api/users/unfollow/${userId}/`, {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
          }
        });

        if (response.ok) {
          const data = await response.json();
          setIsSubscribed(false);
          // –û–±–Ω–æ–≤–ª—è–µ–º –¥–∞–Ω–Ω—ã–µ –ø—Ä–æ—Ñ–∏–ª—è
          fetchUserProfile();
        } else {
          const errorData = await response.json();
          // –ù–µ –ø–æ–∫–∞–∑—ã–≤–∞–µ–º alert, –ø—Ä–æ—Å—Ç–æ –ª–æ–≥–∏—Ä—É–µ–º –æ—à–∏–±–∫—É
        }
      } else {
        // –ü–æ–¥–ø–∏—Å–∫–∞
        const response = await fetch(`http://localhost:8000/api/users/follow/${userId}/`, {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
          }
        });

        if (response.ok) {
          const data = await response.json();
          setIsSubscribed(true);
          // –û–±–Ω–æ–≤–ª—è–µ–º –¥–∞–Ω–Ω—ã–µ –ø—Ä–æ—Ñ–∏–ª—è
          fetchUserProfile();
        } else {
          const errorData = await response.json();
          // –ù–µ –ø–æ–∫–∞–∑—ã–≤–∞–µ–º alert, –ø—Ä–æ—Å—Ç–æ –ª–æ–≥–∏—Ä—É–µ–º –æ—à–∏–±–∫—É
        }
      }
    } catch (error) {
      // –ù–µ –ø–æ–∫–∞–∑—ã–≤–∞–µ–º alert, –ø—Ä–æ—Å—Ç–æ –ª–æ–≥–∏—Ä—É–µ–º –æ—à–∏–±–∫—É
    } finally {
      setIsSubscribing(false);
    }
  };

  const handleStartChat = async () => {
    if (!isLoggedIn()) {
      alert('–î–ª—è –Ω–∞—á–∞–ª–∞ —á–∞—Ç–∞ –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ –≤–æ–π—Ç–∏ –≤ —Å–∏—Å—Ç–µ–º—É');
      return;
    }

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –Ω–µ –ø—ã—Ç–∞–µ—Ç—Å—è —Å–æ–∑–¥–∞—Ç—å —á–∞—Ç —Å —Å–∞–º–∏–º —Å–æ–±–æ–π
    const currentUserId = localStorage.getItem('user_id');
    if (currentUserId && parseInt(currentUserId) === parseInt(userId)) {
      alert('–ù–µ–ª—å–∑—è —Å–æ–∑–¥–∞—Ç—å —á–∞—Ç —Å —Å–∞–º–∏–º —Å–æ–±–æ–π');
      return;
    }

    try {
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      if (!token) {
        alert('–¢–æ–∫–µ–Ω –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏–∏ –Ω–µ –Ω–∞–π–¥–µ–Ω. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤–æ–π–¥–∏—Ç–µ –≤ —Å–∏—Å—Ç–µ–º—É –∑–∞–Ω–æ–≤–æ.');
        return;
      }

      console.log('–°–æ–∑–¥–∞–µ–º —á–∞—Ç —Å –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–º:', userId);

      // –°–æ–∑–¥–∞–µ–º —á–∞—Ç —á–µ—Ä–µ–∑ –æ—Ç–ø—Ä–∞–≤–∫—É —Å–æ–æ–±—â–µ–Ω–∏—è (—ç—Ç–æ –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏ —Å–æ–∑–¥–∞—Å—Ç —á–∞—Ç)
      const response = await fetch(`http://localhost:8000/users/chats/create/`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          content: '–ü—Ä–∏–≤–µ—Ç! üëã',
          message_type: 'text'
        }),
      });

      if (response.ok) {
        const data = await response.json();
        console.log('–ß–∞—Ç —Å–æ–∑–¥–∞–Ω –∏ —Å–æ–æ–±—â–µ–Ω–∏–µ –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω–æ:', data);

        // –û—Ç–∫—Ä—ã–≤–∞–µ–º –º–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ —á–∞—Ç–∞
        const userName = user ? 
          (user.first_name && user.last_name ? `${user.first_name} ${user.last_name}` : 
           user.first_name || user.username || '–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å') : 
          '–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å';

        setSelectedUser({
          id: userId,
          name: userName
        });
        setModalOpen(true);
      } else {
        const errorData = await response.json();
        console.error('–û—à–∏–±–∫–∞ —Å–æ–∑–¥–∞–Ω–∏—è —á–∞—Ç–∞:', response.status, errorData);
        alert(`–ù–µ —É–¥–∞–ª–æ—Å—å –Ω–∞—á–∞—Ç—å —á–∞—Ç: ${errorData.message || '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–∞—è –æ—à–∏–±–∫–∞'}`);
      }
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ —Å–æ–∑–¥–∞–Ω–∏—è —á–∞—Ç–∞:', error);
      alert('–ù–µ —É–¥–∞–ª–æ—Å—å –Ω–∞—á–∞—Ç—å —á–∞—Ç');
    }
  };

  const isLoggedIn = () => {
    return !!(localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken'));
  };

  const getAuthorInitials = (authorName) => {
    if (!authorName) return '–ê';
    const names = authorName.split(' ');
    return `${names[0]?.charAt(0) || '–ê'}${names[1]?.charAt(0) || ''}`.toUpperCase();
  };

  const formatDate = (dateString) => {
    if (!dateString) return '–ù–µ–¥–∞–≤–Ω–æ';
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));

    if (diffInHours < 1) return '–¢–æ–ª—å–∫–æ —á—Ç–æ';
    if (diffInHours < 24) return `${diffInHours} —á –Ω–∞–∑–∞–¥`;

    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays < 7) return `${diffInDays} –¥–Ω –Ω–∞–∑–∞–¥`;

    return date.toLocaleDateString('ru-RU');
  };

  const renderPosts = () => {
    if (posts.length === 0) {
      return (
        <div className="empty-posts">
          <div className="empty-icon">
            <FontAwesomeIcon icon={faEye} />
          </div>
          <h3>–ü–æ—Å—Ç–æ–≤ –ø–æ–∫–∞ –Ω–µ—Ç</h3>
          <p>–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –µ—â–µ –Ω–µ —Å–æ–∑–¥–∞–ª –ø—É–±–ª–∏–∫–∞—Ü–∏–π</p>
        </div>
      );
    }

    return (
      <div className="posts-grid">
        {posts.map((post) => (
          <div key={post.id} className="post-item">
            {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ */}
            <div 
              className="post-title clickable-title"
              onClick={(e) => {
                e.stopPropagation();
                const postSlug = post.slug && post.slug !== 'post' ? post.slug : post.id;
                navigate(`/post-detail/${postSlug}`);
              }}
            >
              {post.title}
            </div>

            {/* –¢–µ–≥ */}
            <div className="post-tag">
              {post.category?.name || '–û–±—â–µ–µ'}
            </div>

            {/* –ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ–± –∞–≤—Ç–æ—Ä–µ */}
            <div className="post-author">
              <div className="author-avatar">
                {user?.avatar ? (
                  <img 
                    src={user.avatar} 
                    alt={user.first_name}
                    onError={(e) => {
                      e.target.style.display = 'none';
                      e.target.nextSibling.style.display = 'flex';
                    }}
                  />
                ) : null}
                <div className="author-initials" style={{ 
                  display: user?.avatar ? 'none' : 'flex' 
                }}>
                  {getAuthorInitials(user?.first_name || user?.username)}
                </div>
              </div>
              <div className="author-info">
                <span className="author-name">
                  {user?.first_name || user?.username || '–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å'}
                </span>
                <span className="author-location">
                  <FontAwesomeIcon icon={faMapMarkerAlt} />
                  {user?.city || '–ù–µ —É–∫–∞–∑–∞–Ω'}
                </span>
                <span className="post-date">
                  <FontAwesomeIcon icon={faCalendar} />
                  {formatDate(post.created_at)}
                </span>
              </div>
            </div>

            {/* –û—Ç—Ä—ã–≤–æ–∫ —Ç–µ–∫—Å—Ç–∞ —Å—Ç–∞—Ç—å–∏ */}
            {post.content && (
              <div className="post-excerpt">
                <p>{post.content.length > 67 ? `${post.content.substring(0, 67)}...` : post.content}</p>
              </div>
            )}

            {/* –ö–Ω–æ–ø–∫–∞ "–ß–∏—Ç–∞—Ç—å –¥–∞–ª—å—à–µ" */}
            <div className="post-read-more">
              <button 
                className="read-more-btn"
                onClick={(e) => {
                  e.stopPropagation();
                  const postSlug = post.slug && post.slug !== 'post' ? post.slug : post.id;
                  navigate(`/post-detail/${postSlug}`);
                }}
              >
                –ß–∏—Ç–∞—Ç—å –¥–∞–ª—å—à–µ
              </button>
            </div>
          </div>
        ))}
      </div>
    );
  };

  if (loading) {
    return (
      <div className="user-profile-loading">
        <div className="loading-spinner"></div>
        <p>–ó–∞–≥—Ä—É–∑–∫–∞ –ø—Ä–æ—Ñ–∏–ª—è...</p>
      </div>
    );
  }

  if (error || !user) {
    return (
      <div className="user-profile-error">
        <h2>–û—à–∏–±–∫–∞</h2>
        <p>{error || '–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –Ω–µ –Ω–∞–π–¥–µ–Ω'}</p>
        <button onClick={() => navigate('/')} className="btn-back">
          <FontAwesomeIcon icon={faArrowLeft} />
          –í–µ—Ä–Ω—É—Ç—å—Å—è –Ω–∞ –≥–ª–∞–≤–Ω—É—é
        </button>
      </div>
    );
  }

  return (
    <div className="user-profile-page">
      <div className="user-profile-container">
        {/* –ö–Ω–æ–ø–∫–∞ –Ω–∞–∑–∞–¥ */}
        <div className="user-profile-header">
          <button onClick={() => navigate('/')} className="btn-back">
            <FontAwesomeIcon icon={faArrowLeft} />
            –ù–∞–∑–∞–¥
          </button>
        </div>

        {/* –°–µ–∫—Ü–∏—è –ø—Ä–æ—Ñ–∏–ª—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è */}
        <div className="user-profile-section">
          <div className="user-avatar">
            {user.avatar ? (
              <img 
                src={user.avatar} 
                alt={user.first_name || user.username}
                onError={(e) => {
                  e.target.style.display = 'none';
                  e.target.nextSibling.style.display = 'flex';
                }}
              />
            ) : null}
            <div className="avatar-placeholder" style={{ 
              display: user.avatar ? 'none' : 'flex' 
            }}>
              {getAuthorInitials(user.first_name || user.username)}
            </div>
          </div>

          <div className="user-info">
            <h1 className="user-name">{user.first_name || user.username}</h1>
            <div className="user-login">@{user.username}</div>
            <div className="user-details">
              <span>
                <FontAwesomeIcon icon={faMapMarkerAlt} />
                {user.city || '–ù–µ —É–∫–∞–∑–∞–Ω'}
              </span>
              <span>
                <FontAwesomeIcon icon={faCalendar} />
                {formatDate(user.date_joined || user.created_at)}
              </span>
            </div>

            {/* –ö–Ω–æ–ø–∫–∏ –¥–µ–π—Å—Ç–≤–∏–π */}
            <div className="user-actions">
              <button 
                className={`action-btn subscribe-btn ${isSubscribed ? 'subscribed' : ''}`}
                onClick={handleSubscribe}
                disabled={isSubscribing}
              >
                <FontAwesomeIcon icon={faUserPlus} />
                {isSubscribing ? '–ó–∞–≥—Ä—É–∑–∫–∞...' : (isSubscribed ? '–û—Ç–ø–∏—Å–∞—Ç—å—Å—è' : '–ü–æ–¥–ø–∏—Å–∞—Ç—å—Å—è')}
              </button>

              <button 
                className="action-btn chat-btn"
                onClick={handleStartChat}
              >
                <FontAwesomeIcon icon={faComments} />
                –ù–∞—á–∞—Ç—å —á–∞—Ç
              </button>
            </div>
          </div>
        </div>

                 {/* –°–µ–∫—Ü–∏—è —Å –¥–µ—Ç—å–º–∏ */}
         {children.length > 0 && (
           <div className="children-section">
             <div className="section-header">
               <FontAwesomeIcon icon={faBaby} />
               <h2>–î–µ—Ç–∏</h2>
             </div>
             <div className="children-grid">
               {children.map((child) => (
                 <div key={child.id} className="child-card">
                   <div className={`child-avatar ${child.gender === 'female' ? 'female' : 'male'}`}>
                     <FontAwesomeIcon icon={child.gender === 'female' ? faVenus : faMars} />
                   </div>
                   <div className="child-info">
                     <div className="child-name">{child.name}</div>
                     <div className="child-age">{child.age}</div>
                   </div>
                 </div>
               ))}
             </div>
           </div>
         )}



        {/* –¢–∞–±—ã */}
        <div className="tabs-section">
          <div className="tabs">
            <button 
              className={`tab ${activeTab === 'posts' ? 'active' : ''}`}
              onClick={() => setActiveTab('posts')}
            >
              –ó–∞–ø–∏—Å–∏ ({posts.length})
            </button>
          </div>
        </div>

        {/* –ö–æ–Ω—Ç–µ–Ω—Ç */}
        <div className="content-section">
          {activeTab === 'posts' && renderPosts()}
        </div>
      </div>

      {/* –ú–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ —á–∞—Ç–∞ */}
      <ChatModal
        isOpen={modalOpen}
        onClose={() => {
          setModalOpen(false);
          setSelectedUser(null);
        }}
        userId={selectedUser?.id}
        userName={selectedUser?.name}
      />
    </div>
  );
};

export default UserProfilePage;
