import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft, 
  faMapMarkerAlt,
  faCalendar,
  faUserPlus,
  faUserMinus,
  faSearch,
  faUsers,
  faUserFriends
} from '@fortawesome/free-solid-svg-icons';
import './FriendsPage.css';

const FriendsPage = () => {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [searchLoading, setSearchLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('search');

  const isLoggedIn = () => {
    return !!(localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken'));
  };

  const getAuthHeaders = () => {
    const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
    return {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${token}`
    };
  };

  const searchUsers = async () => {
    if (!searchQuery.trim()) return;

    setSearchLoading(true);
    try {
      // Убираем символ @ если пользователь его ввел
      const cleanQuery = searchQuery.replace('@', '');
      const response = await fetch(`http://localhost:8000/api/users/search/?q=${encodeURIComponent(cleanQuery)}`, {
        headers: getAuthHeaders()
      });

      if (response.ok) {
        const data = await response.json();
        setSearchResults(data.users || []);
      } else {
        console.error('Ошибка поиска пользователей');
        setSearchResults([]);
      }
    } catch (error) {
      console.error('Ошибка поиска:', error);
      setSearchResults([]);
    } finally {
      setSearchLoading(false);
    }
  };

  const handleFollow = async (userId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/users/follow/${userId}/`, {
        method: 'POST',
        headers: getAuthHeaders()
      });

      if (response.ok) {
        // Обновляем поиск
        searchUsers();
      }
    } catch (error) {
      console.error('Ошибка подписки:', error);
    }
  };

  const handleUnfollow = async (userId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/users/unfollow/${userId}/`, {
        method: 'POST',
        headers: getAuthHeaders()
      });

      if (response.ok) {
        // Обновляем поиск
        searchUsers();
      }
    } catch (error) {
      console.error('Ошибка отписки:', error);
    }
  };

  const getAuthorInitials = (authorName) => {
    if (!authorName) return 'А';
    const names = authorName.split(' ');
    return `${names[0]?.charAt(0) || 'А'}${names[1]?.charAt(0) || ''}`.toUpperCase();
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Недавно';
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));

    if (diffInHours < 1) return 'Только что';
    if (diffInHours < 24) return `${diffInHours} ч назад`;

    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays < 7) return `${diffInDays} дн назад`;

    return date.toLocaleDateString('ru-RU');
  };

  const renderUserCard = (user) => (
    <div key={user.id} className="user-card">
      <div className="user-header">
        <div className="user-avatar">
          {user.avatar ? (
            <img src={user.avatar} alt={user.first_name || user.username} />
          ) : (
            <div className="avatar-placeholder">
              {getAuthorInitials(user.first_name || user.username)}
            </div>
          )}
        </div>
        <div className="user-info">
          <h3 className="user-name">{user.first_name || user.username}</h3>
          <div className="user-login">@{user.username}</div>
          <div className="user-details">
            <span className="user-location">
              <FontAwesomeIcon icon={faMapMarkerAlt} />
              {user.city || 'Не указан'}
            </span>
            <span className="user-join-date">
              <FontAwesomeIcon icon={faCalendar} />
              {formatDate(user.date_joined)}
            </span>
          </div>
        </div>
      </div>

      <div className="user-actions">
        {user.is_following ? (
          <button 
            className="unfollow-btn"
            onClick={() => handleUnfollow(user.id)}
          >
            <FontAwesomeIcon icon={faUserMinus} />
            Отписаться
          </button>
        ) : (
          <button 
            className="follow-btn"
            onClick={() => handleFollow(user.id)}
          >
            <FontAwesomeIcon icon={faUserPlus} />
            Подписаться
          </button>
        )}
      </div>
    </div>
  );

  if (!isLoggedIn()) {
    return (
      <div className="friends-page">
        <div className="friends-container">
          <div className="auth-required">
            <div className="auth-icon">
              <FontAwesomeIcon icon={faUsers} />
            </div>
            <h2>Требуется авторизация</h2>
            <p>Для просмотра друзей и подписок необходимо войти в систему</p>
            <button className="auth-btn" onClick={() => navigate('/auth')}>
              Войти в систему
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="friends-page">
      <div className="friends-container">
        <div className="friends-header">
          <button onClick={() => navigate('/')} className="btn-back">
            <FontAwesomeIcon icon={faArrowLeft} />
            Назад
          </button>
          <h1 className="friends-title">
            <FontAwesomeIcon icon={faUserFriends} />
            Друзья
          </h1>
        </div>

        <div className="friends-content">
          {/* Вкладки */}
          <div className="friends-tabs">
            <button 
              className={`tab ${activeTab === 'search' ? 'active' : ''}`}
              onClick={() => setActiveTab('search')}
            >
              <FontAwesomeIcon icon={faSearch} />
              Поиск пользователей
            </button>
            <button 
              className={`tab ${activeTab === 'following' ? 'active' : ''}`}
              onClick={() => setActiveTab('following')}
            >
              <FontAwesomeIcon icon={faUserPlus} />
              Мои подписки
            </button>
            <button 
              className={`tab ${activeTab === 'followers' ? 'active' : ''}`}
              onClick={() => setActiveTab('followers')}
            >
              <FontAwesomeIcon icon={faUsers} />
              Подписчики
            </button>
          </div>

          {/* Секция поиска */}
          {activeTab === 'search' && (
            <div className="search-section">
              <div className="search-header">
                <h2>Найти пользователей</h2>
                <p>Ищите по имени или логину (с символом @ или без него)</p>
              </div>

              <form className="search-form" onSubmit={(e) => { e.preventDefault(); searchUsers(); }}>
                <div className="search-input-wrapper">
                  <FontAwesomeIcon icon={faSearch} className="search-icon" />
                  <input
                    type="text"
                    className="search-input"
                    placeholder="Введите имя или @логин пользователя..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                  />
                </div>
                <button 
                  type="submit" 
                  className="search-btn"
                  disabled={searchLoading || !searchQuery.trim()}
                >
                  {searchLoading ? 'Поиск...' : 'Найти'}
                </button>
              </form>

              {searchLoading ? (
                <div className="loading">
                  <div className="loading-spinner"></div>
                  <p>Поиск пользователей...</p>
                </div>
              ) : searchResults.length > 0 ? (
                <div className="search-results">
                  <h3>Результаты поиска ({searchResults.length})</h3>
                  <div className="users-grid">
                    {searchResults.map(renderUserCard)}
                  </div>
                </div>
              ) : searchQuery && (
                <div className="empty-state">
                  <div className="empty-icon">
                    <FontAwesomeIcon icon={faSearch} />
                  </div>
                  <h3>Пользователи не найдены</h3>
                  <p>Попробуйте изменить поисковый запрос или проверить правильность написания</p>
                </div>
              )}
            </div>
          )}

          {/* Секция подписок */}
          {activeTab === 'following' && (
            <div className="following-section">
              <div className="section-header">
                <h2>Мои подписки</h2>
                <p>Пользователи, на которых вы подписаны</p>
              </div>
              <div className="empty-state">
                <div className="empty-icon">
                  <FontAwesomeIcon icon={faUserPlus} />
                </div>
                <h3>Подписок пока нет</h3>
                <p>Найдите интересных пользователей и подпишитесь на них</p>
                <button className="tab-btn" onClick={() => setActiveTab('search')}>
                  Найти пользователей
                </button>
              </div>
            </div>
          )}

          {/* Секция подписчиков */}
          {activeTab === 'followers' && (
            <div className="followers-section">
              <div className="section-header">
                <h2>Мои подписчики</h2>
                <p>Пользователи, которые подписаны на вас</p>
              </div>
              <div className="empty-state">
                <div className="empty-icon">
                  <FontAwesomeIcon icon={faUsers} />
                </div>
                <h3>Подписчиков пока нет</h3>
                <p>Создавайте интересный контент, чтобы привлечь подписчиков</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default FriendsPage;
