import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrash, faTimes } from '@fortawesome/free-solid-svg-icons';
import './MessageActionsModal.css';

const MessageActionsModal = ({ 
  isOpen, 
  onClose, 
  message, 
  onEdit, 
  onDelete, 
  isMyMessage 
}) => {
  if (!isOpen || !message) {
    return null;
  }

  const handleEdit = () => {
    console.log('Редактирование сообщения:', message.id);
    onEdit(message.id, message.content);
    onClose();
  };

  const handleDelete = () => {
    console.log('Удаление сообщения:', message.id);
    if (window.confirm('Вы уверены, что хотите удалить это сообщение?')) {
      onDelete(message.id);
      onClose();
    }
  };

  return (
    <div className="message-actions-modal-overlay" onClick={onClose}>
      <div className="message-actions-modal" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h3>Действия с сообщением</h3>
          <button className="close-button" onClick={onClose}>
            <FontAwesomeIcon icon={faTimes} />
          </button>
        </div>

        <div className="modal-content">
          <div className="actions-section">
            <div className="message-preview">
              <p><strong>Сообщение:</strong></p>
              <p className="message-text">"{message.content}"</p>
              <p className="message-time">
                {new Date(message.created_at).toLocaleString('ru-RU')}
              </p>
            </div>

            <div className="action-buttons">
              <button 
                className="action-button edit-button"
                onClick={handleEdit}
              >
                <FontAwesomeIcon icon={faEdit} />
                <span>Редактировать</span>
              </button>

              {isMyMessage && (
                <button 
                  className="action-button delete-button"
                  onClick={handleDelete}
                >
                  <FontAwesomeIcon icon={faTrash} />
                  <span>Удалить</span>
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MessageActionsModal;
