import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft, 
  faBell,
  faUserPlus,
  faComments,
  faHeart,
  faComment,
  faCheck,
  faEye,
  faMapMarkerAlt,
  faCalendar
} from '@fortawesome/free-solid-svg-icons';
import './Notifications.css';

const Notifications = () => {
  const navigate = useNavigate();
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [unreadCount, setUnreadCount] = useState(0);

  useEffect(() => {
    loadNotifications();
  }, []);

  const isLoggedIn = () => {
    return !!(localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken'));
  };

  const getAuthHeaders = () => {
    const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
    return {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${token}`
    };
  };

  const loadNotifications = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/users/notifications/', {
        headers: getAuthHeaders()
      });
      
      if (response.ok) {
        const data = await response.json();
        setNotifications(data.notifications || []);
        setUnreadCount(data.unread_count || 0);
      } else {
        console.error('Ошибка загрузки уведомлений');
        setNotifications([]);
        setUnreadCount(0);
      }
    } catch (error) {
      console.error('Ошибка загрузки уведомлений:', error);
      setNotifications([]);
      setUnreadCount(0);
    } finally {
      setLoading(false);
    }
  };

  const markAsRead = async (notificationId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/users/notifications/${notificationId}/read/`, {
        method: 'POST',
        headers: getAuthHeaders()
      });
      
      if (response.ok) {
        // Обновляем локальное состояние
        setNotifications(prev => 
          prev.map(notification => 
            notification.id === notificationId 
              ? { ...notification, is_read: true }
              : notification
          )
        );
        setUnreadCount(prev => Math.max(0, prev - 1));
      }
    } catch (error) {
      console.error('Ошибка отметки уведомления как прочитанного:', error);
    }
  };

  const markAllAsRead = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/users/notifications/mark-all-read/', {
        method: 'POST',
        headers: getAuthHeaders()
      });
      
      if (response.ok) {
        setNotifications(prev => 
          prev.map(notification => ({ ...notification, is_read: true }))
        );
        setUnreadCount(0);
      }
    } catch (error) {
      console.error('Ошибка отметки всех уведомлений как прочитанных:', error);
    }
  };

  const getAuthorInitials = (authorName) => {
    if (!authorName) return 'А';
    const names = authorName.split(' ');
    return `${names[0]?.charAt(0) || 'А'}${names[1]?.charAt(0) || ''}`.toUpperCase();
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Недавно';
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));
    
    if (diffInHours < 1) return 'Только что';
    if (diffInHours < 24) return `${diffInHours} ч назад`;
    
    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays < 7) return `${diffInDays} дн назад`;
    
    return date.toLocaleDateString('ru-RU');
  };

  const getNotificationIcon = (type) => {
    switch (type) {
      case 'follow':
        return faUserPlus;
      case 'message':
        return faComments;
      case 'like':
        return faHeart;
      case 'comment':
        return faComment;
      default:
        return faBell;
    }
  };

  const getNotificationText = (notification) => {
    const senderName = notification.sender?.full_name || notification.sender?.username || 'Пользователь';
    
    switch (notification.notification_type) {
      case 'follow':
        return (
          <span>
            <strong>{senderName}</strong> подписался на вас
          </span>
        );
      case 'message':
        return (
          <span>
            <strong>{senderName}</strong> отправил вам сообщение
          </span>
        );
      case 'like':
        return (
          <span>
            <strong>{senderName}</strong> поставил лайк вашему посту
          </span>
        );
      case 'comment':
        return (
          <span>
            <strong>{senderName}</strong> прокомментировал ваш пост
          </span>
        );
      default:
        return notification.message || 'Новое уведомление';
    }
  };

  const handleNotificationClick = (notification) => {
    // Отмечаем как прочитанное
    if (!notification.is_read) {
      markAsRead(notification.id);
    }
    
    // Переходим к профилю отправителя
    if (notification.sender?.id) {
      navigate(`/user/${notification.sender.id}`);
    }
  };

  if (!isLoggedIn()) {
    return (
      <div className="notifications-page">
        <div className="notifications-container">
          <div className="auth-required">
            <h2>Требуется авторизация</h2>
            <p>Для просмотра уведомлений необходимо войти в систему</p>
            <button className="auth-btn" onClick={() => navigate('/auth')}>
              Войти в систему
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="notifications-page">
      <div className="notifications-container">
        <div className="notifications-header">
          <div className="header-left">
            <button onClick={() => navigate('/')} className="btn-back">
              <FontAwesomeIcon icon={faArrowLeft} />
              Назад
            </button>
            <h1 className="notifications-title">Уведомления</h1>
          </div>
          
          <div className="header-actions">
            {unreadCount > 0 && (
              <div className="unread-badge">
                {unreadCount}
              </div>
            )}
            {unreadCount > 0 && (
              <button className="mark-all-read-btn" onClick={markAllAsRead}>
                <FontAwesomeIcon icon={faCheck} />
                Отметить все как прочитанные
              </button>
            )}
          </div>
        </div>

        <div className="notifications-content">
          {loading ? (
            <div className="loading">
              <div className="loading-spinner"></div>
              <p>Загрузка уведомлений...</p>
            </div>
          ) : notifications.length > 0 ? (
            <div className="notifications-list">
              {notifications.map((notification) => (
                <div 
                  key={notification.id} 
                  className={`notification-item ${!notification.is_read ? 'unread' : ''}`}
                >
                  <div className="notification-content">
                    {notification.sender?.avatar ? (
                      <div className="notification-avatar">
                        <img 
                          src={notification.sender.avatar} 
                          alt={notification.sender.full_name || notification.sender.username}
                          onError={(e) => {
                            e.target.style.display = 'none';
                            e.target.nextSibling.style.display = 'flex';
                          }}
                        />
                        <div className="avatar-placeholder" style={{ display: 'none' }}>
                          {getAuthorInitials(notification.sender.full_name || notification.sender.username)}
                        </div>
                      </div>
                    ) : (
                      <div className="notification-avatar">
                        {getAuthorInitials(notification.sender?.full_name || notification.sender?.username)}
                      </div>
                    )}
                    
                    <div className={`notification-icon ${notification.notification_type}`}>
                      <FontAwesomeIcon icon={getNotificationIcon(notification.notification_type)} />
                    </div>
                    
                    <div className="notification-details">
                      <div className="notification-text">
                        {getNotificationText(notification)}
                      </div>
                      
                      <div className="notification-meta">
                        <div className="notification-time">
                          <FontAwesomeIcon icon={faCalendar} />
                          {formatDate(notification.created_at)}
                        </div>
                        
                        {notification.sender?.city && (
                          <div>
                            <FontAwesomeIcon icon={faMapMarkerAlt} />
                            {notification.sender.city}
                          </div>
                        )}
                      </div>
                      
                      <div className="notification-actions">
                        {!notification.is_read && (
                          <button 
                            className="mark-read-btn"
                            onClick={() => markAsRead(notification.id)}
                          >
                            <FontAwesomeIcon icon={faCheck} />
                            Отметить как прочитанное
                          </button>
                        )}
                        
                        {notification.sender?.id && (
                          <button 
                            className="view-profile-btn"
                            onClick={() => handleNotificationClick(notification)}
                          >
                            <FontAwesomeIcon icon={faEye} />
                            Посмотреть профиль
                          </button>
                        )}
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="empty-state">
              <div className="empty-icon">
                <FontAwesomeIcon icon={faBell} />
              </div>
              <h3>Уведомлений пока нет</h3>
              <p>Когда кто-то подпишется на вас, поставит лайк или прокомментирует ваш пост, уведомления появятся здесь</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Notifications;
