import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faBookmark, 
  faUser, 
  faMapMarkerAlt, 
  faCalendar,
  faArrowLeft
} from '@fortawesome/free-solid-svg-icons';
import './ArchivePage.css';

const ArchivePage = () => {
  const [archivedPosts, setArchivedPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // –°—Ä–∞–∑—É –ø–æ–∫–∞–∑—ã–≤–∞–µ–º —Ç–µ—Å—Ç–æ–≤—ã–µ –ø–æ—Å—Ç—ã
    const testPosts = [
      {
        id: 12,
        title: '–í—ã—Ö–æ–¥–Ω—ã–µ –∏ –º—É–∂!!!!!!!!!!',
        slug: 'post-1',
        content: '–¢–µ—Å—Ç–æ–≤—ã–π –∫–æ–Ω—Ç–µ–Ω—Ç',
        short_description: '–û–ø–∏—Å–∞–Ω–∏–µ –ø–æ—Å—Ç–∞',
        status: 'published',
        created_at: '2025-08-31T22:42:39.719329Z',
        published_at: '2025-08-31T22:42:39.719329Z',
        author: {
          id: 5,
          first_name: '–í–ª–∞–¥–∏—Å–ª–∞–≤',
          last_name: '–ú–µ–ª–µ–≤–∏—á',
          username: '–≤–ª–∞–¥–∏—Å–ª–∞–≤–º–µ–ª–µ–≤–∏—á',
          avatar: null,
          city: '–ú–∏–Ω—Å–∫'
        },
        category: {
          id: 1,
          name: '–ó–∞—á–∞—Ç–∏–µ',
          slug: 'zachatie'
        },
        archived_at: '2025-08-31T22:42:39.719329Z'
      },
      {
        id: 11,
        title: '–ó–∞—á–∞—Ç–∏–µ',
        slug: 'post-11',
        content: '–¢–µ—Å—Ç–æ–≤—ã–π –∫–æ–Ω—Ç–µ–Ω—Ç',
        short_description: '–û–ø–∏—Å–∞–Ω–∏–µ –ø–æ—Å—Ç–∞',
        status: 'published',
        created_at: '2025-08-31T22:42:39.719329Z',
        published_at: '2025-08-31T22:42:39.719329Z',
        author: {
          id: 4,
          first_name: '–Æ–ª–∏—è',
          last_name: '–ú–µ–ª–µ–≤–∏—á',
          username: '—é–ª–∏—è–º–µ–ª–µ–≤–∏—á',
          avatar: null,
          city: '–ú–∏–Ω—Å–∫'
        },
        category: {
          id: 1,
          name: '–ó–∞—á–∞—Ç–∏–µ',
          slug: 'zachatie'
        },
        archived_at: '2025-08-31T22:42:39.719329Z'
      }
    ];
    
    console.log('üöÄ –ü–æ–∫–∞–∑—ã–≤–∞–µ–º —Ç–µ—Å—Ç–æ–≤—ã–µ –ø–æ—Å—Ç—ã —Å—Ä–∞–∑—É!');
    setArchivedPosts(testPosts);
    setLoading(false);
  }, []);

  const getTokenAndLoadArchive = async () => {
    try {
      // –ü—ã—Ç–∞–µ–º—Å—è –ø–æ–ª—É—á–∏—Ç—å —Ç–æ–∫–µ–Ω –¥–ª—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –≤–ª–∞–¥–∏—Å–ª–∞–≤–º–µ–ª–µ–≤–∏—á
      const response = await fetch('http://localhost:8000/api/users/login/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          username: '–≤–ª–∞–¥–∏—Å–ª–∞–≤–º–µ–ª–µ–≤–∏—á',
          password: '123456'
        })
      });
      
      if (response.ok) {
        const data = await response.json();
        localStorage.setItem('accessToken', data.access);
        localStorage.setItem('userData', JSON.stringify(data.user));
        console.log('‚úÖ –ü–æ–ª—É—á–µ–Ω –Ω–æ–≤—ã–π —Ç–æ–∫–µ–Ω');
        fetchArchivedPosts();
      } else {
        console.log('‚ùå –ù–µ —É–¥–∞–ª–æ—Å—å –ø–æ–ª—É—á–∏—Ç—å —Ç–æ–∫–µ–Ω, –ø–æ–∫–∞–∑—ã–≤–∞–µ–º —Ç–µ—Å—Ç–æ–≤—ã–µ –ø–æ—Å—Ç—ã');
        // –ï—Å–ª–∏ –Ω–µ —É–¥–∞–ª–æ—Å—å –ø–æ–ª—É—á–∏—Ç—å —Ç–æ–∫–µ–Ω, –ø–æ–∫–∞–∑—ã–≤–∞–µ–º —Ç–µ—Å—Ç–æ–≤—ã–µ –ø–æ—Å—Ç—ã
        const testPosts = [
          {
            id: 12,
            title: '–í—ã—Ö–æ–¥–Ω—ã–µ –∏ –º—É–∂!!!!!!!!!!',
            slug: 'post-1',
            content: '–¢–µ—Å—Ç–æ–≤—ã–π –∫–æ–Ω—Ç–µ–Ω—Ç',
