import React, { useState, useEffect } from 'react';
import './ProfileModal.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faTimes, 
  faEdit, 
  faPlus, 
  faUser, 
  faMapMarkerAlt, 
  faCalendar,
  faBaby,
  faHeart,
  faEye,
  faEyeSlash,
  faArchive,
  faTrash,
  faPencilAlt
} from '@fortawesome/free-solid-svg-icons';

const ProfileModal = ({ isOpen, onClose, userData }) => {
  const [activeTab, setActiveTab] = useState('posts');
  const [posts, setPosts] = useState([]);
  const [drafts, setDrafts] = useState([]);
  const [archived, setArchived] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (isOpen && userData) {
      fetchUserPosts();
    }
  }, [isOpen, userData, activeTab]);

  const fetchUserPosts = async () => {
    if (!userData?.id) return;
    
    setLoading(true);
    try {
      const response = await fetch(`http://localhost:8000/api/posts/user/${userData.id}/`);
      if (response.ok) {
        const data = await response.json();
        const allPosts = data.results || data;
        
        // Разделяем посты по статусам
        setPosts(allPosts.filter(post => post.status === 'published'));
        setDrafts(allPosts.filter(post => post.status === 'draft'));
        setArchived(allPosts.filter(post => post.status === 'archived'));
      }
    } catch (error) {
      console.error('Ошибка загрузки постов:', error);
    } finally {
      setLoading(false);
    }
  };

  const getAuthorInitials = (authorName) => {
    if (!authorName) return 'А';
    const names = authorName.split(' ');
    return `${names[0]?.charAt(0) || 'А'}${names[1]?.charAt(0) || ''}`.toUpperCase();
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Недавно';
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));
    
    if (diffInHours < 1) return 'Только что';
    if (diffInHours < 24) return `${diffInHours} ч назад`;
    
    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays < 7) return `${diffInDays} дн назад`;
    
    return date.toLocaleDateString('ru-RU');
  };

  const renderPosts = (postsList, type) => {
    if (loading) {
      return <div className="loading">Загрузка...</div>;
    }

    if (postsList.length === 0) {
      return (
        <div className="empty-state">
          <div className="empty-icon">
            <FontAwesomeIcon icon={type === 'drafts' ? faPencilAlt : type === 'archived' ? faArchive : faUser} />
          </div>
          <p className="empty-title">
            {type === 'drafts' ? 'Черновиков пока нет' : 
             type === 'archived' ? 'Архив пуст' : 'Постов пока нет'}
          </p>
          <p className="empty-description">
            {type === 'drafts' ? 'Создайте свой первый черновик' : 
             type === 'archived' ? 'Здесь будут ваши архивные записи' : 
             'Создайте свою первую запись'}
          </p>
        </div>
      );
    }

    return (
      <div className="posts-grid">
        {postsList.map((post) => (
          <div key={post.id} className="post-card">
            <div className="post-header">
              <div className="post-category">{post.category?.name || 'Общее'}</div>
              <div className="post-date">{formatDate(post.created_at)}</div>
            </div>
            <h3 className="post-title">{post.title}</h3>
            <p className="post-excerpt">
              {post.content?.length > 67 ? `${post.content.substring(0, 67)}...` : post.content}
            </p>
            <div className="post-actions">
              <button className="btn-edit">
                <FontAwesomeIcon icon={faEdit} />
                Редактировать
              </button>
              {type === 'posts' && (
                <button className="btn-archive">
                  <FontAwesomeIcon icon={faArchive} />
                  В архив
                </button>
              )}
              {type === 'archived' && (
                <button className="btn-restore">
                  <FontAwesomeIcon icon={faEye} />
                  Восстановить
                </button>
              )}
              <button className="btn-delete">
                <FontAwesomeIcon icon={faTrash} />
                Удалить
              </button>
            </div>
          </div>
        ))}
      </div>
    );
  };

  if (!isOpen) return null;

  return (
    <div className="profile-modal-overlay" onClick={onClose}>
      <div className="profile-modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="profile-modal-header">
          <h2>Мой профиль</h2>
          <button className="modal-close" onClick={onClose}>
            <FontAwesomeIcon icon={faTimes} />
          </button>
        </div>

        <div className="profile-modal-body">
          {/* Информация о пользователе */}
          <div className="user-profile-section">
            <div className="user-avatar">
              {userData?.avatar ? (
                <img src={userData.avatar} alt={userData.first_name} />
              ) : (
                <div className="avatar-placeholder">
                  {getAuthorInitials(userData?.first_name || userData?.username)}
                </div>
              )}
            </div>
            <div className="user-info">
              <h3>{userData?.first_name || userData?.username || 'Пользователь'}</h3>
              <div className="user-details">
                <span className="user-location">
                  <FontAwesomeIcon icon={faMapMarkerAlt} />
                  {userData?.city || 'Не указан'}
                </span>
                <span className="user-join-date">
                  <FontAwesomeIcon icon={faCalendar} />
                  {userData?.date_joined ? formatDate(userData.date_joined) : 'Недавно'}
                </span>
              </div>
            </div>
            <div className="user-actions">
              <button className="btn-edit-profile">
                <FontAwesomeIcon icon={faEdit} />
                Редактировать профиль
              </button>
              <button className="btn-add-child">
                <FontAwesomeIcon icon={faBaby} />
                Добавить ребенка
              </button>
            </div>
          </div>

          {/* Вкладки */}
          <div className="profile-tabs">
            <button 
              className={`tab ${activeTab === 'posts' ? 'active' : ''}`}
              onClick={() => setActiveTab('posts')}
            >
              <FontAwesomeIcon icon={faUser} />
              Записи ({posts.length})
            </button>
            <button 
              className={`tab ${activeTab === 'drafts' ? 'active' : ''}`}
              onClick={() => setActiveTab('drafts')}
            >
              <FontAwesomeIcon icon={faPencilAlt} />
              Черновики ({drafts.length})
            </button>
            <button 
              className={`tab ${activeTab === 'activity' ? 'active' : ''}`}
              onClick={() => setActiveTab('activity')}
            >
              <FontAwesomeIcon icon={faHeart} />
              Активность
            </button>
            <button 
              className={`tab ${activeTab === 'archive' ? 'active' : ''}`}
              onClick={() => setActiveTab('archive')}
            >
              <FontAwesomeIcon icon={faArchive} />
              Архив ({archived.length})
            </button>
          </div>

          {/* Контент вкладок */}
          <div className="tab-content">
            {activeTab === 'posts' && renderPosts(posts, 'posts')}
            {activeTab === 'drafts' && renderPosts(drafts, 'drafts')}
            {activeTab === 'activity' && (
              <div className="activity-content">
                <div className="empty-state">
                  <div className="empty-icon">
                    <FontAwesomeIcon icon={faHeart} />
                  </div>
                  <p className="empty-title">Активность</p>
                  <p className="empty-description">Здесь будет отображаться ваша активность</p>
                </div>
              </div>
            )}
            {activeTab === 'archive' && renderPosts(archived, 'archived')}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProfileModal;
