import React, { useState, useEffect, useRef } from 'react';
import { useParams, Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faHeart, 
  faCalendar, 
  faClipboard, 
  faStethoscope,
  faBaby,
  faCalculator,
  faHospital,
  faUsers,
  faBrain,
  faGamepad,
  faEllipsisV,
  faMapMarkerAlt,
  faChevronRight,
  faChevronLeft
} from '@fortawesome/free-solid-svg-icons';
import './CategoryPage.css';
import categoryData from '../../data/categoryPosts.json';

const CategoryPage = () => {
  const { categoryName } = useParams();
  const [category, setCategory] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentServiceSlide, setCurrentServiceSlide] = useState(0);
  const servicesSliderRef = useRef(null);

  useEffect(() => {
    const foundCategory = categoryData[categoryName];
    setCategory(foundCategory);
    setLoading(false);
  }, [categoryName]);

  useEffect(() => {
    if (servicesSliderRef.current) {
      const slideWidth = 300; // 280px + 20px margin
      servicesSliderRef.current.style.transform = `translateX(-${currentServiceSlide * slideWidth}px)`;
    }
  }, [currentServiceSlide]);

  const nextServiceSlide = () => {
    if (category && currentServiceSlide < category.services.length - 1) {
      setCurrentServiceSlide(currentServiceSlide + 1);
    }
  };

  const prevServiceSlide = () => {
    if (currentServiceSlide > 0) {
      setCurrentServiceSlide(currentServiceSlide - 1);
    }
  };

  const goToServiceSlide = (index) => {
    setCurrentServiceSlide(index);
  };

  if (loading) {
    return <div className="loading">Загрузка...</div>;
  }

  if (!category) {
    return <div className="error">Категория не найдена</div>;
  }

  const getIcon = (iconName) => {
    const iconMap = {
      heart: faHeart,
      calendar: faCalendar,
      clipboard: faClipboard,
      stethoscope: faStethoscope,
      baby: faBaby,
      calculator: faCalculator,
      hospital: faHospital,
      users: faUsers,
      brain: faBrain,
      gamepad: faGamepad
    };
    return iconMap[iconName] || faHeart;
  };

  const getCategoryIcon = (iconName) => {
    const iconMap = {
      heart: faHeart,
      baby: faBaby,
      users: faUsers
    };
    return iconMap[iconName] || faHeart;
  };

  return (
    <div className="category-page">
      {/* Хлебные крошки */}
      <div className="breadcrumbs-container">
        <nav className="breadcrumbs">
          <Link to="/" className="breadcrumb-link">Главная</Link>
          <FontAwesomeIcon icon={faChevronRight} className="breadcrumb-separator" />
          <span className="breadcrumb-link active">{category.title}</span>
        </nav>
      </div>

      {/* Заголовок категории */}
      <div className="category-header">
        <h1 className="category-title">
          <FontAwesomeIcon 
            icon={getCategoryIcon(category.name)} 
            className="category-icon"
          />
          {category.name}
        </h1>
      </div>

      {/* Посты категории */}
      <div className="posts-section">
        <h2 className="posts-title">Посты сообщества</h2>
        <div className="posts-list">
          {category.posts.map((post) => (
            <div key={post.id} className="post-card">
              <div className="post-header">
                <div className="author-info">
                  <div className="author-avatar">{post.avatar}</div>
                  <div className="author-details">
                    <div className="author-name">{post.author}</div>
                    <div className="post-meta">
                      <span className="post-time">{post.time}</span>
                      <span className="post-location">
                        <FontAwesomeIcon icon={faMapMarkerAlt} />
                        {post.city}
                      </span>
                    </div>
                  </div>
                </div>
                <div className="post-options">
                  <FontAwesomeIcon icon={faEllipsisV} />
                </div>
              </div>

                             <div className="post-content">
                 <h3 className="post-title">{post.title}</h3>
                 <div className="post-category">{post.category}</div>
                 <p className="post-text">{post.content}</p>
                 <a href="#" className="read-more">Читать далее</a>
               </div>


            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CategoryPage;
