import React, { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronLeft, faChevronRight } from '@fortawesome/free-solid-svg-icons';
import './FeaturedCards.css';
import featuredCardsData from '../../../data/featuredCards.json';

const FeaturedCards = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isAutoPlaying, setIsAutoPlaying] = useState(true);
  const sliderRef = useRef(null);
  const autoPlayRef = useRef(null);

  const largeCards = featuredCardsData.filter(card => card.type === 'large');
  const smallCards = featuredCardsData.filter(card => card.type === 'small');

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % featuredCardsData.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + featuredCardsData.length) % featuredCardsData.length);
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
    // Останавливаем автопереключение при ручном выборе
    setIsAutoPlaying(false);
    // Возобновляем через 5 секунд
    setTimeout(() => setIsAutoPlaying(true), 5000);
  };

  // Автоматическое переключение слайдов
  useEffect(() => {
    if (isAutoPlaying) {
      autoPlayRef.current = setInterval(() => {
        setCurrentSlide((prev) => (prev + 1) % featuredCardsData.length);
      }, 4000);
    }

    return () => {
      if (autoPlayRef.current) {
        clearInterval(autoPlayRef.current);
      }
    };
  }, [isAutoPlaying, featuredCardsData.length]);

  // Останавливаем автопереключение при взаимодействии пользователя
  const handleUserInteraction = () => {
    setIsAutoPlaying(false);
    setTimeout(() => setIsAutoPlaying(true), 5000);
  };

  // Обработчики для кнопок навигации
  const handlePrevClick = () => {
    prevSlide();
    handleUserInteraction();
  };

  const handleNextClick = () => {
    nextSlide();
    handleUserInteraction();
  };

  return (
    <div className="featured-content-wrapper">
      <div className="featured-content">
        {/* Десктопная версия - 2 ряда */}
        <div className="desktop-layout">
          {/* Верхний ряд - 2 большие карточки */}
          <div className="top-row">
            {largeCards.map(card => (
              <Link key={card.id} to={`/post/${card.id}`} className="featured-card-link large">
                <div className={`featured-card ${card.color}`}>
                  <div className="card-content">
                    <h3>{card.title}</h3>
                  </div>
                  <div className="card-image">
                    <div className={`illustration ${card.illustration}`}></div>
                  </div>
                </div>
              </Link>
            ))}
          </div>

          {/* Нижний ряд - 3 маленькие карточки */}
          <div className="bottom-row">
            {smallCards.map(card => (
              <Link key={card.id} to={`/post/${card.id}`} className="featured-card-link small">
                <div className={`featured-card ${card.color}`}>
                  <div className="card-content">
                    <h3>{card.title}</h3>
                  </div>
                  <div className="card-image">
                    <div className={`illustration ${card.illustration}`}></div>
                  </div>
                </div>
              </Link>
            ))}
          </div>
        </div>

        {/* Мобильная версия - слайдер */}
        <div className="mobile-layout">
          <div className="slider-container" ref={sliderRef}>
            <div 
              className="slider-track"
              style={{ transform: `translateX(-${currentSlide * 100}%)` }}
            >
              {featuredCardsData.map(card => (
                <div key={card.id} className="slider-slide">
                  <Link to={`/post/${card.id}`} className="featured-card-link">
                    <div className={`featured-card ${card.color}`}>
                      <div className="card-content">
                        <h3>{card.title}</h3>
                      </div>
                      <div className="card-image">
                        <div className={`illustration ${card.illustration}`}></div>
                      </div>
                    </div>
                  </Link>
                </div>
              ))}
            </div>
          </div>

          {/* Навигационные кнопки */}
          <button className="slider-btn prev-btn" onClick={handlePrevClick}>
            <FontAwesomeIcon icon={faChevronLeft} />
          </button>
          <button className="slider-btn next-btn" onClick={handleNextClick}>
            <FontAwesomeIcon icon={faChevronRight} />
          </button>

          {/* Индикаторы слайдов */}
          <div className="slider-indicators">
            {featuredCardsData.map((_, index) => (
              <button
                key={index}
                className={`indicator ${index === currentSlide ? 'active' : ''}`}
                onClick={() => goToSlide(index)}
              />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default FeaturedCards;
