import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faBookmark, 
  faUser, 
  faMapMarkerAlt, 
  faCalendar,
  faArrowLeft
} from '@fortawesome/free-solid-svg-icons';
import './ArchivePage.css';

const ArchivePage = () => {
  const [archivedPosts, setArchivedPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // –û—Ç–ª–∞–¥–æ—á–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ
    const userData = localStorage.getItem('userData') || sessionStorage.getItem('userData');
    if (userData) {
      try {
        const user = JSON.parse(userData);
        console.log('üë§ –¢–µ–∫—É—â–∏–π –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –≤ ArchivePage:', user);
      } catch (e) {
        console.log('‚ùå –û—à–∏–±–∫–∞ –ø–∞—Ä—Å–∏–Ω–≥–∞ –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è:', e);
      }
    } else {
      console.log('‚ùå –î–∞–Ω–Ω—ã–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –Ω–µ –Ω–∞–π–¥–µ–Ω—ã –≤ ArchivePage');
    }
    
    fetchArchivedPosts();
  }, []);

  const fetchArchivedPosts = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      
      // –ü—Ä–æ–≤–µ—Ä—è–µ–º —Ç–æ–∫–µ–Ω –∏ –ø–æ–ª—É—á–∞–µ–º –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ
      if (token) {
        try {
          const userResponse = await fetch('http://localhost:8000/api/users/profile/', {
            headers: {
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json'
            }
          });
          
          if (userResponse.ok) {
            const userData = await userResponse.json();
            console.log('üë§ –¢–µ–∫—É—â–∏–π –∞–≤—Ç–æ—Ä–∏–∑–æ–≤–∞–Ω–Ω—ã–π –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å:', userData);
          } else {
            console.log('‚ùå –û—à–∏–±–∫–∞ –ø–æ–ª—É—á–µ–Ω–∏—è –ø—Ä–æ—Ñ–∏–ª—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è:', userResponse.status);
          }
        } catch (error) {
          console.log('‚ùå –û—à–∏–±–∫–∞ –ø—Ä–∏ –ø—Ä–æ–≤–µ—Ä–∫–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è:', error);
        }
      }
      
      console.log('üîç –ó–∞–≥—Ä—É–∑–∫–∞ –∞—Ä—Ö–∏–≤–∞...');
      console.log('–¢–æ–∫–µ–Ω:', token ? '–ï—Å—Ç—å' : '–ù–µ—Ç');
      
      // –ü—Ä–æ–≤–µ—Ä—è–µ–º –¥–∞–Ω–Ω—ã–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
      const userData = localStorage.getItem('userData') || sessionStorage.getItem('userData');
      if (userData) {
        try {
          const user = JSON.parse(userData);
          console.log('üë§ –¢–µ–∫—É—â–∏–π –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å:', user);
        } catch (e) {
          console.log('‚ùå –û—à–∏–±–∫–∞ –ø–∞—Ä—Å–∏–Ω–≥–∞ –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è:', e);
        }
      } else {
        console.log('‚ùå –î–∞–Ω–Ω—ã–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –Ω–µ –Ω–∞–π–¥–µ–Ω—ã');
      }
      
      if (!token) {
        setError('–ù–µ–æ–±—Ö–æ–¥–∏–º–∞ –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏—è');
        setLoading(false);
        return;
      }

      console.log('üîó –û—Ç–ø—Ä–∞–≤–ª—è–µ–º –∑–∞–ø—Ä–æ—Å –∫ API –∞—Ä—Ö–∏–≤–∞...');
      const response = await fetch('http://localhost:8000/api/users/archive/', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      console.log('–°—Ç–∞—Ç—É—Å –æ—Ç–≤–µ—Ç–∞:', response.status);
      console.log('–ó–∞–≥–æ–ª–æ–≤–∫–∏ –æ—Ç–≤–µ—Ç–∞:', response.headers);

      if (response.ok) {
        const data = await response.json();
        console.log('–î–∞–Ω–Ω—ã–µ –æ—Ç API:', data);
        console.log('–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –ø–æ—Å—Ç–æ–≤:', data.results ? data.results.length : 0);
        
        setArchivedPosts(data.results || []);
      } else {
        const errorText = await response.text();
        console.error('–û—à–∏–±–∫–∞ API:', errorText);
        setError('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∞—Ä—Ö–∏–≤–∞');
      }
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∞—Ä—Ö–∏–≤–∞:', error);
      setError('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∞—Ä—Ö–∏–≤–∞');
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('ru-RU', {
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      });
    } catch (error) {
      return '–ù–µ–¥–∞–≤–Ω–æ';
    }
  };

  const getAuthorInitials = (author) => {
    if (!author) return '–ê';
    const firstName = author.first_name || '';
    const lastName = author.last_name || '';
    return `${firstName.charAt(0)}${lastName.charAt(0)}`.toUpperCase() || '–ê';
  };

  const getUserAvatarUrl = (userData) => {
    if (userData.avatar) return userData.avatar;
    const initials = getAuthorInitials(userData);
    return `https://via.placeholder.com/40x40/667eea/ffffff?text=${encodeURIComponent(initials)}`;
  };

  if (loading) {
    return (
      <div className="archive-loading">
        <div className="loading-spinner"></div>
        <p>–ó–∞–≥—Ä—É–∑–∫–∞ –∞—Ä—Ö–∏–≤–∞...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="archive-error">
        <h2>–û—à–∏–±–∫–∞</h2>
        <p>{error}</p>
        <button onClick={fetchArchivedPosts} className="btn-retry">
          –ü–æ–ø—Ä–æ–±–æ–≤–∞—Ç—å —Å–Ω–æ–≤–∞
        </button>
      </div>
    );
  }

  return (
    <div className="archive-page">
      {/* –•–ª–µ–±–Ω—ã–µ –∫—Ä–æ—à–∫–∏ */}
      <div className="breadcrumbs-container">
        <nav className="breadcrumbs">
          <Link to="/" className="breadcrumb-link">–ì–ª–∞–≤–Ω–∞—è</Link>
          <FontAwesomeIcon icon={faArrowLeft} className="breadcrumb-separator" />
          <span className="breadcrumb-link active">–ê—Ä—Ö–∏–≤</span>
        </nav>
      </div>

      {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ */}
      <div className="archive-header">
        <div className="archive-title">
          <FontAwesomeIcon icon={faBookmark} className="archive-icon" />
          <h1>–ú–æ–π –∞—Ä—Ö–∏–≤</h1>
        </div>
        <p className="archive-description">
          –ó–¥–µ—Å—å —Å–æ–±—Ä–∞–Ω—ã –≤—Å–µ –ø–æ—Å—Ç—ã, –∫–æ—Ç–æ—Ä—ã–µ –≤—ã —Å–æ—Ö—Ä–∞–Ω–∏–ª–∏ –¥–ª—è —Å–µ–±—è
        </p>
        <button onClick={fetchArchivedPosts} className="refresh-btn">
          –û–±–Ω–æ–≤–∏—Ç—å –∞—Ä—Ö–∏–≤
        </button>
      </div>

      {/* –°–ø–∏—Å–æ–∫ –ø–æ—Å—Ç–æ–≤ */}
      <div className="archive-content">
        {archivedPosts.length === 0 ? (
          <div className="archive-empty">
            <FontAwesomeIcon icon={faBookmark} className="empty-icon" />
            <h3>–ê—Ä—Ö–∏–≤ –ø—É—Å—Ç</h3>
            <p>–í—ã –ø–æ–∫–∞ –Ω–µ —Å–æ—Ö—Ä–∞–Ω–∏–ª–∏ –Ω–∏ –æ–¥–Ω–æ–≥–æ –ø–æ—Å—Ç–∞ –≤ –∞—Ä—Ö–∏–≤</p>
            <Link to="/posts" className="btn-browse-posts">
              –ü—Ä–æ—Å–º–æ—Ç—Ä–µ—Ç—å –ø–æ—Å—Ç—ã
            </Link>
          </div>
        ) : (
          <div className="archived-posts-grid">
            {archivedPosts.map((post) => (
              <div key={post.id} className="archived-post-card">
                <div className="post-header">
                  <div className="author-info">
                    <div className="author-avatar">
                      {post.author.avatar ? (
                        <img 
                          src={post.author.avatar} 
                          alt={post.author.first_name}
                          onError={(e) => {
                            e.target.style.display = 'none';
                            e.target.nextSibling.style.display = 'flex';
                          }}
                        />
                      ) : null}
                      <div className="author-initials" style={{ 
                        display: post.author.avatar ? 'none' : 'flex' 
                      }}>
                        {getAuthorInitials(post.author)}
                      </div>
                    </div>
                    <div className="author-details">
                      <div className="author-name">
                        {post.author.first_name} {post.author.last_name}
                      </div>
                      <div className="post-meta">
                        <span className="post-time">
                          <FontAwesomeIcon icon={faCalendar} />
                          {formatDate(post.created_at)}
                        </span>
                        <span className="post-location">
                          <FontAwesomeIcon icon={faMapMarkerAlt} />
                          {post.author.city || '–ù–µ —É–∫–∞–∑–∞–Ω'}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
                
                <div className="post-content">
                  <h3 className="post-title">
                    <Link to={`/post-detail/${post.slug}`}>
                      {post.title}
                    </Link>
                  </h3>
                  
                  {post.category && (
                    <div className="post-category">
                      {post.category.name}
                    </div>
                  )}
                  
                  {post.short_description && (
                    <p className="post-excerpt">
                      {post.short_description}
                    </p>
                  )}
                  
                  <div className="post-actions">
                    <Link to={`/post-detail/${post.slug}`} className="read-more">
                      –ß–∏—Ç–∞—Ç—å –¥–∞–ª–µ–µ
                    </Link>
                    <span className="archived-date">
                      –°–æ—Ö—Ä–∞–Ω–µ–Ω–æ: {formatDate(post.archived_at)}
                    </span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ArchivePage;
