import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faPen,
  faUser,
  faBaby,
  faEdit,
  faCog,
  faPlus,
  faTimes,
  faMapMarkerAlt
} from '@fortawesome/free-solid-svg-icons';
import profileData from '../../data/profileData.json';
import './ProfilePage.css';

const ProfilePage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const [profile, setProfile] = useState(profileData.profile);
  const [activeTab, setActiveTab] = useState('posts');
  const [selectedCategory, setSelectedCategory] = useState('–í—Å–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏');
  const [showAddChildModal, setShowAddChildModal] = useState(false);
  const [posts, setPosts] = useState([]);
  const [drafts, setDrafts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showEditProfileModal, setShowEditProfileModal] = useState(false);
  const [childData, setChildData] = useState({
    name: '',
    gender: '',
    age: ''
  });
  const [editProfileData, setEditProfileData] = useState({
    city: '',
    birthDate: '',
    status: ''
  });
  const [children, setChildren] = useState([]);

  useEffect(() => {
    // –ü–æ–ª—É—á–∞–µ–º –¥–∞–Ω–Ω—ã–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –∏–∑ localStorage (–µ—Å–ª–∏ –µ—Å—Ç—å)
    const userData = localStorage.getItem('userData');
    if (userData) {
      const user = JSON.parse(userData);
      console.log('–ó–∞–≥—Ä—É–∂–µ–Ω–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è:', user);
      console.log('–ê–≤–∞—Ç–∞—Ä –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è:', user.avatar);
      
      setProfile(prev => ({
        ...prev,
        username: user.first_name || user.username || prev.username,
        fullName: `${user.first_name || ''} ${user.last_name || ''}`.trim(),
        avatar: user.first_name ? `${user.first_name[0]}${user.last_name ? user.last_name[0] : ''}` : prev.avatar,
        location: user.city || prev.location,
        personalInfo: {
          ...prev.personalInfo,
          firstName: user.first_name || prev.personalInfo.firstName,
          lastName: user.last_name || prev.personalInfo.lastName,
          email: user.email || prev.personalInfo.email,
          status: user.status || prev.personalInfo.status,
          city: user.city || prev.personalInfo.city,
          birthDate: user.birth_date || prev.personalInfo.birthDate,
          avatar: user.avatar || prev.personalInfo.avatar
        }
      }));

      // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä—É–µ–º –¥–∞–Ω–Ω—ã–µ –¥–ª—è —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏—è –ø—Ä–æ—Ñ–∏–ª—è
      setEditProfileData({
        city: user.city || '',
        birthDate: user.birth_date || '',
        status: user.status || ''
      });

      // –ó–∞–≥—Ä—É–∂–∞–µ–º –¥–µ—Ç–µ–π –∏–∑ localStorage
      const savedChildren = JSON.parse(localStorage.getItem('children') || '[]');
      setChildren(savedChildren);
    }
    
    // –ó–∞–≥—Ä—É–∂–∞–µ–º –ø–æ—Å—Ç—ã –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
    fetchUserPosts();
  }, [userId]);

  const fetchUserPosts = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      const response = await fetch('http://localhost:8000/api/posts/user-posts/', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        const publishedPosts = data.posts.filter(post => post.status === 'published');
        const draftPosts = data.posts.filter(post => post.status === 'draft');
        
        setPosts(publishedPosts);
        setDrafts(draftPosts);
      } else {
        console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –ø–æ—Å—Ç–æ–≤:', response.status);
      }
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –ø–æ—Å—Ç–æ–≤:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleTabChange = (tabId) => {
    setActiveTab(tabId);
  };

  const handleWritePost = () => {
    navigate('/create-post');
  };

  const handlePublishDraft = async (draftId) => {
    try {
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      const response = await fetch(`http://localhost:8000/api/posts/${draftId}/publish/`, {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ status: 'published' })
      });
      
      if (response.ok) {
        // –û–±–Ω–æ–≤–ª—è–µ–º —Å–ø–∏—Å–∫–∏ –ø–æ—Å—Ç–æ–≤ –∏ —á–µ—Ä–Ω–æ–≤–∏–∫–æ–≤
        await fetchUserPosts();
        alert('–ß–µ—Ä–Ω–æ–≤–∏–∫ —É—Å–ø–µ—à–Ω–æ –æ–ø—É–±–ª–∏–∫–æ–≤–∞–Ω!');
      } else {
        alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø—É–±–ª–∏–∫–∞—Ü–∏–∏ —á–µ—Ä–Ω–æ–≤–∏–∫–∞');
      }
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –ø—É–±–ª–∏–∫–∞—Ü–∏–∏ —á–µ—Ä–Ω–æ–≤–∏–∫–∞:', error);
      alert('–ü—Ä–æ–∏–∑–æ—à–ª–∞ –æ—à–∏–±–∫–∞ –ø—Ä–∏ –ø—É–±–ª–∏–∫–∞—Ü–∏–∏');
    }
  };

  const handleEditProfile = () => {
    setShowEditProfileModal(true);
  };

  const handleAddChild = () => {
    setShowAddChildModal(true);
  };

  const handleCloseModal = () => {
    setShowAddChildModal(false);
    setShowEditProfileModal(false);
    setChildData({ name: '', gender: '', age: '' });
  };

  const handleEditProfileDataChange = (field, value) => {
    setEditProfileData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleSaveProfile = () => {
    // –û–±–Ω–æ–≤–ª—è–µ–º –¥–∞–Ω–Ω—ã–µ –≤ localStorage
    const userData = JSON.parse(localStorage.getItem('userData') || '{}');
    const updatedUserData = {
      ...userData,
      city: editProfileData.city,
      birth_date: editProfileData.birthDate,
      status: editProfileData.status
    };
    localStorage.setItem('userData', JSON.stringify(updatedUserData));

    // –û–±–Ω–æ–≤–ª—è–µ–º —Å–æ—Å—Ç–æ—è–Ω–∏–µ –ø—Ä–æ—Ñ–∏–ª—è
    setProfile(prev => ({
      ...prev,
      location: editProfileData.city,
      personalInfo: {
        ...prev.personalInfo,
        city: editProfileData.city,
        birthDate: editProfileData.birthDate,
        status: editProfileData.status
      }
    }));

    // –ó–∞–∫—Ä—ã–≤–∞–µ–º –º–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ
    setShowEditProfileModal(false);
  };

  const handleChildDataChange = (field, value) => {
    setChildData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleSaveChild = () => {
    if (childData.name && childData.gender && childData.age) {
      // –ó–¥–µ—Å—å –º–æ–∂–Ω–æ —Å–æ—Ö—Ä–∞–Ω–∏—Ç—å –¥–∞–Ω–Ω—ã–µ —Ä–µ–±–µ–Ω–∫–∞ –≤ localStorage –∏–ª–∏ –æ—Ç–ø—Ä–∞–≤–∏—Ç—å –Ω–∞ —Å–µ—Ä–≤–µ—Ä
      console.log('–î–∞–Ω–Ω—ã–µ —Ä–µ–±–µ–Ω–∫–∞:', childData);
      
      // –°–æ—Ö—Ä–∞–Ω—è–µ–º –≤ localStorage –¥–ª—è –¥–µ–º–æ–Ω—Å—Ç—Ä–∞—Ü–∏–∏
      const existingChildren = JSON.parse(localStorage.getItem('children') || '[]');
      const newChild = {
        id: Date.now(),
        ...childData
      };
      const updatedChildren = [...existingChildren, newChild];
      localStorage.setItem('children', JSON.stringify(updatedChildren));
      
      // –û–±–Ω–æ–≤–ª—è–µ–º —Å–æ—Å—Ç–æ—è–Ω–∏–µ
      setChildren(updatedChildren);
      
      handleCloseModal();
    }
  };

  const getStatusText = (status) => {
    const statusMap = {
      'mom': '–ú–∞–º–∞',
      'pregnant': '–ë–µ—Ä–µ–º–µ–Ω–Ω–∞—è',
      'planning': '–ü–ª–∞–Ω–∏—Ä—É—é –±–µ—Ä–µ–º–µ–Ω–Ω–æ—Å—Ç—å',
      'other': '–î—Ä—É–≥–æ–µ'
    };
    return statusMap[status] || status;
  };

  const getChildAvatar = (gender) => {
    return gender === 'boy' ? 'üë∂' : 'üëß';
  };

  const getChildAgeText = (age) => {
    const ageMap = {
      '0-1': '0-1 –≥–æ–¥',
      '1-2': '1-2 –≥–æ–¥–∞',
      '2-3': '2-3 –≥–æ–¥–∞',
      '3-4': '3-4 –≥–æ–¥–∞',
      '4-5': '4-5 –ª–µ—Ç',
      '5-6': '5-6 –ª–µ—Ç',
      '6-7': '6-7 –ª–µ—Ç',
      '7+': '7+ –ª–µ—Ç'
    };
    return ageMap[age] || age;
  };

  const renderTabContent = () => {
    switch (activeTab) {
      case 'posts':
        return (
          <div className="tab-content">
                         <div className="content-filters">
               <div className="category-filter">
                 <select 
                   value={selectedCategory} 
                   onChange={(e) => setSelectedCategory(e.target.value)}
                 >
                   {profile.categories.map(category => (
                     <option key={category} value={category}>{category}</option>
                   ))}
                 </select>
               </div>
             </div>
            
            {profile.posts.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">
                  <FontAwesomeIcon icon={faPen} />
                </div>
                <h3>–í—ã –Ω–µ –Ω–∞–ø–∏—Å–∞–ª–∏ –µ—â–µ –Ω–∏ –æ–¥–Ω–æ–π –∑–∞–ø–∏—Å–∏</h3>
                <p>–ü–æ–¥–µ–ª–∏—Ç–µ—Å—å —Å–≤–æ–∏–º–∏ –º—ã—Å–ª—è–º–∏ –∏ –æ–ø—ã—Ç–æ–º —Å –¥—Ä—É–≥–∏–º–∏ –º–∞–º–∞–º–∏</p>
                <button className="write-button" onClick={handleWritePost}>
                  <FontAwesomeIcon icon={faPen} />
                  –ù–∞–ø–∏—Å–∞—Ç—å –ø–µ—Ä–≤—É—é –∑–∞–ø–∏—Å—å
                </button>
              </div>
            ) : (
              <div className="posts-list">
                {/* –ó–¥–µ—Å—å –±—É–¥–µ—Ç —Å–ø–∏—Å–æ–∫ –ø–æ—Å—Ç–æ–≤ */}
              </div>
            )}
          </div>
        );
      
      case 'drafts':
        return (
          <div className="tab-content">
            <div className="empty-state">
              <div className="empty-icon">
                <FontAwesomeIcon icon={faEdit} />
              </div>
              <h3>–£ –≤–∞—Å –ø–æ–∫–∞ –Ω–µ—Ç —á–µ—Ä–Ω–æ–≤–∏–∫–æ–≤</h3>
              <p>–°–æ–∑–¥–∞–π—Ç–µ —á–µ—Ä–Ω–æ–≤–∏–∫, —á—Ç–æ–±—ã —Å–æ—Ö—Ä–∞–Ω–∏—Ç—å –∏–¥–µ–∏ –¥–ª—è –±—É–¥—É—â–∏—Ö –∑–∞–ø–∏—Å–µ–π</p>
            </div>
          </div>
        );
      
      case 'archive':
        return (
          <div className="tab-content">
            <div className="empty-state">
              <div className="empty-icon">
                <FontAwesomeIcon icon={faCog} />
              </div>
              <h3>–ê—Ä—Ö–∏–≤ –ø—É—Å—Ç</h3>
              <p>–ó–¥–µ—Å—å –±—É–¥—É—Ç —Ö—Ä–∞–Ω–∏—Ç—å—Å—è –≤–∞—à–∏ —Å—Ç–∞—Ä—ã–µ –∑–∞–ø–∏—Å–∏</p>
            </div>
          </div>
        );
      
      default:
        return null;
    }
  };

  return (
    <div className="profile-page">
      {/* Main Content */}
      <main className="profile-main">
        {/* Profile Banner –≤ —Å—Ç–∏–ª–µ MomsBlog */}
        <div className="profile-banner">
          <div className="profile-avatar">
            {profile.personalInfo.avatar && profile.personalInfo.avatar !== 'null' ? (
              <img 
                src={profile.personalInfo.avatar} 
                alt={profile.fullName}
                onError={(e) => {
                  console.log('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∞–≤–∞—Ç–∞—Ä–∞:', e.target.src);
                  e.target.style.display = 'none';
                  e.target.nextSibling.style.display = 'flex';
                }}
                onLoad={() => {
                  console.log('–ê–≤–∞—Ç–∞—Ä —É—Å–ø–µ—à–Ω–æ –∑–∞–≥—Ä—É–∂–µ–Ω:', profile.personalInfo.avatar);
                }}
              />
            ) : null}
            <div className="avatar-placeholder" style={{ display: (profile.personalInfo.avatar && profile.personalInfo.avatar !== 'null') ? 'none' : 'flex' }}>
              {profile.avatar}
              {profile.isOnline && <span className="online-indicator"></span>}
            </div>
          </div>
          
          <div className="profile-info">
            <div className="profile-name">
              <h1>{profile.username}</h1>
            </div>
            <div className="profile-details">
              <div className="profile-location">
                <FontAwesomeIcon icon={faMapMarkerAlt} />
                {profile.location}
              </div>
              <div className="profile-age">
                <FontAwesomeIcon icon={faUser} />
                {profile.personalInfo.birthDate ? 
                  `${new Date().getFullYear() - new Date(profile.personalInfo.birthDate).getFullYear()} –ª–µ—Ç` : 
                  '–í–æ–∑—Ä–∞—Å—Ç –Ω–µ —É–∫–∞–∑–∞–Ω'
                }
              </div>
              <div className="profile-status">
                <FontAwesomeIcon icon={faBaby} />
                {getStatusText(profile.personalInfo.status)}
              </div>
            </div>
            <div className="profile-actions">
              <button className="write-diary-btn" onClick={handleWritePost}>
                <FontAwesomeIcon icon={faPen} />
                –ù–∞–ø–∏—Å–∞—Ç—å –≤ –¥–Ω–µ–≤–Ω–∏–∫
              </button>
              <button className="edit-profile-btn" onClick={handleEditProfile}>
                <FontAwesomeIcon icon={faEdit} />
                –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å –ø—Ä–æ—Ñ–∏–ª—å
              </button>
            </div>
          </div>
        </div>

        {/* Statistics */}
        <div className="profile-stats">
          <div className="stat-item">
            <span className="stat-number">{profile.statistics.posts}</span>
            <span className="stat-label">–∑–∞–ø–∏—Å–µ–π</span>
          </div>
          <div className="stat-item">
            <span className="stat-number">{profile.statistics.friends}</span>
            <span className="stat-label">–ø–æ–¥–ø–∏—Å–æ–∫</span>
          </div>
          <div className="stat-item">
            <span className="stat-number">{profile.statistics.subscribers}</span>
            <span className="stat-label">–ø–æ–¥–ø–∏—Å—á–∏–∫–æ–≤</span>
          </div>
        </div>

        {/* Add Child Button */}
        <div className="add-child-section">
          {children.length > 0 && (
            <div className="children-list">
              {children.map((child) => (
                <div key={child.id} className="child-card">
                  <div className="child-avatar">
                    <span className="child-emoji">{getChildAvatar(child.gender)}</span>
                  </div>
                  <div className="child-info">
                    <div className="child-name">{child.name}</div>
                    <div className="child-age">{getChildAgeText(child.age)}</div>
                  </div>
                </div>
              ))}
            </div>
          )}
          <button className="add-child-btn" onClick={handleAddChild}>
            <FontAwesomeIcon icon={faPlus} />
            –î–æ–±–∞–≤–∏—Ç—å —Ä–µ–±–µ–Ω–∫–∞
          </button>
        </div>

        {/* Tabs */}
        <div className="profile-tabs">
          <button
            className={`tab-button ${activeTab === 'posts' ? 'active' : ''}`}
            onClick={() => handleTabChange('posts')}
          >
            –ó–∞–ø–∏—Å–∏
          </button>
          <button
            className={`tab-button ${activeTab === 'drafts' ? 'active' : ''}`}
            onClick={() => handleTabChange('drafts')}
          >
            –ß–µ—Ä–Ω–æ–≤–∏–∫–∏
          </button>
          <button
            className={`tab-button ${activeTab === 'archive' ? 'active' : ''}`}
            onClick={() => handleTabChange('archive')}
          >
            –ê—Ä—Ö–∏–≤
          </button>
        </div>

        {/* Tab Content */}
        {renderTabContent()}
      </main>

      {/* Add Child Modal */}
      {showAddChildModal && (
        <div className="modal-overlay" onClick={handleCloseModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>–î–æ–±–∞–≤–∏—Ç—å —Ä–µ–±–µ–Ω–∫–∞</h3>
              <button className="modal-close" onClick={handleCloseModal}>
                <FontAwesomeIcon icon={faTimes} />
              </button>
            </div>
            
            <div className="modal-body">
              <div className="form-group">
                <label>–ò–º—è —Ä–µ–±–µ–Ω–∫–∞</label>
                <input
                  type="text"
                  placeholder="–í–≤–µ–¥–∏—Ç–µ –∏–º—è"
                  value={childData.name}
                  onChange={(e) => handleChildDataChange('name', e.target.value)}
                />
              </div>
              
              <div className="form-group">
                <label>–ü–æ–ª</label>
                <select
                  value={childData.gender}
                  onChange={(e) => handleChildDataChange('gender', e.target.value)}
                >
                  <option value="">–í—ã–±–µ—Ä–∏—Ç–µ –ø–æ–ª</option>
                  <option value="boy">–ú–∞–ª—å—á–∏–∫</option>
                  <option value="girl">–î–µ–≤–æ—á–∫–∞</option>
                </select>
              </div>
              
              <div className="form-group">
                <label>–í–æ–∑—Ä–∞—Å—Ç</label>
                <select
                  value={childData.age}
                  onChange={(e) => handleChildDataChange('age', e.target.value)}
                >
                  <option value="">–í—ã–±–µ—Ä–∏—Ç–µ –≤–æ–∑—Ä–∞—Å—Ç</option>
                  <option value="0-1">0-1 –≥–æ–¥</option>
                  <option value="1-2">1-2 –≥–æ–¥–∞</option>
                  <option value="2-3">2-3 –≥–æ–¥–∞</option>
                  <option value="3-4">3-4 –≥–æ–¥–∞</option>
                  <option value="4-5">4-5 –ª–µ—Ç</option>
                  <option value="5-6">5-6 –ª–µ—Ç</option>
                  <option value="6-7">6-7 –ª–µ—Ç</option>
                  <option value="7+">7+ –ª–µ—Ç</option>
                </select>
              </div>
            </div>
            
            <div className="modal-footer">
              <button className="btn-cancel" onClick={handleCloseModal}>
                –û—Ç–º–µ–Ω–∞
              </button>
              <button 
                className="btn-save" 
                onClick={handleSaveChild}
                disabled={!childData.name || !childData.gender || !childData.age}
              >
                –°–æ—Ö—Ä–∞–Ω–∏—Ç—å
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Edit Profile Modal */}
      {showEditProfileModal && (
        <div className="modal-overlay" onClick={handleCloseModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å –ø—Ä–æ—Ñ–∏–ª—å</h3>
              <button className="modal-close" onClick={handleCloseModal}>
                <FontAwesomeIcon icon={faTimes} />
              </button>
            </div>
            
            <div className="modal-body">
              <div className="form-group">
                <label>–ì–æ—Ä–æ–¥</label>
                <input
                  type="text"
                  placeholder="–í–≤–µ–¥–∏—Ç–µ –≥–æ—Ä–æ–¥"
                  value={editProfileData.city}
                  onChange={(e) => handleEditProfileDataChange('city', e.target.value)}
                />
              </div>
              
              <div className="form-group">
                <label>–î–∞—Ç–∞ —Ä–æ–∂–¥–µ–Ω–∏—è</label>
                <input
                  type="date"
                  value={editProfileData.birthDate}
                  onChange={(e) => handleEditProfileDataChange('birthDate', e.target.value)}
                />
              </div>
              
              <div className="form-group">
                <label>–°—Ç–∞—Ç—É—Å</label>
                <select
                  value={editProfileData.status}
                  onChange={(e) => handleEditProfileDataChange('status', e.target.value)}
                >
                  <option value="">–í—ã–±–µ—Ä–∏—Ç–µ —Å—Ç–∞—Ç—É—Å</option>
                  <option value="mom">–ú–∞–º–∞</option>
                  <option value="pregnant">–ë–µ—Ä–µ–º–µ–Ω–Ω–∞—è</option>
                  <option value="planning">–ü–ª–∞–Ω–∏—Ä—É—é –±–µ—Ä–µ–º–µ–Ω–Ω–æ—Å—Ç—å</option>
                  <option value="other">–î—Ä—É–≥–æ–µ</option>
                </select>
              </div>
            </div>
            
            <div className="modal-footer">
              <button className="btn-cancel" onClick={handleCloseModal}>
                –û—Ç–º–µ–Ω–∞
              </button>
              <button 
                className="btn-save" 
                onClick={handleSaveProfile}
                disabled={!editProfileData.city || !editProfileData.birthDate || !editProfileData.status}
              >
                –°–æ—Ö—Ä–∞–Ω–∏—Ç—å
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProfilePage;
