import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSave, faEye } from '@fortawesome/free-solid-svg-icons';
import { apiClient } from '../../utils/api.js';
import { removeTokens, checkTokenValidity, getUserData, getUserIdFromToken } from '../../utils/auth.js';
import './CreatePost.css';

const CreatePost = () => {
  const navigate = useNavigate();
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    short_description: '',
    content: '',
    category: '',
    status: 'draft'
  });
  const [errors, setErrors] = useState({});

  useEffect(() => {
    const checkAuth = async () => {
      const isValid = await checkTokenValidity();
      if (!isValid) {
        removeTokens();
        navigate('/auth');
        return;
      }
      fetchCategories();
    };

    checkAuth();
  }, [navigate]);

  const fetchCategories = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/posts/categories/');
      if (response.ok) {
        const data = await response.json();
        console.log('Полученные данные категорий:', data);
        // Проверяем, что data является массивом
        if (Array.isArray(data)) {
          setCategories(data);
        } else if (data.results && Array.isArray(data.results)) {
          // Если API возвращает объект с полем results
          setCategories(data.results);
        } else {
          console.error('Неожиданный формат данных категорий:', data);
          setCategories([]);
        }
      } else {
        console.error('Ошибка загрузки категорий:', response.status);
        setCategories([]);
      }
    } catch (error) {
      console.error('Ошибка загрузки категорий:', error);
      setCategories([]);
      // Показываем сообщение пользователю
      alert('Не удалось загрузить категории. Убедитесь, что сервер запущен.');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Очищаем ошибку для этого поля
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.title.trim()) {
      newErrors.title = 'Заголовок обязателен';
    }

    if (!formData.short_description.trim()) {
      newErrors.short_description = 'Краткое описание обязательно';
    }

    if (!formData.content.trim()) {
      newErrors.content = 'Содержание обязательно';
    }

    if (!formData.category) {
      newErrors.category = 'Выберите категорию';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setLoading(true);

    try {
      const formDataToSend = new FormData();
      formDataToSend.append('title', formData.title);
      formDataToSend.append('short_description', formData.short_description);
      formDataToSend.append('content', formData.content);
      formDataToSend.append('category', formData.category);
      formDataToSend.append('status', formData.status);


      const data = await apiClient.createPost(formDataToSend);
      console.log('Ответ сервера при создании поста:', data);
      console.log('Статус созданного поста:', data.post?.status);

        // Если это черновик, перенаправляем на профиль
        if (formData.status === 'draft') {
          const userData = getUserData();
          const userId = userData?.id || getUserIdFromToken() || 1;
          console.log('Перенаправление на профиль пользователя:', userId);
          navigate(`/profile/${userId}`);
        } else {
          // Если опубликованный пост, перенаправляем на детали поста
          navigate(`/post-detail/${data.post.slug}`);
        }
    } catch (error) {
      console.error('Ошибка создания поста:', error);

      if (error.message.includes('авторизация')) {
        removeTokens();
        navigate('/auth');
      } else {
        setErrors({ submit: 'Произошла ошибка при создании поста' });
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="create-post">
      <div className="create-post-container">
        <div className="create-post-header">
          <h1>Создать новый пост</h1>
          <p>Поделитесь своими мыслями и опытом с другими мамами</p>
        </div>

        <form onSubmit={handleSubmit} className="create-post-form">
          {/* Основная информация */}
          <div className="form-section">
            <h3>Основная информация</h3>

            <div className="form-group">
              <label htmlFor="title">Заголовок *</label>
              <input
                type="text"
                id="title"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                placeholder="Введите заголовок поста"
                className={errors.title ? 'error' : ''}
              />
              {errors.title && <span className="error-message">{errors.title}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="short_description">Краткое описание (1-2 слова) *</label>
              <input
                type="text"
                id="short_description"
                name="short_description"
                value={formData.short_description}
                onChange={handleInputChange}
                placeholder="Например: Прикорм, Сон, Игры"
                className={errors.short_description ? 'error' : ''}
              />
              {errors.short_description && <span className="error-message">{errors.short_description}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="category">Категория *</label>
              <select
                id="category"
                name="category"
                value={formData.category}
                onChange={handleInputChange}
                className={errors.category ? 'error' : ''}
              >
                <option value="">Выберите категорию</option>
                {Array.isArray(categories) && categories.map(category => (
                  <option key={category.id} value={category.id}>
                    {category.name}
                  </option>
                ))}
              </select>
              {errors.category && <span className="error-message">{errors.category}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="content">Содержание *</label>
              <textarea
                id="content"
                name="content"
                value={formData.content}
                onChange={handleInputChange}
                placeholder="Напишите содержание вашего поста..."
                rows="10"
                className={errors.content ? 'error' : ''}
              />
              {errors.content && <span className="error-message">{errors.content}</span>}
            </div>
          </div>

          {/* Настройки */}
          <div className="form-section">
            <h3>Настройки</h3>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="status">Статус публикации</label>
                <select
                  id="status"
                  name="status"
                  value={formData.status}
                  onChange={handleInputChange}
                >
                  <option value="draft">Черновик</option>
                  <option value="published">Опубликовать</option>
                </select>
              </div>


            </div>
          </div>

          {/* Общая ошибка */}
          {errors.submit && (
            <div className="form-error">
              <span className="error-message">{errors.submit}</span>
            </div>
          )}

          {/* Кнопки */}
          <div className="form-actions">
            <button type="submit" className="btn-save" disabled={loading}>
              <FontAwesomeIcon icon={faSave} />
              {loading ? 'Создание...' : 'Создать пост'}
            </button>

            <button type="button" className="btn-preview" onClick={() => navigate('/')}>
              <FontAwesomeIcon icon={faEye} />
              Отмена
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreatePost;
