import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faMessage, faPlus } from '@fortawesome/free-solid-svg-icons';
import { apiClient } from '../../utils/api';
import './FriendsPage.css';

const FriendsPage = () => {
  const navigate = useNavigate();
  const [users, setUsers] = useState([]);
  const [chats, setChats] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setIsLoading(true);
      
      // Проверяем токен
      const token = localStorage.getItem('access_token');
      if (!token) {
        navigate('/auth');
        return;
      }
      
      // Получаем текущего пользователя
      const user = await apiClient.getCurrentUser();
      setCurrentUser(user);
      
      // Получаем список пользователей
      const usersData = await apiClient.getUsers();
      setUsers(usersData);
      
      // Получаем чаты
      const chatsData = await apiClient.getChats();
      setChats(chatsData);
      
    } catch (error) {
      console.error('Ошибка загрузки данных:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const startChat = async (userId) => {
    try {
      // Создаем или получаем чат
      const chat = await apiClient.createChat(userId);
      
      // Переходим к чату
      navigate(`/chat/${chat.id}`);
      
    } catch (error) {
      console.error('Ошибка создания чата:', error);
    }
  };

  const getChatUser = (chat) => {
    return chat.user1_id === currentUser.id ? chat.user2_id : chat.user1_id;
  };

  const getUserById = (userId) => {
    return users.find(user => user.id === userId);
  };

  if (isLoading) {
    return (
      <div className="friends-page">
        <div className="loading">Загрузка...</div>
      </div>
    );
  }

  return (
    <div className="friends-page">
      <div className="friends-header">
        <h1>Чаты</h1>
      </div>

      <div className="friends-content">
        {/* Существующие чаты */}
        {chats.length > 0 && (
          <div className="chats-section">
            <h2>Активные чаты</h2>
            <div className="friends-list">
              {chats.map((chat) => {
                const otherUserId = getChatUser(chat);
                const otherUser = getUserById(otherUserId);
                
                if (!otherUser) return null;
                
                return (
                  <div
                    key={chat.id}
                    className="friend-item chat-item"
                    onClick={() => navigate(`/chat/${chat.id}`)}
                  >
                    <div className="friend-avatar">
                      <img 
                        src={otherUser.avatar || '/default-avatar.png'} 
                        alt={otherUser.name} 
                      />
                    </div>
                    <div className="friend-info">
                      <h3 className="friend-name">{otherUser.name}</h3>
                      <p className="friend-city">📍 {otherUser.city || 'Город не указан'}</p>
                    </div>
                    <div className="friend-actions">
                      <button 
                        className="message-button"
                        onClick={(e) => {
                          e.stopPropagation();
                          navigate(`/chat/${chat.id}`);
                        }}
                        title="Открыть чат"
                      >
                        <FontAwesomeIcon icon={faMessage} />
                      </button>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        )}

        {/* Все пользователи */}
        <div className="users-section">
          <h2>Все пользователи</h2>
          <div className="friends-list">
            {users.map((user) => (
              <div key={user.id} className="friend-item">
                <div className="friend-avatar">
                  <img 
                    src={user.avatar || '/default-avatar.png'} 
                    alt={user.name} 
                  />
                </div>
                <div className="friend-info">
                  <h3 className="friend-name">{user.name}</h3>
                  <p className="friend-city">📍 {user.city || 'Город не указан'}</p>
                </div>
                <div className="friend-actions">
                  <button 
                    className="message-button"
                    onClick={() => startChat(user.id)}
                    title="Начать чат"
                  >
                    <FontAwesomeIcon icon={faMessage} />
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default FriendsPage;
