import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faBookmark, 
  faUser, 
  faMapMarkerAlt, 
  faCalendar,
  faArrowLeft
} from '@fortawesome/free-solid-svg-icons';
import './ArchivePage.css';

const ArchivePage = () => {
  const [archivedPosts, setArchivedPosts] = useState([]);

  useEffect(() => {
    // Сразу показываем тестовые посты
    const testPosts = [
      {
        id: 12,
        title: 'Выходные и муж!!!!!!!!!!',
        slug: 'post-1',
        content: 'Тестовый контент',
        short_description: 'Описание поста',
        status: 'published',
        created_at: '2025-08-31T22:42:39.719329Z',
        published_at: '2025-08-31T22:42:39.719329Z',
        author: {
          id: 5,
          first_name: 'Владислав',
          last_name: 'Мелевич',
          username: 'владиславмелевич',
          avatar: null,
          city: 'Минск'
        },
        category: {
          id: 1,
          name: 'Зачатие',
          slug: 'zachatie'
        },
        archived_at: '2025-08-31T22:42:39.719329Z'
      },
      {
        id: 11,
        title: 'Зачатие',
        slug: 'post-11',
        content: 'Тестовый контент',
        short_description: 'Описание поста',
        status: 'published',
        created_at: '2025-08-31T22:42:39.719329Z',
        published_at: '2025-08-31T22:42:39.719329Z',
        author: {
          id: 4,
          first_name: 'Юлия',
          last_name: 'Мелевич',
          username: 'юлиямелевич',
          avatar: null,
          city: 'Минск'
        },
        category: {
          id: 1,
          name: 'Зачатие',
          slug: 'zachatie'
        },
        archived_at: '2025-08-31T22:42:39.719329Z'
      },
      {
        id: 13,
        title: 'Третий пост для тестирования',
        slug: 'post-13',
        content: 'Тестовый контент третьего поста',
        short_description: 'Описание третьего поста',
        status: 'published',
        created_at: '2025-08-31T22:42:39.719329Z',
        published_at: '2025-08-31T22:42:39.719329Z',
        author: {
          id: 5,
          first_name: 'Владислав',
          last_name: 'Мелевич',
          username: 'владиславмелевич',
          avatar: null,
          city: 'Минск'
        },
        category: {
          id: 1,
          name: 'Зачатие',
          slug: 'zachatie'
        },
        archived_at: '2025-08-31T22:42:39.719329Z'
      },
      {
        id: 14,
        title: 'Четвертый пост в архиве',
        slug: 'post-14',
        content: 'Тестовый контент четвертого поста',
        short_description: 'Описание четвертого поста',
        status: 'published',
        created_at: '2025-08-31T22:42:39.719329Z',
        published_at: '2025-08-31T22:42:39.719329Z',
        author: {
          id: 4,
          first_name: 'Юлия',
          last_name: 'Мелевич',
          username: 'юлиямелевич',
          avatar: null,
          city: 'Минск'
        },
        category: {
          id: 1,
          name: 'Зачатие',
          slug: 'zachatie'
        },
        archived_at: '2025-08-31T22:42:39.719329Z'
      }
    ];
    
    console.log('🚀 Показываем тестовые посты сразу!');
    console.log('📊 Количество постов:', testPosts.length);
    setArchivedPosts(testPosts);
  }, []);

  const formatDate = (dateString) => {
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('ru-RU', {
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      });
    } catch (error) {
      return 'Недавно';
    }
  };

  const getAuthorInitials = (author) => {
    if (!author) return 'А';
    const firstName = author.first_name || '';
    const lastName = author.last_name || '';
    return `${firstName.charAt(0)}${lastName.charAt(0)}`.toUpperCase() || 'А';
  };

  return (
    <div className="archive-page">
      {/* Хлебные крошки */}
      <div className="breadcrumbs-container">
        <nav className="breadcrumbs">
          <Link to="/" className="breadcrumb-link">Главная</Link>
          <FontAwesomeIcon icon={faArrowLeft} className="breadcrumb-separator" />
          <span className="breadcrumb-link active">Архив</span>
        </nav>
      </div>

      {/* Заголовок */}
      <div className="archive-header">
        <div className="archive-title">
          <FontAwesomeIcon icon={faBookmark} className="archive-icon" />
          <h1>Мой архив</h1>
        </div>
        <p className="archive-description">
          Здесь собраны все посты, которые вы сохранили для себя
        </p>
        <div style={{background: 'yellow', padding: '10px', margin: '10px 0'}}>
          <strong>ОТЛАДКА:</strong> archivedPosts.length = {archivedPosts.length}
        </div>
      </div>

      {/* Список постов */}
      <div className="archive-content">
        {archivedPosts.length === 0 ? (
          <div className="archive-empty">
            <FontAwesomeIcon icon={faBookmark} className="empty-icon" />
            <h3>Архив пуст</h3>
            <p>Вы пока не сохранили ни одного поста в архив</p>
            <Link to="/posts" className="btn-browse-posts">
              Просмотреть посты
            </Link>
          </div>
        ) : (
          <div className="archived-posts-grid">
            {archivedPosts.map((post) => (
              <div key={post.id} className="archived-post-card">
                <div className="post-header">
                  <div className="author-info">
                    <div className="author-avatar">
                      {post.author.avatar ? (
                        <img 
                          src={post.author.avatar} 
                          alt={post.author.first_name}
                          onError={(e) => {
                            e.target.style.display = 'none';
                            e.target.nextSibling.style.display = 'flex';
                          }}
                        />
                      ) : null}
                      <div className="author-initials" style={{ 
                        display: post.author.avatar ? 'none' : 'flex' 
                      }}>
                        {getAuthorInitials(post.author)}
                      </div>
                    </div>
                    <div className="author-details">
                      <div className="author-name">
                        {post.author.first_name} {post.author.last_name}
                      </div>
                      <div className="post-meta">
                        <span className="post-time">
                          <FontAwesomeIcon icon={faCalendar} />
                          {formatDate(post.created_at)}
                        </span>
                        <span className="post-location">
                          <FontAwesomeIcon icon={faMapMarkerAlt} />
                          {post.author.city || 'Не указан'}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
                
                <div className="post-content">
                  <h3 className="post-title">
                    <Link to={`/post-detail/${post.slug}`}>
                      {post.title}
                    </Link>
                  </h3>
                  
                  {post.category && (
                    <div className="post-category">
                      {post.category.name}
                    </div>
                  )}
                  
                  {post.short_description && (
                    <p className="post-excerpt">
                      {post.short_description}
                    </p>
                  )}
                  
                  <div className="post-actions">
                    <Link to={`/post-detail/${post.slug}`} className="read-more">
                      Читать далее
                    </Link>
                    <span className="archived-date">
                      Сохранено: {formatDate(post.archived_at)}
                    </span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ArchivePage;
