import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEye, faEyeSlash } from '@fortawesome/free-solid-svg-icons';
import { apiClient } from '../../utils/api';
import './AuthPage.css';

const AuthPage = () => {
  const navigate = useNavigate();
  const [isLogin, setIsLogin] = useState(true);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  // Состояния для формы
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    firstName: '',
    lastName: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    setError(''); // Очищаем ошибку при изменении
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    // Валидация паролей при регистрации
    if (!isLogin && formData.password !== formData.confirmPassword) {
      setError('Пароли не совпадают');
      setIsLoading(false);
      return;
    }

    try {
      if (isLogin) {
        // Вход
        const response = await apiClient.login(formData.email, formData.password);
        console.log('Полный ответ сервера при входе:', response);
        console.log('Тип ответа:', typeof response);
        console.log('Ключи в ответе:', Object.keys(response || {}));
        const token = response.access_token || response.access;
        const refreshToken = response.refresh_token || response.refresh;
        if (token) {
          apiClient.setToken(token);

          // Сохраняем refresh токен
          if (refreshToken) {
            localStorage.setItem('refreshToken', refreshToken);
          }

          // Сохраняем данные пользователя для Header
          const userData = {
            id: response.user?.id || response.id,
            first_name: response.user?.first_name || response.first_name,
            last_name: response.user?.last_name || response.last_name,
            email: formData.email,
            username: response.user?.username || response.username
          };
          localStorage.setItem('userData', JSON.stringify(userData));

          // Обновляем состояние авторизации в Header
          window.dispatchEvent(new Event('authUpdate'));
          navigate('/friends');
        } else {
          setError('Не удалось получить токен авторизации');
        }
      } else {
        // Регистрация
        const response = await apiClient.register(formData);
        console.log('Ответ сервера при регистрации:', response);
        const token = response.access_token || response.access;
        const refreshToken = response.refresh_token || response.refresh;
        if (token) {
          apiClient.setToken(token);

          // Сохраняем refresh токен
          if (refreshToken) {
            localStorage.setItem('refreshToken', refreshToken);
          }

          // Сохраняем данные пользователя для Header
          const userData = {
            id: response.user?.id || response.id,
            first_name: response.user?.first_name || response.first_name || formData.firstName,
            last_name: response.user?.last_name || response.last_name || formData.lastName,
            email: formData.email,
            username: response.user?.username || response.username
          };
          localStorage.setItem('userData', JSON.stringify(userData));

          // Обновляем состояние авторизации в Header
          window.dispatchEvent(new Event('authUpdate'));
          navigate('/friends');
        } else {
          setError('Не удалось получить токен авторизации');
        }
      }
    } catch (error) {
      console.error('Ошибка аутентификации:', error);
      setError(error.message || 'Произошла ошибка');
    } finally {
      setIsLoading(false);
    }
  };

  const toggleMode = () => {
    setIsLogin(!isLogin);
    setError('');
    setFormData({
      email: '',
      password: '',
      confirmPassword: '',
      firstName: '',
      lastName: ''
    });
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const toggleConfirmPasswordVisibility = () => {
    setShowConfirmPassword(!showConfirmPassword);
  };

  return (
    <div className="auth-page">
      <div className="auth-container">
        <div className="auth-header">
          <h1>{isLogin ? 'Вход' : 'Регистрация'}</h1>
          <p>{isLogin ? 'Войдите в свой аккаунт' : 'Создайте новый аккаунт'}</p>
        </div>

        <form className="auth-form" onSubmit={handleSubmit}>
          {error && (
            <div className="error-message">
              {error}
            </div>
          )}

          {!isLogin && (
            <div className="form-group">
              <div className="input-wrapper">
                <input
                  type="text"
                  name="firstName"
                  placeholder="Имя"
                  value={formData.firstName}
                  onChange={handleInputChange}
                  required={!isLogin}
                />
              </div>
            </div>
          )}

          {!isLogin && (
            <div className="form-group">
              <div className="input-wrapper">
                <input
                  type="text"
                  name="lastName"
                  placeholder="Фамилия"
                  value={formData.lastName}
                  onChange={handleInputChange}
                  required={!isLogin}
                />
              </div>
            </div>
          )}

          <div className="form-group">
            <div className="input-wrapper">
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleInputChange}
                required
              />
            </div>
          </div>

          <div className="form-group">
            <div className="input-wrapper">
              <input
                type={showPassword ? 'text' : 'password'}
                name="password"
                placeholder="Пароль"
                value={formData.password}
                onChange={handleInputChange}
                required
                minLength={6}
              />
              <button
                type="button"
                className="password-toggle"
                onClick={togglePasswordVisibility}
              >
                <FontAwesomeIcon icon={showPassword ? faEyeSlash : faEye} />
              </button>
            </div>
          </div>

          {!isLogin && (
            <div className="form-group">
              <div className="input-wrapper">
                <input
                  type={showConfirmPassword ? 'text' : 'password'}
                  name="confirmPassword"
                  placeholder="Подтвердите пароль"
                  value={formData.confirmPassword}
                  onChange={handleInputChange}
                  required={!isLogin}
                  minLength={6}
                />
                <button
                  type="button"
                  className="password-toggle"
                  onClick={toggleConfirmPasswordVisibility}
                >
                  <FontAwesomeIcon icon={showConfirmPassword ? faEyeSlash : faEye} />
                </button>
              </div>
            </div>
          )}


          <button 
            type="submit" 
            className="auth-button"
            disabled={isLoading}
          >
            {isLoading ? 'Загрузка...' : (isLogin ? 'Войти' : 'Зарегистрироваться')}
          </button>
        </form>

        <div className="auth-footer">
          <p>
            {isLogin ? 'Нет аккаунта?' : 'Уже есть аккаунт?'}
            <button 
              type="button" 
              className="toggle-button"
              onClick={toggleMode}
            >
              {isLogin ? 'Зарегистрироваться' : 'Войти'}
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default AuthPage;
