import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft, 
  faPaperPlane, 
  faEdit, 
  faTrash, 
  faCheck, 
  faTimes,
  faUser,
  faMessage,
  faReply,
  faImage,
  faVideo,
  faFile,
  faMicrophone,
  faPaperclip
} from '@fortawesome/free-solid-svg-icons';
import { getChat, saveMessage, updateMessage, deleteMessage } from '../../utils/chatStorage';
import { getUserInfo } from '../../utils/userUtils';
import './ChatPage.css';

const ChatPage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const messagesEndRef = useRef(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [editingMessageId, setEditingMessageId] = useState(null);
  const [editText, setEditText] = useState('');
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isTyping, setIsTyping] = useState(false);
  const [typingTimeout, setTypingTimeout] = useState(null);
  const [contextMenu, setContextMenu] = useState(null);
  const [modalMessage, setModalMessage] = useState(null);
  const [swipeStartX, setSwipeStartX] = useState(null);
  const [swipeStartY, setSwipeStartY] = useState(null);
  const [replyToMessage, setReplyToMessage] = useState(null);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [showMediaOptions, setShowMediaOptions] = useState(false);
  const fileInputRef = useRef(null);

  // Проверяем userId сразу при загрузке

  if (!userId) {
    console.error('userId не найден в параметрах');
    return (
      <div className="chat-loading">
        <p>Ошибка: ID пользователя не найден</p>
        <button onClick={() => navigate(-1)}>Вернуться назад</button>
      </div>
    );
  }

  // Загружаем пользователя и сообщения
  useEffect(() => {
    const loadChat = async () => {
      try {
        // Загружаем существующие сообщения
        const existingMessages = getChat(userId);
        setMessages(existingMessages);

        // Получаем информацию о пользователе
        // Сначала проверяем localStorage на наличие реальных данных
        const userInfo = await getUserInfo(userId);

        if (userInfo && userInfo.name && userInfo.name !== `Пользователь ${userId}`) {
          setUser(userInfo);
        } else {
          const mockUserInfo = {
            id: userId,
            name: `Пользователь ${userId}`,
            avatar: '',
            city: 'Неизвестный город',
            lastSeen: new Date().toISOString()
          };
          setUser(mockUserInfo);
        }

        setIsLoading(false);
      } catch (error) {
        // Fallback на моковые данные
        const fallbackUser = {
          id: userId,
          name: `Пользователь ${userId}`,
          avatar: '',
          city: 'Неизвестный город',
          lastSeen: new Date().toISOString()
        };
        setUser(fallbackUser);
        setIsLoading(false);
      }
    };

    loadChat();
  }, [userId]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Очистка таймаута при размонтировании
  useEffect(() => {
    return () => {
      if (typingTimeout) {
        clearTimeout(typingTimeout);
      }
    };
  }, [typingTimeout]);

  const handleSendMessage = () => {
    if (newMessage.trim() || selectedFiles.length > 0) {
      const messageData = {
        text: newMessage.trim(),
        sender: 'user',
        files: selectedFiles.length > 0 ? selectedFiles : null
      };

      // Сохраняем сообщение в localStorage
      const savedMessage = saveMessage(userId, messageData);

      if (savedMessage) {
        // Добавляем класс для анимации нового сообщения
        const messageWithAnimation = { ...savedMessage, isNew: true };
        setMessages(prev => [...prev, messageWithAnimation]);
        setNewMessage('');
        setSelectedFiles([]);
        setShowMediaOptions(false);

        // Убираем анимацию через некоторое время
        setTimeout(() => {
          setMessages(prev => prev.map(msg => 
            msg.id === savedMessage.id ? { ...msg, isNew: false } : msg
          ));
        }, 500);
      }

      // Останавливаем индикатор печати
      setIsTyping(false);
      if (typingTimeout) {
        clearTimeout(typingTimeout);
        setTypingTimeout(null);
      }
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleInputChange = (e) => {
    setNewMessage(e.target.value);

    // Показываем индикатор печати
    if (!isTyping) {
      setIsTyping(true);
    }

    // Очищаем предыдущий таймаут
    if (typingTimeout) {
      clearTimeout(typingTimeout);
    }

    // Устанавливаем новый таймаут для скрытия индикатора печати
    const timeout = setTimeout(() => {
      setIsTyping(false);
    }, 1000);

    setTypingTimeout(timeout);
  };

  // Функции для работы с медиафайлами
  const handleFileSelect = (e) => {
    const files = Array.from(e.target.files);
    const validFiles = files.filter(file => {
      const maxSize = 10 * 1024 * 1024; // 10MB
      return file.size <= maxSize;
    });

    setSelectedFiles(prev => [...prev, ...validFiles]);
    setShowMediaOptions(false);
  };

  const removeFile = (index) => {
    setSelectedFiles(prev => prev.filter((_, i) => i !== index));
  };

  const getFileIcon = (file) => {
    if (file.type.startsWith('image/')) return faImage;
    if (file.type.startsWith('video/')) return faVideo;
    return faFile;
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const handleEditMessage = (messageId) => {
    const message = messages.find(msg => msg.id === messageId);
    if (message) {
      setEditingMessageId(messageId);
      setEditText(message.text);
    }
  };

  const handleSaveEdit = () => {
    if (editText.trim() && editingMessageId) {
      // Обновляем сообщение в localStorage
      const success = updateMessage(userId, editingMessageId, editText.trim());

      if (success) {
        setMessages(prev => prev.map(msg => 
          msg.id === editingMessageId 
            ? { ...msg, text: editText.trim(), isEdited: true, editedAt: new Date().toISOString() }
            : msg
        ));
        setEditingMessageId(null);
        setEditText('');
      }
    }
  };

  const handleCancelEdit = () => {
    setEditingMessageId(null);
    setEditText('');
  };

  const handleDeleteMessage = (messageId) => {
    if (window.confirm('Вы уверены, что хотите удалить это сообщение?')) {
      // Удаляем сообщение из localStorage
      const success = deleteMessage(userId, messageId);

      if (success) {
        setMessages(prev => prev.filter(msg => msg.id !== messageId));
      }
    }
  };

  // Функции для контекстного меню
  const handleMessageContextMenu = (e, messageId) => {
    e.preventDefault();
    e.stopPropagation();

    const message = messages.find(msg => msg.id === messageId);
    if (message && message.sender === 'user') {
      const rect = e.currentTarget.getBoundingClientRect();
      setContextMenu({
        messageId,
        x: e.clientX,
        y: e.clientY,
        messageRect: rect
      });
    }
  };

  const handleContextMenuAction = (action, messageId) => {
    setContextMenu(null);

    if (action === 'edit') {
      handleEditMessage(messageId);
    } else if (action === 'delete') {
      handleDeleteMessage(messageId);
    }
  };

  const closeContextMenu = () => {
    setContextMenu(null);
  };

  // Функции для модального окна
  const handleMessageClick = (messageId) => {
    const message = messages.find(msg => msg.id === messageId);
    if (message && message.sender === 'user') {
      setModalMessage(message);
    }
  };

  const closeModal = () => {
    setModalMessage(null);
  };

  const handleModalEdit = () => {
    if (modalMessage) {
      setEditingMessageId(modalMessage.id);
      setEditText(modalMessage.text);
      setModalMessage(null);
    }
  };

  const handleModalDelete = () => {
    if (modalMessage) {
      handleDeleteMessage(modalMessage.id);
      setModalMessage(null);
    }
  };

  const handleModalReply = () => {
    if (modalMessage) {
      setReplyToMessage(modalMessage);
      setModalMessage(null);
    }
  };

  // Функции для свайпа
  const handleTouchStart = (e, messageId) => {
    const message = messages.find(msg => msg.id === messageId);
    if (message && message.sender !== 'user') {
      setSwipeStartX(e.touches[0].clientX);
      setSwipeStartY(e.touches[0].clientY);
    }
  };

  const handleTouchEnd = (e, messageId) => {
    if (swipeStartX === null) return;

    const swipeEndX = e.changedTouches[0].clientX;
    const swipeEndY = e.changedTouches[0].clientY;
    const deltaX = swipeStartX - swipeEndX;
    const deltaY = Math.abs(swipeStartY - swipeEndY);

    // Проверяем, что это свайп влево (deltaX > 50) и не вертикальный (deltaY < 100)
    if (deltaX > 50 && deltaY < 100) {
      const message = messages.find(msg => msg.id === messageId);
      if (message) {
        setReplyToMessage(message);
      }
    }

    setSwipeStartX(null);
    setSwipeStartY(null);
  };

  // Закрытие контекстного меню при клике вне его
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (contextMenu && !e.target.closest('.context-menu')) {
        closeContextMenu();
      }
    };

    if (contextMenu) {
      document.addEventListener('click', handleClickOutside);
      document.addEventListener('contextmenu', handleClickOutside);
    }

    return () => {
      document.removeEventListener('click', handleClickOutside);
      document.removeEventListener('contextmenu', handleClickOutside);
    };
  }, [contextMenu]);

  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString('ru-RU', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const formatDate = (timestamp) => {
    const date = new Date(timestamp);
    const today = new Date();
    const yesterday = new Date(today);
    yesterday.setDate(yesterday.getDate() - 1);

    if (date.toDateString() === today.toDateString()) {
      return 'Сегодня';
    } else if (date.toDateString() === yesterday.toDateString()) {
      return 'Вчера';
    } else {
      return date.toLocaleDateString('ru-RU');
    }
  };

  if (isLoading) {
    return (
      <div className="chat-loading">
        <div className="loading-spinner"></div>
        <p>Загрузка чата...</p>
      </div>
    );
  }

  // Проверяем, что у нас есть информация о пользователе
  if (!user) {
    // Fallback на моковые данные прямо в рендере
    const fallbackUser = {
      id: userId,
      name: `Пользователь ${userId}`,
      avatar: '',
      city: 'Неизвестный город',
      lastSeen: new Date().toISOString()
    };
    return (
      <div className="chat-loading">
        <div className="loading-spinner"></div>
        <p>Загрузка информации о пользователе...</p>
        <button 
          onClick={() => setUser(fallbackUser)}
          style={{ 
            marginTop: '20px', 
            padding: '10px 20px', 
            background: '#8b5cf6', 
            color: 'white', 
            border: 'none', 
            borderRadius: '8px',
            cursor: 'pointer'
          }}
        >
          Принудительно загрузить данные
        </button>
      </div>
    );
  }

  return (
    <div className="chat-page">
      {/* Заголовок чата */}
      <div className="chat-header">
        <button className="back-button" onClick={() => navigate(-1)}>
          <FontAwesomeIcon icon={faArrowLeft} />
        </button>

        <div className="chat-user-info">
          <div className="user-avatar">
            {user?.avatar ? (
              <img src={user.avatar} alt={user.name} />
            ) : (
              <div className="avatar-placeholder">
                <FontAwesomeIcon icon={faUser} />
              </div>
            )}
          </div>

          <div className="user-details">
            <h3 className="user-name">
              {user?.name || 'Загрузка...'}
            </h3>
            {user?.city && (
              <span className="user-city">
                📍 {user.city}
              </span>
            )}
          </div>
        </div>

        {/* Убираем статус онлайн */}
      </div>

      {/* Контейнер сообщений */}
      <div className="messages-container">
        {messages.length === 0 ? (
          <div className="empty-chat">
            <div className="empty-chat-icon">
              <FontAwesomeIcon icon={faMessage} />
            </div>
            <h3>💬 Чат пуст</h3>
            <p>Начните разговор, отправив первое сообщение</p>
            <div className="empty-chat-features">
              <span>✨ Отправляйте сообщения</span>
              <span>✏️ Редактируйте их</span>
              <span>🗑️ Удаляйте при необходимости</span>
            </div>
          </div>
        ) : (
          <div className="messages-list">
            {messages.map((message) => {
              const messageClass = message.sender === 'user' ? 'my-message' : 'incoming-message';
              return (
              <div 
                key={message.id} 
                className={`message ${messageClass} ${message.isNew ? 'new-message' : ''}`}
                onContextMenu={(e) => handleMessageContextMenu(e, message.id)}
                onClick={() => handleMessageClick(message.id)}
                onTouchStart={(e) => handleTouchStart(e, message.id)}
                onTouchEnd={(e) => handleTouchEnd(e, message.id)}
              >
                {editingMessageId === message.id ? (
                  <div className="edit-message-form">
                    <textarea
                      className="edit-textarea"
                      value={editText}
                      onChange={(e) => setEditText(e.target.value)}
                      placeholder="Редактировать сообщение..."
                      autoFocus
                    />
                    <div className="edit-actions">
                      <button 
                        className="edit-btn save-btn" 
                        onClick={handleSaveEdit}
                      >
                        <FontAwesomeIcon icon={faCheck} />
                      </button>
                      <button 
                        className="edit-btn cancel-btn" 
                        onClick={handleCancelEdit}
                      >
                        <FontAwesomeIcon icon={faTimes} />
                      </button>
                    </div>
                  </div>
                ) : (
                  <>
                    <div className="message-content">
                      {message.files && message.files.length > 0 && (
                        <div className="message-files">
                          {message.files.map((file, index) => (
                            <div key={index} className="message-file">
                              <FontAwesomeIcon icon={getFileIcon(file)} className="file-icon" />
                              <span className="file-name">{file.name}</span>
                              <span className="file-size">({formatFileSize(file.size)})</span>
                            </div>
                          ))}
                        </div>
                      )}
                      {message.text && (
                        <p className="message-text">{message.text}</p>
                      )}
                      {message.isEdited && (
                        <span className="edited-indicator">(изменено)</span>
                      )}
                      <div className="message-meta">
                        <span className="message-time">{formatTime(message.timestamp)}</span>
                      </div>
                    </div>
                  </>
                )}
              </div>
              );
            })}
            <div ref={messagesEndRef} />
          </div>
        )}

        {/* Индикатор печати */}
        {isTyping && (
          <div className="typing-indicator">
            <div className="typing-dot"></div>
            <div className="typing-dot"></div>
            <div className="typing-dot"></div>
          </div>
        )}
      </div>

      {/* Форма ввода сообщения */}
      <div className="message-input-container">
        <div className="message-input-wrapper">
          <textarea
            className="message-input"
            value={newMessage}
            onChange={handleInputChange}
            onKeyPress={handleKeyPress}
            placeholder="Сообщение"
            rows="1"
          />
          <button 
            className="send-button" 
            onClick={handleSendMessage}
            disabled={!newMessage.trim()}
          >
            <FontAwesomeIcon icon={faPaperPlane} />
          </button>
        </div>
      </div>

      {/* Контекстное меню */}
      {contextMenu && (
        <>
          <div className="context-menu-overlay" onClick={closeContextMenu} />
          <div 
            className="context-menu"
            style={{
              left: contextMenu.x,
              top: contextMenu.y
            }}
          >
            <button 
              className="context-menu-item edit-item"
              onClick={() => handleContextMenuAction('edit', contextMenu.messageId)}
            >
              <FontAwesomeIcon icon={faEdit} className="icon" />
              Редактировать
            </button>
            <button 
              className="context-menu-item delete-item"
              onClick={() => handleContextMenuAction('delete', contextMenu.messageId)}
            >
              <FontAwesomeIcon icon={faTrash} className="icon" />
              Удалить
            </button>
          </div>
        </>
      )}

      {/* Модальное окно для действий с сообщением */}
      {modalMessage && (
        <div className="message-modal-overlay" onClick={closeModal}>
          <div className="message-modal" onClick={(e) => e.stopPropagation()}>
            <div className="message-modal-header">
              <h3>Действия с сообщением</h3>
              <button className="modal-close-btn" onClick={closeModal}>
                <FontAwesomeIcon icon={faTimes} />
              </button>
            </div>
            <div className="message-modal-content">
              <div className="message-preview">
                <p>"{modalMessage.text}"</p>
                <span className="message-time">{formatTime(modalMessage.timestamp)}</span>
              </div>
            </div>
            <div className="message-modal-actions">
              <button className="modal-action-btn edit-btn" onClick={handleModalEdit}>
                <FontAwesomeIcon icon={faEdit} />
                Редактировать
              </button>
              <button className="modal-action-btn reply-btn" onClick={handleModalReply}>
                <FontAwesomeIcon icon={faReply} />
                Ответить
              </button>
              <button className="modal-action-btn delete-btn" onClick={handleModalDelete}>
                <FontAwesomeIcon icon={faTrash} />
                Удалить
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Индикатор ответа на сообщение */}
      {replyToMessage && (
        <div className="reply-indicator">
          <div className="reply-info">
            <span className="reply-label">Ответ на:</span>
            <span className="reply-text">"{replyToMessage.text}"</span>
          </div>
          <button className="reply-cancel-btn" onClick={() => setReplyToMessage(null)}>
            <FontAwesomeIcon icon={faTimes} />
          </button>
        </div>
      )}
    </div>
  );
};

export default ChatPage;
