import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft, 
  faSearch, 
  faUser,
  faMessage,
  faPlus
} from '@fortawesome/free-solid-svg-icons';
import { apiClient } from '../../utils/api';
import ChatModal from '../ChatModal/ChatModal';
import './ChatsPage.css';

const ChatsPage = () => {
  const navigate = useNavigate();
  const [chats, setChats] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    fetchChats();
  }, []);

  const fetchChats = async () => {
    try {
      setLoading(true);

      // Получаем чаты из API
      const response = await apiClient.request('/users/chats/');

      if (response.success) {
        setChats(response.chats);
      } else {
        console.error('Ошибка загрузки чатов:', response.message);
        setChats([]);
      }
    } catch (error) {
      console.error('Ошибка загрузки чатов:', error);
      setChats([]);
    } finally {
      setLoading(false);
    }
  };

  const handleChatClick = (chat) => {
    // Переходим к чату с userId другого участника
    const otherUserId = chat.other_participant?.id;
    if (otherUserId) {
      navigate(`/chat/${otherUserId}`);
    }
  };

  const formatTime = (timestamp) => {
    try {
      const date = new Date(timestamp);
      const now = new Date();
      const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));

      if (diffInHours < 1) {
        const diffInMinutes = Math.floor((now - date) / (1000 * 60));
        if (diffInMinutes < 1) return 'Только что';
        return `${diffInMinutes} мин назад`;
      }
      if (diffInHours < 24) return `${diffInHours} ч назад`;

      return date.toLocaleDateString('ru-RU', { 
        day: 'numeric', 
        month: 'short' 
      });
    } catch (error) {
      return 'Недавно';
    }
  };

  // Фильтрация чатов
  const filteredChats = chats.filter(chat => {
    const otherUser = chat.other_participant;
    const userName = otherUser ? `${otherUser.first_name} ${otherUser.last_name}`.trim() : '';
    const lastMessage = chat.last_message?.content || '';

    return userName.toLowerCase().includes(searchQuery.toLowerCase()) ||
           lastMessage.toLowerCase().includes(searchQuery.toLowerCase());
  });

  if (loading) {
    return (
      <div className="chats-page">
        <div className="chats-header">
          <button className="btn-back" onClick={() => navigate(-1)}>
            <FontAwesomeIcon icon={faArrowLeft} />
            <span>Назад</span>
          </button>
          <h1>Мои чаты</h1>
        </div>
        <div className="chats-loading">
          <div className="loading-spinner"></div>
          <p>Загрузка чатов...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="chats-page">
      {/* Заголовок */}
      <div className="chats-header">
        <button className="btn-back" onClick={() => navigate(-1)}>
          <FontAwesomeIcon icon={faArrowLeft} />
          <span>Назад</span>
        </button>
        <h1>Мои чаты</h1>
        <button className="new-chat-btn" onClick={() => navigate('/friends')}>
          <FontAwesomeIcon icon={faPlus} />
          <span>Новый чат</span>
        </button>
      </div>

      {/* Поиск */}
      <div className="search-container">
        <div className="search-wrapper">
          <FontAwesomeIcon icon={faSearch} className="search-icon" />
          <input
            type="text"
            placeholder="Поиск по чатам..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="search-input"
          />
          {searchQuery && (
            <button 
              className="clear-search" 
              onClick={() => setSearchQuery('')}
            >
              ×
            </button>
          )}
        </div>
      </div>

      {/* Список чатов */}
      <div className="chats-container">
        {filteredChats.length === 0 ? (
          <div className="empty-chats">
            {searchQuery ? (
              <>
                <div className="empty-chats-icon">
                  <FontAwesomeIcon icon={faSearch} />
                </div>
                <h3>Чаты не найдены</h3>
                <p>Попробуйте изменить поисковый запрос</p>
              </>
            ) : (
              <>
                <div className="empty-chats-icon">
                  <FontAwesomeIcon icon={faMessage} />
                </div>
                <h3>У вас пока нет чатов</h3>
                <p>Начните общение с другими пользователями</p>
              </>
            )}
          </div>
        ) : (
          <div className="chats-list">
            {filteredChats.map((chat) => {
              const otherUser = chat.other_participant;
              const userName = otherUser ? `${otherUser.first_name} ${otherUser.last_name}`.trim() : 'Пользователь';
              const lastMessage = chat.last_message?.content || 'Нет сообщений';
              const lastMessageTime = chat.last_message?.created_at || chat.updated_at;

              return (
                <div 
                  key={chat.id} 
                  className="chat-item"
                  onClick={() => handleChatClick(chat)}
                >
                  <div className="chat-avatar">
                    {otherUser?.avatar ? (
                      <img src={otherUser.avatar} alt={userName} />
                    ) : (
                      <div className="avatar-placeholder">
                        <FontAwesomeIcon icon={faUser} />
                      </div>
                    )}
                  </div>

                  <div className="chat-info">
                    <div className="chat-header-info">
                      <h3 className="chat-user-name">{userName}</h3>
                      <span className="chat-time">
                        {formatTime(lastMessageTime)}
                      </span>
                    </div>

                    <div className="chat-message-preview">
                      <p className="last-message">{lastMessage}</p>
                      {chat.unread_count > 0 && (
                        <span className="unread-count">{chat.unread_count}</span>
                      )}
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
};

export default ChatsPage;
