// Утилиты для работы с авторизацией

// Получить токен из хранилища
export const getToken = () => {
  return localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
};

// Получить refresh токен из хранилища
export const getRefreshToken = () => {
  return localStorage.getItem('refreshToken') || sessionStorage.getItem('refreshToken');
};

// Сохранить токены
export const saveTokens = (accessToken, refreshToken, rememberMe = false) => {
  if (rememberMe) {
    localStorage.setItem('accessToken', accessToken);
    localStorage.setItem('refreshToken', refreshToken);
  } else {
    sessionStorage.setItem('accessToken', accessToken);
    sessionStorage.setItem('refreshToken', refreshToken);
  }
};

// Удалить токены
export const removeTokens = () => {
  localStorage.removeItem('accessToken');
  localStorage.removeItem('refreshToken');
  localStorage.removeItem('userData');
  sessionStorage.removeItem('accessToken');
  sessionStorage.removeItem('refreshToken');
  sessionStorage.removeItem('userData');
};

// Обновить токен
export const refreshAccessToken = async () => {
  const refreshToken = getRefreshToken();

  if (!refreshToken) {
    throw new Error('Нет refresh токена');
  }

  try {
    const response = await fetch('http://localhost:8000/api/auth/token/refresh/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        refresh: refreshToken
      })
    });

    if (response.ok) {
      const data = await response.json();

      // Сохраняем новый токен в том же хранилище, где был старый
      const storage = localStorage.getItem('accessToken') ? localStorage : sessionStorage;
      storage.setItem('accessToken', data.access);

      return data.access;
    } else {
      throw new Error('Не удалось обновить токен');
    }
  } catch (error) {
    console.error('Ошибка обновления токена:', error);
    removeTokens();
    throw error;
  }
};

// Проверить, авторизован ли пользователь
export const isAuthenticated = () => {
  return !!getToken();
};

// Получить данные пользователя
export const getUserData = () => {
  const userData = localStorage.getItem('userData') || sessionStorage.getItem('userData');
  return userData ? JSON.parse(userData) : null;
};

// Автоматическая проверка токена при загрузке
export const checkAuthOnLoad = async () => {
  const token = getToken();

  if (!token) {
    return false;
  }

  try {
    const response = await fetch('http://localhost:8000/api/auth/me/', {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      }
    });

    if (response.ok) {
      return true;
    } else if (response.status === 401) {
      // Токен истек, пробуем обновить
      try {
        await refreshAccessToken();
        return true;
      } catch (error) {
        removeTokens();
        return false;
      }
    } else {
      return false;
    }
  } catch (error) {
    console.error('Ошибка проверки авторизации:', error);
    return false;
  }
};
