import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faMessage, faPlus } from '@fortawesome/free-solid-svg-icons';
import { apiClient } from '../../utils/api';
import './FriendsPage.css';

const FriendsPage = () => {
  const navigate = useNavigate();
  const [users, setUsers] = useState([]);
  const [chats, setChats] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setIsLoading(true);
      
      // –ü–æ–ª—É—á–∞–µ–º —Ç–µ–∫—É—â–µ–≥–æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
      const user = await apiClient.getCurrentUser();
      setCurrentUser(user);
      
      // –ü–æ–ª—É—á–∞–µ–º —Å–ø–∏—Å–æ–∫ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π
      const usersData = await apiClient.getUsers();
      setUsers(usersData);
      
      // –ü–æ–ª—É—á–∞–µ–º —á–∞—Ç—ã
      const chatsData = await apiClient.getChats();
      setChats(chatsData);
      
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const startChat = async (userId) => {
    try {
      // –°–æ–∑–¥–∞–µ–º –∏–ª–∏ –ø–æ–ª—É—á–∞–µ–º —á–∞—Ç
      const chat = await apiClient.createChat(userId);
      
      // –ü–µ—Ä–µ—Ö–æ–¥–∏–º –∫ —á–∞—Ç—É
      navigate(`/chat/${chat.id}`);
      
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ —Å–æ–∑–¥–∞–Ω–∏—è —á–∞—Ç–∞:', error);
    }
  };

  const getChatUser = (chat) => {
    return chat.user1_id === currentUser.id ? chat.user2_id : chat.user1_id;
  };

  const getUserById = (userId) => {
    return users.find(user => user.id === userId);
  };

  if (isLoading) {
    return (
      <div className="friends-page">
        <div className="loading">–ó–∞–≥—Ä—É–∑–∫–∞...</div>
      </div>
    );
  }

  return (
    <div className="friends-page">
      <div className="friends-header">
        <h1>–ß–∞—Ç—ã</h1>
      </div>

      <div className="friends-content">
        {/* –°—É—â–µ—Å—Ç–≤—É—é—â–∏–µ —á–∞—Ç—ã */}
        {chats.length > 0 && (
          <div className="chats-section">
            <h2>–ê–∫—Ç–∏–≤–Ω—ã–µ —á–∞—Ç—ã</h2>
            <div className="friends-list">
              {chats.map((chat) => {
                const otherUserId = getChatUser(chat);
                const otherUser = getUserById(otherUserId);
                
                if (!otherUser) return null;
                
                return (
                  <div
                    key={chat.id}
                    className="friend-item chat-item"
                    onClick={() => navigate(`/chat/${chat.id}`)}
                  >
                    <div className="friend-avatar">
                      <img 
                        src={otherUser.avatar || '/default-avatar.png'} 
                        alt={otherUser.name} 
                      />
                    </div>
                    <div className="friend-info">
                      <h3 className="friend-name">{otherUser.name}</h3>
                      <p className="friend-city">üìç {otherUser.city || '–ì–æ—Ä–æ–¥ –Ω–µ —É–∫–∞–∑–∞–Ω'}</p>
                    </div>
                    <div className="friend-actions">
                      <button 
                        className="message-button"
                        onClick={(e) => {
                          e.stopPropagation();
                          navigate(`/chat/${chat.id}`);
                        }}
                        title="–û—Ç–∫—Ä—ã—Ç—å —á–∞—Ç"
                      >
                        <FontAwesomeIcon icon={faMessage} />
                      </button>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        )}

        {/* –í—Å–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏ */}
        <div className="users-section">
          <h2>–í—Å–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏</h2>
          <div className="friends-list">
            {users.map((user) => (
              <div key={user.id} className="friend-item">
                <div className="friend-avatar">
                  <img 
                    src={user.avatar || '/default-avatar.png'} 
                    alt={user.name} 
                  />
                </div>
                <div className="friend-info">
                  <h3 className="friend-name">{user.name}</h3>
                  <p className="friend-city">üìç {user.city || '–ì–æ—Ä–æ–¥ –Ω–µ —É–∫–∞–∑–∞–Ω'}</p>
                </div>
                <div className="friend-actions">
                  <button 
                    className="message-button"
                    onClick={() => startChat(user.id)}
                    title="–ù–∞—á–∞—Ç—å —á–∞—Ç"
                  >
                    <FontAwesomeIcon icon={faMessage} />
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default FriendsPage;
