import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEye, faHeart, faComment, faCalendar, faUser, faPen } from '@fortawesome/free-solid-svg-icons';
import './PostsList.css';

const PostsList = ({ category = null, limit = null }) => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchPosts();
  }, [category]);

  const fetchPosts = async () => {
    try {
      setLoading(true);
      let url = 'http://localhost:8000/api/posts/';
      
      if (category) {
        url += `?category=${category}`;
      }
      
      if (limit) {
        url += `${category ? '&' : '?'}page_size=${limit}`;
      }
      
      const response = await fetch(url);
      if (response.ok) {
        const data = await response.json();
        setPosts(data.results || data);
      } else {
        setError('Ошибка загрузки постов');
      }
    } catch (error) {
      console.error('Ошибка загрузки постов:', error);
      setError('Ошибка загрузки постов');
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('ru-RU', {
      day: 'numeric',
      month: 'long',
      year: 'numeric'
    });
  };

  if (loading) {
    return (
      <div className="posts-loading">
        <div className="loading-spinner"></div>
        <p>Загрузка постов...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="posts-error">
        <p>{error}</p>
        <button onClick={fetchPosts} className="btn-retry">
          Попробовать снова
        </button>
      </div>
    );
  }

  if (posts.length === 0) {
    return (
      <div className="posts-empty">
        <div className="empty-icon">
          <FontAwesomeIcon icon={faEye} />
        </div>
        <h3>Постов пока нет</h3>
        <p>Будьте первым, кто поделится своими мыслями и опытом!</p>
        <Link to="/create-post" className="btn-create-post">
          <FontAwesomeIcon icon={faPen} />
          Написать первый пост
        </Link>
      </div>
    );
  }

  return (
    <div className="posts-list">
      {posts.map(post => (
        <article key={post.id} className="post-card">
          <div className="post-image">
            {post.featured_image ? (
              <img src={post.featured_image} alt={post.title} />
            ) : (
              <div className="post-image-placeholder">
                <FontAwesomeIcon icon={faEye} />
              </div>
            )}
            <div className="post-category">
              {post.category?.name}
            </div>
          </div>
          
          <div className="post-content">
            <div className="post-meta">
              <span className="post-author">
                <FontAwesomeIcon icon={faUser} />
                {post.author?.first_name || post.author?.username || 'Автор'}
              </span>
              <span className="post-date">
                <FontAwesomeIcon icon={faCalendar} />
                {formatDate(post.published_at || post.created_at)}
              </span>
            </div>
            
            <h3 className="post-title">
              <Link to={`/post/${post.slug}`}>
                {post.title}
              </Link>
            </h3>
            
            <p className="post-excerpt">
              {post.content.length > 150 
                ? `${post.content.substring(0, 150)}...` 
                : post.content
              }
            </p>
            
            <div className="post-tags">
              <span className="post-tag">{post.short_description}</span>
            </div>
            
            <div className="post-stats">
              <span className="post-stat">
                <FontAwesomeIcon icon={faEye} />
                {post.views_count}
              </span>
              <span className="post-stat">
                <FontAwesomeIcon icon={faHeart} />
                {post.likes_count}
              </span>
              <span className="post-stat">
                <FontAwesomeIcon icon={faComment} />
                {post.comments_count}
              </span>
            </div>
          </div>
        </article>
      ))}
    </div>
  );
};

export default PostsList;
