import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft, faPaperPlane } from '@fortawesome/free-solid-svg-icons';
import './SimpleChat.css';

const SimpleChat = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (userId) {
      loadUserData();
      loadChatMessages();
    }
  }, [userId]);

  const loadUserData = async () => {
    try {
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      const response = await fetch(`http://localhost:8000/users/${userId}/`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success && data.user) {
          setUser(data.user);
        }
      }
    } catch (error) {
      console.error('Ошибка загрузки данных пользователя:', error);
    }
  };

  const loadChatMessages = async () => {
    try {
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      const response = await fetch(`http://localhost:8000/users/async/chats/${userId}/messages/`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success && data.messages) {
          setMessages(data.messages);
        }
      }
    } catch (error) {
      console.error('Ошибка загрузки сообщений:', error);
    }
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;

    setLoading(true);
    try {
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      const response = await fetch(`http://localhost:8000/users/async/chats/${userId}/messages/create/`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          content: newMessage,
          message_type: 'text'
        }),
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success && data.message) {
          setMessages(prev => [...prev, data.message]);
          setNewMessage('');
        }
      }
    } catch (error) {
      console.error('Ошибка отправки сообщения:', error);
    } finally {
      setLoading(false);
    }
  };

  const displayName = user ? 
    (user.first_name && user.last_name ? `${user.first_name} ${user.last_name}` : 
     user.first_name || user.username || 'Пользователь') : 
    'Пользователь';

  return (
    <div className="simple-chat">
      <div className="chat-header">
        <button className="back-button" onClick={() => navigate(-1)}>
          <FontAwesomeIcon icon={faArrowLeft} />
          <span>Переписки</span>
        </button>
        <div className="chat-user-info">
          <h3 className="user-name">{displayName}</h3>
        </div>
      </div>

      <div className="messages-container">
        {messages.length === 0 ? (
          <div className="empty-messages">
            <p className="empty-title">Сообщений нет.</p>
            <p className="empty-subtitle">Собеседник не видит эту переписку.</p>
          </div>
        ) : (
          <div className="messages-list">
            {messages.map((message) => {
              // Получаем ID пользователя из userData
              const userDataString = localStorage.getItem('userData') || sessionStorage.getItem('userData');
              let currentUserId = null;
              if (userDataString) {
                try {
                  const userData = JSON.parse(userDataString);
                  currentUserId = parseInt(userData.id);
                } catch (error) {
                  console.error('Ошибка парсинга userData:', error);
                }
              }

              const messageSenderId = parseInt(message.sender);
              const isMyMessage = messageSenderId === currentUserId;

              console.log('Message debug:', {
                messageSender: message.sender,
                messageSenderId: messageSenderId,
                currentUserId: currentUserId,
                isMyMessage: isMyMessage,
                messageContent: message.content,
                localStorage_user_id: localStorage.getItem('user_id'),
                sessionStorage_user_id: sessionStorage.getItem('user_id')
              });

              return (
                <div key={message.id} className={`message ${isMyMessage ? 'my-message' : 'incoming-message'}`}>
                  <div className="message-content" style={isMyMessage ? {backgroundColor: '#007bff', color: 'white'} : {backgroundColor: '#ffffff', color: 'black'}}>
                    <p>{message.content}</p>
                    <span className="message-time">
                      {new Date(message.created_at).toLocaleTimeString('ru-RU', {
                        hour: '2-digit',
                        minute: '2-digit'
                      })}
                    </span>
                    {/* Отладочная информация */}
                    <div style={{fontSize: '10px', opacity: 0.7, marginTop: '4px'}}>
                      {isMyMessage ? 'МОЕ' : 'НЕ МОЕ'} (Sender: {message.sender}, MyID: {currentUserId})
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>

      <div className="message-input-container">
        <div className="message-input-wrapper">
          <input
            type="text"
            className="message-input"
            placeholder="Ваше сообщение"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSendMessage(e)}
          />
          <button 
            className="send-button" 
            onClick={handleSendMessage}
            disabled={!newMessage.trim() || loading}
          >
            <FontAwesomeIcon icon={faPaperPlane} />
            <span>Отправить</span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default SimpleChat;
