import React from 'react';
import { Link } from 'react-router-dom';
import './FeaturedCards.css';

const FeaturedCards = () => {
  const featuredCards = [
    {
      id: 1,
      title: 'Слушаем и растём: аудиоконтент для малышей и школьников',
      color: 'purple',
      illustration: 'audio-content'
    },
    {
      id: 2,
      title: 'Секреты спокойного сна: как уложить малыша без слез',
      color: 'pink',
      illustration: 'sleeping-baby'
    },
    {
      id: 3,
      title: 'Первый год жизни: важные этапы развития ребенка',
      color: 'blue',
      illustration: 'baby-development'
    }
  ];

  return (
    <div className="featured-content">
      {featuredCards.map(card => (
        <Link key={card.id} to={`/post/${card.id}`} className="featured-card-link">
          <div className={`featured-card ${card.color}`}>
            <div className="card-content">
              <h3>{card.title}</h3>
            </div>
            <div className="card-image">
              <div className={`illustration ${card.illustration}`}></div>
            </div>
          </div>
        </Link>
      ))}
    </div>
  );
};

export default FeaturedCards;
