import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft, 
  faSearch, 
  faUser,
  faCircle,
  faUserFriends
} from '@fortawesome/free-solid-svg-icons';
import './ChatsPage.css';

const ChatsPage = () => {
  const navigate = useNavigate();
  const [chats, setChats] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredChats, setFilteredChats] = useState([]);

  useEffect(() => {
    fetchChats();
  }, []);

  useEffect(() => {
    // Фильтрация чатов по поисковому запросу
    const filtered = chats.filter(chat => 
      chat.user?.first_name?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      chat.user?.last_name?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      chat.user?.username?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      chat.lastMessage?.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setFilteredChats(filtered);
  }, [searchQuery, chats]);

  const fetchChats = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      
      if (!token) {
        navigate('/auth');
        return;
      }

      const response = await fetch('http://localhost:8000/api/users/chats/', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        }
      });

      if (response.ok) {
        const data = await response.json();
        setChats(data.chats || []);
      } else {
        console.error('Ошибка загрузки чатов:', response.status);
        // Показываем тестовые данные для демонстрации
        setChats(getMockChats());
      }
    } catch (error) {
      console.error('Ошибка загрузки чатов:', error);
      // Показываем тестовые данные для демонстрации
      setChats(getMockChats());
    } finally {
      setLoading(false);
    }
  };

  const getMockChats = () => {
    return [
      {
        id: 1,
        user: {
          id: 2,
          first_name: 'Анна',
          last_name: 'Петрова',
          username: 'anna_p',
          avatar: null
        },
        lastMessage: 'Привет! Как дела?',
        lastMessageTime: '2024-01-15T10:30:00Z',
        unreadCount: 2,
        isOnline: true
      },
      {
        id: 2,
        user: {
          id: 3,
          first_name: 'Мария',
          last_name: 'Иванова',
          username: 'maria_i',
          avatar: null
        },
        lastMessage: 'Спасибо за совет!',
        lastMessageTime: '2024-01-15T09:15:00Z',
        unreadCount: 0,
        isOnline: false
      },
      {
        id: 3,
        user: {
          id: 4,
          first_name: 'Елена',
          last_name: 'Сидорова',
          username: 'elena_s',
          avatar: null
        },
        lastMessage: 'Когда встретимся?',
        lastMessageTime: '2024-01-14T18:45:00Z',
        unreadCount: 1,
        isOnline: true
      }
    ];
  };

  const handleChatClick = (chatId) => {
    navigate(`/chat/${chatId}`);
  };

  const getAuthorInitials = (user) => {
    if (!user) return 'А';
    const firstName = user.first_name || '';
    const lastName = user.last_name || '';
    return `${firstName.charAt(0)}${lastName.charAt(0)}`.toUpperCase() || 'А';
  };

  const getUserAvatarUrl = (user) => {
    if (user.avatar) return user.avatar;
    const initials = getAuthorInitials(user);
    return `https://via.placeholder.com/50x50/667eea/ffffff?text=${encodeURIComponent(initials)}`;
  };

  const formatTime = (dateString) => {
    if (!dateString) return '';
    
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));
    
    if (diffInHours < 1) return 'Только что';
    if (diffInHours < 24) return `${diffInHours} ч назад`;
    
    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays < 7) return `${diffInDays} дн назад`;
    
    return date.toLocaleDateString('ru-RU');
  };

  const getUserName = (user) => {
    if (!user) return 'Пользователь';
    const firstName = user.first_name || '';
    const lastName = user.last_name || '';
    const fullName = `${firstName} ${lastName}`.trim();
    return fullName || user.username || 'Пользователь';
  };

  if (loading) {
    return (
      <div className="chats-page">
        <div className="chats-header">
          <button className="btn-back" onClick={() => navigate('/')}>
            <FontAwesomeIcon icon={faArrowLeft} />
            Назад
          </button>
          <h1>Чаты</h1>
        </div>
        <div className="loading-state">
          <div className="loading-spinner"></div>
          <p>Загрузка чатов...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="chats-page">
      <div className="chats-header">
        <button className="btn-back" onClick={() => navigate('/')}>
          <FontAwesomeIcon icon={faArrowLeft} />
          Назад
        </button>
        <h1>Чаты</h1>
      </div>

      <div className="chats-search">
        <div className="search-container">
          <FontAwesomeIcon icon={faSearch} className="search-icon" />
          <input
            type="text"
            placeholder="Поиск чатов..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="search-input"
          />
        </div>
      </div>

      <div className="chats-list">
        {filteredChats.length > 0 ? (
          filteredChats.map(chat => (
            <div 
              key={chat.id} 
              className="chat-item"
              onClick={() => handleChatClick(chat.id)}
            >
              <div className="chat-avatar">
                <img 
                  src={getUserAvatarUrl(chat.user)} 
                  alt={getUserName(chat.user)}
                  onError={(e) => {
                    e.target.style.display = 'none';
                    e.target.nextSibling.style.display = 'flex';
                  }}
                />
                <div className="avatar-placeholder">
                  <FontAwesomeIcon icon={faUser} />
                </div>
                {chat.isOnline && (
                  <div className="online-indicator">
                    <FontAwesomeIcon icon={faCircle} />
                  </div>
                )}
              </div>
              
              <div className="chat-info">
                <div className="chat-header">
                  <h3 className="chat-name">{getUserName(chat.user)}</h3>
                  <span className="chat-time">{formatTime(chat.lastMessageTime)}</span>
                </div>
                
                <div className="chat-message">
                  <p className="message-text">{chat.lastMessage}</p>
                  {chat.unreadCount > 0 && (
                    <div className="unread-badge">
                      {chat.unreadCount}
                    </div>
                  )}
                </div>
              </div>
            </div>
          ))
        ) : (
          <div className="empty-state">
            <div className="empty-icon">
              <FontAwesomeIcon icon={faUserFriends} />
            </div>
            <h3>Чатов пока нет</h3>
            <p>Начните общение с другими пользователями</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default ChatsPage;
