import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faSearch,
  faPen,
  faUser,
  faFilter,
  faBaby,
  faMapMarkerAlt,
  faEdit,
  faCog,
  faComment,
  faEye
} from '@fortawesome/free-solid-svg-icons';
import profileData from '../../data/profileData.json';
import './ProfilePage.css';

const ProfilePage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const [profile, setProfile] = useState(profileData.profile);
  const [activeTab, setActiveTab] = useState('posts');
  const [selectedCategory, setSelectedCategory] = useState('Все категории');
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    // Получаем данные пользователя из localStorage (если есть)
    const userData = localStorage.getItem('userData');
    if (userData) {
      const user = JSON.parse(userData);
      setProfile(prev => ({
        ...prev,
        username: user.firstName || prev.username,
        fullName: `${user.firstName || ''} ${user.lastName || ''}`.trim(),
        avatar: user.firstName ? `${user.firstName[0]}${user.lastName ? user.lastName[0] : ''}` : prev.avatar,
        location: user.city ? `${user.city}, Россия` : prev.location,
        personalInfo: {
          ...prev.personalInfo,
          firstName: user.firstName || prev.personalInfo.firstName,
          lastName: user.lastName || prev.personalInfo.lastName,
          email: user.email || prev.personalInfo.email,
          status: user.status || prev.personalInfo.status,
          city: user.city || prev.personalInfo.city,
          birthDate: user.birthDate || prev.personalInfo.birthDate,
          avatar: user.avatar || prev.personalInfo.avatar
        }
      }));
    }
  }, [userId]);

  const handleTabChange = (tabId) => {
    setActiveTab(tabId);
  };

  const handleWritePost = () => {
    navigate('/write-post');
  };

  const handleEditProfile = () => {
    navigate('/edit-profile');
  };

  const getStatusText = (status) => {
    const statusMap = {
      'mom': 'Мама',
      'pregnant': 'Беременная',
      'planning': 'Планирую беременность',
      'other': 'Другое'
    };
    return statusMap[status] || status;
  };

  const renderTabContent = () => {
    switch (activeTab) {
      case 'posts':
        return (
          <div className="tab-content">
            <div className="content-filters">
              <div className="category-filter">
                <FontAwesomeIcon icon={faFilter} />
                <select 
                  value={selectedCategory} 
                  onChange={(e) => setSelectedCategory(e.target.value)}
                >
                  {profile.categories.map(category => (
                    <option key={category} value={category}>{category}</option>
                  ))}
                </select>
              </div>
              <div className="search-filter">
                <FontAwesomeIcon icon={faSearch} />
                <input
                  type="text"
                  placeholder="Поиск"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
              </div>
            </div>
            
            {profile.posts.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">
                  <FontAwesomeIcon icon={faPen} />
                </div>
                <h3>Вы не написали еще ни одной записи</h3>
                <p>Поделитесь своими мыслями и опытом с другими мамами</p>
                <button className="write-button" onClick={handleWritePost}>
                  <FontAwesomeIcon icon={faPen} />
                  Написать первую запись
                </button>
              </div>
            ) : (
              <div className="posts-list">
                {/* Здесь будет список постов */}
              </div>
            )}
          </div>
        );
      
      case 'drafts':
        return (
          <div className="tab-content">
            <div className="empty-state">
              <div className="empty-icon">
                <FontAwesomeIcon icon={faEdit} />
              </div>
              <h3>У вас пока нет черновиков</h3>
              <p>Создайте черновик, чтобы сохранить идеи для будущих записей</p>
            </div>
          </div>
        );
      
      case 'activity':
        return (
          <div className="tab-content">
            <div className="empty-state">
              <div className="empty-icon">
                <FontAwesomeIcon icon={faEye} />
              </div>
              <h3>Ваша активность пока не отображается</h3>
              <p>Начните общаться с другими мамами, чтобы увидеть свою активность</p>
            </div>
          </div>
        );
      
      case 'archive':
        return (
          <div className="tab-content">
            <div className="empty-state">
              <div className="empty-icon">
                <FontAwesomeIcon icon={faCog} />
              </div>
              <h3>Архив пуст</h3>
              <p>Здесь будут храниться ваши старые записи</p>
            </div>
          </div>
        );
      
      default:
        return null;
    }
  };

  return (
    <div className="profile-page">
      {/* Main Content */}
      <main className="profile-main">
        {/* Profile Banner в стиле MomsBlog */}
        <div className="profile-banner">
          <div className="profile-avatar">
            {profile.personalInfo.avatar ? (
              <img src={profile.personalInfo.avatar} alt={profile.fullName} />
            ) : (
              <div className="avatar-placeholder">
                {profile.avatar}
                {profile.isOnline && <span className="online-indicator"></span>}
              </div>
            )}
          </div>
          
          <div className="profile-info">
            <div className="profile-name">
              <h1>{profile.username}</h1>
              <span className="status-badge">{profile.status}</span>
            </div>
            <div className="profile-location">
              <FontAwesomeIcon icon={faMapMarkerAlt} />
              {profile.location}
            </div>
            <div className="profile-actions">
              <button className="write-diary-btn" onClick={handleWritePost}>
                <FontAwesomeIcon icon={faPen} />
                Написать в дневник
              </button>
              <button className="edit-profile-btn" onClick={handleEditProfile}>
                <FontAwesomeIcon icon={faEdit} />
                Редактировать профиль
              </button>
            </div>
          </div>
        </div>

        {/* Statistics */}
        <div className="profile-stats">
          <div className="stat-item">
            <span className="stat-number">{profile.statistics.posts}</span>
            <span className="stat-label">записей</span>
          </div>
          <div className="stat-item">
            <span className="stat-number">{profile.statistics.friends}</span>
            <span className="stat-label">друзей</span>
          </div>
          <div className="stat-item">
            <span className="stat-number">{profile.statistics.subscribers}</span>
            <span className="stat-label">подписчиков</span>
          </div>
        </div>

        {/* User Status Cards */}
        <div className="status-cards">
          <div className="status-card">
            <FontAwesomeIcon icon={faUser} />
            <div className="status-info">
              <span className="status-name">{profile.personalInfo.firstName || profile.username}</span>
              <span className="status-duration">{profile.joinDate}</span>
            </div>
          </div>
          
          {profile.personalInfo.status === 'pregnant' && (
            <div className="status-card pregnancy">
              <FontAwesomeIcon icon={faBaby} />
              <div className="status-info">
                <span className="status-name">Жду малыша</span>
                <span className="status-duration">2 неделя</span>
              </div>
            </div>
          )}
        </div>

        {/* Tabs */}
        <div className="profile-tabs">
          <button
            className={`tab-button ${activeTab === 'posts' ? 'active' : ''}`}
            onClick={() => handleTabChange('posts')}
          >
            Записи
          </button>
          <button
            className={`tab-button ${activeTab === 'drafts' ? 'active' : ''}`}
            onClick={() => handleTabChange('drafts')}
          >
            Черновики
          </button>
          <button
            className={`tab-button ${activeTab === 'activity' ? 'active' : ''}`}
            onClick={() => handleTabChange('activity')}
          >
            Активность
          </button>
          <button
            className={`tab-button ${activeTab === 'archive' ? 'active' : ''}`}
            onClick={() => handleTabChange('archive')}
          >
            Архив
          </button>
        </div>

        {/* Tab Content */}
        {renderTabContent()}
      </main>
    </div>
  );
};

export default ProfilePage;
