import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMapMarkerAlt, faCalendar } from '@fortawesome/free-solid-svg-icons';
import './CommentTest.css';

const CommentTest = () => {
  const formatDate = (dateString) => {
    try {
      if (!dateString) return 'Недавно';
      
      const date = new Date(dateString);
      if (isNaN(date.getTime())) return 'Недавно';
      
      const now = new Date();
      const diffInMinutes = Math.floor((now - date) / (1000 * 60));
      const diffInHours = Math.floor(diffInMinutes / 60);
      
      if (diffInMinutes < 1) return 'Только что';
      if (diffInMinutes < 60) return `${diffInMinutes} мин${diffInMinutes === 1 ? 'у' : diffInMinutes < 5 ? 'ы' : ''} назад`;
      if (diffInHours < 24) return `${diffInHours} час${diffInHours === 1 ? '' : diffInHours < 5 ? 'а' : 'ов'} назад`;
      
      return date.toLocaleDateString('ru-RU');
    } catch (error) {
      return 'Недавно';
    }
  };

  const getAuthorInitials = (author) => {
    if (!author) return 'А';
    const firstName = author.first_name || '';
    const lastName = author.last_name || '';
    return `${firstName.charAt(0) || 'А'}${lastName.charAt(0) || ''}`.toUpperCase();
  };

  const getAuthorName = (author) => {
    if (!author) return 'Автор';
    const firstName = author.first_name || '';
    const lastName = author.last_name || '';
    return `${firstName} ${lastName}`.trim() || author.username || 'Автор';
  };

  const now = new Date();
  const testComments = [
    {
      id: 1,
      content: "Прикладывайте ребенка чаще, может, и придет, тут только один совет",
      author: {
        id: 1,
        first_name: "Мать",
        last_name: "булочки",
        username: "mother_buns",
        avatar: null,
        city: "Курск"
      },
      created_at: new Date(now.getTime() - 29 * 60 * 1000).toISOString(),
      parent: null,
      is_approved: true
    },
    {
      id: 2,
      content: "Согласна с вами! Это действительно помогает",
      author: {
        id: 2,
        first_name: "Анна",
        last_name: "Петрова",
        username: "anna_p",
        avatar: null,
        city: "Москва"
      },
      created_at: new Date(now.getTime() - 15 * 60 * 1000).toISOString(),
      parent: 1,
      is_approved: true
    },
    {
      id: 3,
      content: "Попробуйте разные позы для кормления, это тоже важно",
      author: {
        id: 3,
        first_name: "Елена",
        last_name: "Сидорова",
        username: "elena_s",
        avatar: null,
        city: "Санкт-Петербург"
      },
      created_at: new Date(now.getTime() - 45 * 60 * 1000).toISOString(),
      parent: null,
      is_approved: true
    }
  ];

  return (
    <div className="comment-test-container">
      <h2>Тест комментариев</h2>
      
      <div className="comments-list">
        {testComments
          .filter(comment => !comment.parent)
          .map(comment => (
            <div key={comment.id} className="comment">
              <div className="comment-author">
                <div className="comment-avatar">
                  {comment.author?.avatar ? (
                    <img 
                      src={comment.author.avatar} 
                      alt={getAuthorName(comment.author)}
                    />
                  ) : (
                    <div className="comment-initials">
                      {getAuthorInitials(comment.author)}
                    </div>
                  )}
                </div>
                <div className="comment-info">
                  <div className="comment-author-name">
                    {getAuthorName(comment.author)}
                  </div>
                  <div className="comment-author-location">
                    <FontAwesomeIcon icon={faMapMarkerAlt} />
                    {comment.author?.city || 'Москва'}
                  </div>
                  <div className="comment-date">
                    <FontAwesomeIcon icon={faCalendar} />
                    {formatDate(comment.created_at)}
                  </div>
                </div>
              </div>
              <div className="comment-content">{comment.content}</div>
              
              {/* Ответы на комментарий */}
              {testComments
                .filter(reply => reply.parent === comment.id)
                .map(reply => (
                  <div key={reply.id} className="comment reply">
                    <div className="comment-author">
                      <div className="comment-avatar">
                        {reply.author?.avatar ? (
                          <img 
                            src={reply.author.avatar} 
                            alt={getAuthorName(reply.author)}
                          />
                        ) : (
                          <div className="comment-initials">
                            {getAuthorInitials(reply.author)}
                          </div>
                        )}
                      </div>
                      <div className="comment-info">
                        <div className="comment-author-name">
                          {getAuthorName(reply.author)}
                        </div>
                        <div className="comment-author-location">
                          <FontAwesomeIcon icon={faMapMarkerAlt} />
                          {reply.author?.city || 'Москва'}
                        </div>
                        <div className="comment-date">
                          <FontAwesomeIcon icon={faCalendar} />
                          {formatDate(reply.created_at)}
                        </div>
                      </div>
                    </div>
                    <div className="comment-content">{reply.content}</div>
                  </div>
                ))}
            </div>
          ))}
      </div>
    </div>
  );
};

export default CommentTest;
