import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faEnvelope, 
  faLock, 
  faEye, 
  faEyeSlash,
  faArrowLeft,
  faUser
} from '@fortawesome/free-solid-svg-icons';
import './AuthPage.css';

const AuthPage = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    // Имитация запроса к серверу
    try {
      // Реальный запрос к API
      const response = await fetch('http://localhost:8000/api/auth/login/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: email,
          password: password
        })
      });

      const data = await response.json();

      if (response.ok && data.success) {
        // Успешная авторизация
        console.log('Авторизация успешна:', data);
        
        // Сохраняем полные данные пользователя из ответа сервера
        const userData = {
          ...data.user,
          email: email
        };
        localStorage.setItem('userData', JSON.stringify(userData));
        
        // Перенаправляем на страницу профиля
        navigate('/profile/1');
      } else {
        // Ошибка от сервера
        setError(data.message || 'Неверный email или пароль');
      }
      
    } catch (err) {
      setError('Ошибка при авторизации. Попробуйте еще раз.');
    } finally {
      setIsLoading(false);
    }
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  return (
    <div className="auth-page">
      <div className="auth-container">
        {/* Кнопка "Назад" */}
        <Link to="/" className="back-button">
          <FontAwesomeIcon icon={faArrowLeft} />
          <span>Назад</span>
        </Link>

        {/* Заголовок */}
        <div className="auth-header">
          <div className="auth-icon">
            <FontAwesomeIcon icon={faUser} />
          </div>
          <h1>Войти в аккаунт</h1>
          <p>Добро пожаловать обратно! Войдите в свой аккаунт</p>
        </div>

        {/* Форма авторизации */}
        <form className="auth-form" onSubmit={handleSubmit}>
          {error && (
            <div className="error-message">
              {error}
            </div>
          )}

          {/* Email поле */}
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <div className="input-wrapper">
              <FontAwesomeIcon icon={faEnvelope} className="input-icon" />
              <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Введите ваш email"
                required
              />
            </div>
          </div>

          {/* Пароль поле */}
          <div className="form-group">
            <label htmlFor="password">Пароль</label>
            <div className="input-wrapper">
              <FontAwesomeIcon icon={faLock} className="input-icon" />
              <input
                type={showPassword ? 'text' : 'password'}
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Введите ваш пароль"
                required
              />
              <button
                type="button"
                className="password-toggle"
                onClick={togglePasswordVisibility}
              >
                <FontAwesomeIcon icon={showPassword ? faEyeSlash : faEye} />
              </button>
            </div>
          </div>

          {/* Забыли пароль */}
          <div className="forgot-password">
            <Link to="/forgot-password">Забыли пароль?</Link>
          </div>

          {/* Кнопка входа */}
          <button 
            type="submit" 
            className="auth-button"
            disabled={isLoading}
          >
            {isLoading ? 'Вход...' : 'Войти'}
          </button>
        </form>

        {/* Разделитель */}
        <div className="auth-divider">
          <span>или</span>
        </div>

        {/* Ссылка на регистрацию */}
        <div className="auth-footer">
          <p>
            Нет аккаунта?{' '}
            <Link to="/register" className="register-link">
              Зарегистрироваться
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default AuthPage;
