import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft, 
  faSearch, 
  faUser,
  faCircle,
  faUserFriends,
  faMessage,
  faClock,
  faEllipsisV,
  faPlus,
  faFilter
} from '@fortawesome/free-solid-svg-icons';
import { getChatsList } from '../../utils/chatStorage';
import './ChatsPage.css';

const ChatsPage = () => {
  const navigate = useNavigate();
  const [chats, setChats] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredChats, setFilteredChats] = useState([]);

  useEffect(() => {
    fetchChats();
  }, []);

  useEffect(() => {
    // Фильтрация чатов по поисковому запросу
    const filtered = chats.filter(chat => 
      chat.userName?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      chat.lastMessage?.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setFilteredChats(filtered);
  }, [searchQuery, chats]);

  const fetchChats = async () => {
    try {
      setLoading(true);

      // Получаем чаты из localStorage
      const chatsList = getChatsList();

      // Преобразуем в формат для отображения
      const formattedChats = chatsList.map(chat => ({
        id: chat.userId,
        user: {
          id: chat.userId,
          first_name: `Пользователь ${chat.userId}`,
          last_name: '',
          username: `user_${chat.userId}`,
          avatar: null,
          city: 'Не указан'
        },
        lastMessage: chat.lastMessage,
        lastMessageTime: chat.lastMessageTime,
        unreadCount: chat.unreadCount,
        isOnline: false
      }));

      setChats(formattedChats);
    } catch (error) {
      console.error('Ошибка загрузки чатов:', error);
      setChats([]);
    } finally {
      setLoading(false);
    }
  };

  const handleChatClick = (userId) => {
    navigate(`/chat/${userId}`);
  };

  const formatTime = (timestamp) => {
    try {
      const date = new Date(timestamp);
      const now = new Date();
      const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));

      if (diffInHours < 1) {
        const diffInMinutes = Math.floor((now - date) / (1000 * 60));
        if (diffInMinutes < 1) return 'Только что';
        return `${diffInMinutes} мин назад`;
      }
      if (diffInHours < 24) return `${diffInHours} ч назад`;

      return date.toLocaleDateString('ru-RU', { 
        day: 'numeric', 
        month: 'short' 
      });
    } catch (error) {
      return 'Недавно';
    }
  };

  const handleRefresh = () => {
    fetchChats();
  };

  if (loading) {
    return (
      <div className="chats-page">
        <div className="chats-header">
          <button className="btn-back" onClick={() => navigate(-1)}>
            <FontAwesomeIcon icon={faArrowLeft} />
            <span>Назад</span>
          </button>
          <h1>Мои чаты</h1>
        </div>
        <div className="chats-loading">
          <div className="loading-spinner"></div>
          <p>Загрузка чатов...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="chats-page">
      {/* Заголовок */}
      <div className="chats-header">
        <button className="btn-back" onClick={() => navigate(-1)}>
          <FontAwesomeIcon icon={faArrowLeft} />
          <span>Назад</span>
        </button>
        <h1>Мои чаты</h1>
        <button className="refresh-btn" onClick={handleRefresh} title="Обновить">
          <FontAwesomeIcon icon={faPlus} style={{ transform: 'rotate(45deg)' }} />
        </button>
      </div>

      {/* Поиск */}
      <div className="search-container">
        <div className="search-wrapper">
          <FontAwesomeIcon icon={faSearch} className="search-icon" />
          <input
            type="text"
            placeholder="Поиск по чатам..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="search-input"
          />
          {searchQuery && (
            <button 
              className="clear-search" 
              onClick={() => setSearchQuery('')}
            >
              ×
            </button>
          )}
        </div>
      </div>

      {/* Список чатов */}
      <div className="chats-container">
        {filteredChats.length === 0 ? (
          <div className="empty-chats">
            {searchQuery ? (
              <>
                <div className="empty-chats-icon">
                  <FontAwesomeIcon icon={faSearch} />
                </div>
                <h3>Чаты не найдены</h3>
                <p>Попробуйте изменить поисковый запрос</p>
              </>
            ) : (
              <>
                <div className="empty-chats-icon">
                  <FontAwesomeIcon icon={faMessage} />
                </div>
                <h3>У вас пока нет чатов</h3>
                <p>Начните общение с другими пользователями</p>
              </>
            )}
          </div>
        ) : (
          <div className="chats-list">
            {filteredChats.map((chat) => (
              <div 
                key={chat.id} 
                className="chat-item"
                onClick={() => handleChatClick(chat.user.id)}
              >
                <div className="chat-avatar">
                  {chat.user.avatar ? (
                    <img src={chat.user.avatar} alt={chat.user.first_name} />
                  ) : (
                    <div className="avatar-placeholder">
                      <FontAwesomeIcon icon={faUser} />
                    </div>
                  )}
                  <div className={`online-indicator ${chat.isOnline ? 'online' : 'offline'}`}></div>
                </div>

                <div className="chat-info">
                  <div className="chat-header-info">
                    <h3 className="chat-user-name">
                      {chat.user.first_name} {chat.user.last_name}
                    </h3>
                    <span className="chat-time">
                      {formatTime(chat.lastMessageTime)}
                    </span>
                  </div>

                  <div className="chat-message-preview">
                    <p className="last-message">{chat.lastMessage}</p>
                    {chat.unreadCount > 0 && (
                      <span className="unread-count">{chat.unreadCount}</span>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ChatsPage;
