// Утилита для работы с пользователями

// Получить информацию о пользователе по ID
export const getUserInfo = async (userId) => {
  try {
    console.log('Получаем информацию о пользователе:', userId);
    
    const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
    console.log('Токен найден:', !!token);
    
    if (!token) {
      console.log('Токен не найден, возвращаем базовую информацию');
      return {
        id: userId,
        name: `Пользователь ${userId}`,
        avatar: null
      };
    }

    console.log('Делаем API-запрос к:', `http://localhost:8000/api/users/profile/${userId}/`);
    
    const response = await fetch(`http://localhost:8000/api/users/profile/${userId}/`, {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      }
    });

    console.log('Ответ API:', response.status, response.statusText);

    if (response.ok) {
      const data = await response.json();
      console.log('Данные пользователя:', data);
      
      const userName = data.user?.first_name || data.user?.username || `Пользователь ${userId}`;
      const userAvatar = data.user?.avatar || null;
      
      console.log('Имя пользователя:', userName);
      console.log('Аватар пользователя:', userAvatar);
      
      return {
        id: userId,
        name: userName,
        avatar: userAvatar
      };
    } else {
      console.log('API вернул ошибку, возвращаем базовую информацию');
      return {
        id: userId,
        name: `Пользователь ${userId}`,
        avatar: null
      };
    }
  } catch (error) {
    console.error('Ошибка при получении информации о пользователе:', error);
    console.log('Возвращаем базовую информацию из-за ошибки');
    return {
      id: userId,
      name: `Пользователь ${userId}`,
      avatar: null
    };
  }
};

// Получить инициалы пользователя
export const getUserInitials = (userName) => {
  if (!userName) return 'А';
  const names = userName.split(' ');
  return `${names[0]?.charAt(0) || 'А'}${names[1]?.charAt(0) || ''}`.toUpperCase();
};

// Форматировать дату
export const formatDate = (dateString) => {
  if (!dateString) return 'Недавно';
  const date = new Date(dateString);
  const now = new Date();
  const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));
  
  if (diffInHours < 1) return 'Только что';
  if (diffInHours < 24) return `${diffInHours} ч назад`;
  
  const diffInDays = Math.floor(diffInHours / 24);
  if (diffInDays < 7) return `${diffInDays} дн назад`;
  
  return date.toLocaleDateString('ru-RU');
};
