import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './EditProfile.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft, 
  faSave, 
  faSpinner,
  faUser,
  faMapMarkerAlt
} from '@fortawesome/free-solid-svg-icons';

const EditProfile = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState(null);
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    city: '',
    avatar: null
  });
  const [avatarPreview, setAvatarPreview] = useState(null);

  useEffect(() => {
    fetchUserProfile();
  }, []);

  const fetchUserProfile = async () => {
    try {
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      
      if (!token) {
        setError('Необходима авторизация');
        return;
      }

      const response = await fetch('http://localhost:8000/api/auth/profile/', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        }
      });

      if (response.ok) {
        const userData = await response.json();
        setFormData({
          first_name: userData.first_name || '',
          last_name: userData.last_name || '',
          city: userData.city || '',
          avatar: null
        });
        if (userData.avatar) {
          setAvatarPreview(userData.avatar);
        }
      } else {
        setError('Ошибка загрузки профиля');
      }
    } catch (error) {
      console.error('Ошибка загрузки профиля:', error);
      setError('Ошибка загрузки профиля');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleAvatarChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({
        ...prev,
        avatar: file
      }));
      
      // Создаем превью для нового аватара
      const reader = new FileReader();
      reader.onload = (e) => {
        setAvatarPreview(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSaving(true);
    setError(null);

    try {
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      
      if (!token) {
        setError('Необходима авторизация');
        return;
      }

      const formDataToSend = new FormData();
      formDataToSend.append('first_name', formData.first_name);
      formDataToSend.append('last_name', formData.last_name);
      formDataToSend.append('city', formData.city);
      
      if (formData.avatar) {
        formDataToSend.append('avatar', formData.avatar);
      }

      const response = await fetch('http://localhost:8000/api/users/profile/update/', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
        body: formDataToSend
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Профиль успешно обновлен:', data);
        
        // Обновляем данные пользователя в localStorage
        const currentUserData = JSON.parse(localStorage.getItem('userData') || '{}');
        const updatedUserData = {
          ...currentUserData,
          first_name: formData.first_name,
          last_name: formData.last_name,
          city: formData.city,
          avatar: data.avatar || currentUserData.avatar
        };
        localStorage.setItem('userData', JSON.stringify(updatedUserData));
        
        // Перенаправляем на профиль
        navigate(`/profile/${updatedUserData.id || currentUserData.id}`);
      } else {
        const errorData = await response.json();
        console.error('Ошибка обновления профиля:', errorData);
        setError(errorData.error || 'Ошибка при обновлении профиля');
      }
    } catch (error) {
      console.error('Ошибка при обновлении профиля:', error);
      setError('Ошибка при обновлении профиля');
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <div className="edit-profile-loading">
        <div className="loading-spinner"></div>
        <p>Загрузка профиля...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="edit-profile-error">
        <h2>Ошибка</h2>
        <p>{error}</p>
        <button onClick={() => navigate(-1)} className="btn-back">
          <FontAwesomeIcon icon={faArrowLeft} />
          Назад
        </button>
      </div>
    );
  }

  return (
    <div className="edit-profile-page">
      <div className="edit-profile-container">
        {/* Заголовок */}
        <div className="edit-profile-header">
          <button onClick={() => navigate(-1)} className="btn-back">
            <FontAwesomeIcon icon={faArrowLeft} />
            Назад
          </button>
          <h1>Редактировать профиль</h1>
        </div>

        {/* Форма редактирования */}
        <form onSubmit={handleSubmit} className="edit-profile-form">
          {/* Аватар */}
          <div className="form-group avatar-group">
            <label>Аватар</label>
            <div className="avatar-upload">
              <div className="avatar-preview">
                {avatarPreview ? (
                  <img src={avatarPreview} alt="Аватар" />
                ) : (
                  <div className="avatar-placeholder">
                    <FontAwesomeIcon icon={faUser} />
                  </div>
                )}
              </div>
              <input
                type="file"
                id="avatar"
                name="avatar"
                accept="image/*"
                onChange={handleAvatarChange}
                className="avatar-input"
              />
              <label htmlFor="avatar" className="avatar-label">
                Выбрать изображение
              </label>
            </div>
          </div>

          {/* Имя */}
          <div className="form-group">
            <label htmlFor="first_name">
              <FontAwesomeIcon icon={faUser} />
              Имя *
            </label>
            <input
              type="text"
              id="first_name"
              name="first_name"
              value={formData.first_name}
              onChange={handleInputChange}
              required
              placeholder="Введите ваше имя"
            />
          </div>

          {/* Фамилия */}
          <div className="form-group">
            <label htmlFor="last_name">
              <FontAwesomeIcon icon={faUser} />
              Фамилия *
            </label>
            <input
              type="text"
              id="last_name"
              name="last_name"
              value={formData.last_name}
              onChange={handleInputChange}
              required
              placeholder="Введите вашу фамилию"
            />
          </div>

          {/* Город */}
          <div className="form-group">
            <label htmlFor="city">
              <FontAwesomeIcon icon={faMapMarkerAlt} />
              Город
            </label>
            <input
              type="text"
              id="city"
              name="city"
              value={formData.city}
              onChange={handleInputChange}
              placeholder="Введите ваш город"
            />
          </div>

          {/* Кнопки */}
          <div className="form-actions">
            <button 
              type="submit" 
              className="btn-save"
              disabled={saving}
            >
              {saving ? (
                <>
                  <FontAwesomeIcon icon={faSpinner} spin />
                  Сохранение...
                </>
              ) : (
                <>
                  <FontAwesomeIcon icon={faSave} />
                  Сохранить
                </>
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
