import React from 'react';
import './SmallCards.css';

const SmallCards = () => {
  const smallCards = [
    {
      id: 1,
      title: 'Пляжный отдых: как занять малыша и расслабиться самой?',
      color: 'yellow',
      illustration: 'baby-beach'
    },
    {
      id: 2,
      title: 'MomsBlog — советы от мам и врачей',
      color: 'blue',
      illustration: 'mom-baby'
    },
    {
      id: 3,
      title: 'Как определить беременность без тестов: методы, их...',
      color: 'gray',
      illustration: 'pregnant-woman'
    }
  ];

          return (
          <div className="small-cards">
            {smallCards.map((card, index) => (
              <div 
                key={card.id} 
                className={`featured-card small-card ${card.color}`}
                style={{ '--card-index': index }}
              >
                <div className="card-content">
                  <h4>{card.title}</h4>
                </div>
                <div className="card-image">
                  <div className={`illustration ${card.illustration}`}></div>
                </div>
              </div>
            ))}
          </div>
        );
};

export default SmallCards;
