import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faBookmark, 
  faUser, 
  faMapMarkerAlt, 
  faCalendar,
  faArrowLeft
} from '@fortawesome/free-solid-svg-icons';
import './ArchivePage.css';

const ArchivePage = () => {
  const [archivedPosts, setArchivedPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchArchivedPosts();
  }, []);

  const fetchArchivedPosts = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      
      if (!token) {
        setError('Необходима авторизация');
        setLoading(false);
        return;
      }

      const response = await fetch('http://localhost:8000/api/users/archive/', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        setArchivedPosts(data.results || []);
      } else {
        setError('Ошибка загрузки архива');
      }
    } catch (error) {
      console.error('Ошибка загрузки архива:', error);
      setError('Ошибка загрузки архива');
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('ru-RU', {
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      });
    } catch (error) {
      return 'Недавно';
    }
  };

  const getAuthorInitials = (author) => {
    if (!author) return 'А';
    const firstName = author.first_name || '';
    const lastName = author.last_name || '';
    return `${firstName.charAt(0)}${lastName.charAt(0)}`.toUpperCase() || 'А';
  };

  const getUserAvatarUrl = (userData) => {
    if (userData.avatar) return userData.avatar;
    const initials = getAuthorInitials(userData);
    return `https://via.placeholder.com/40x40/667eea/ffffff?text=${encodeURIComponent(initials)}`;
  };

  if (loading) {
    return (
      <div className="archive-loading">
        <div className="loading-spinner"></div>
        <p>Загрузка архива...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="archive-error">
        <h2>Ошибка</h2>
        <p>{error}</p>
        <button onClick={fetchArchivedPosts} className="btn-retry">
          Попробовать снова
        </button>
      </div>
    );
  }

  return (
    <div className="archive-page">
      {/* Хлебные крошки */}
      <div className="breadcrumbs-container">
        <nav className="breadcrumbs">
          <Link to="/" className="breadcrumb-link">Главная</Link>
          <FontAwesomeIcon icon={faArrowLeft} className="breadcrumb-separator" />
          <span className="breadcrumb-link active">Архив</span>
        </nav>
      </div>

      {/* Заголовок */}
      <div className="archive-header">
        <div className="archive-title">
          <FontAwesomeIcon icon={faBookmark} className="archive-icon" />
          <h1>Мой архив</h1>
        </div>
        <p className="archive-description">
          Здесь собраны все посты, которые вы сохранили для себя
        </p>
      </div>

      {/* Список постов */}
      <div className="archive-content">
        {archivedPosts.length === 0 ? (
          <div className="archive-empty">
            <FontAwesomeIcon icon={faBookmark} className="empty-icon" />
            <h3>Архив пуст</h3>
            <p>Вы пока не сохранили ни одного поста в архив</p>
            <Link to="/posts" className="btn-browse-posts">
              Просмотреть посты
            </Link>
          </div>
        ) : (
          <div className="archived-posts-grid">
            {archivedPosts.map((post) => (
              <div key={post.id} className="archived-post-card">
                <div className="post-header">
                  <div className="author-info">
                    <div className="author-avatar">
                      {post.author.avatar ? (
                        <img 
                          src={post.author.avatar} 
                          alt={post.author.first_name}
                          onError={(e) => {
                            e.target.style.display = 'none';
                            e.target.nextSibling.style.display = 'flex';
                          }}
                        />
                      ) : null}
                      <div className="author-initials" style={{ 
                        display: post.author.avatar ? 'none' : 'flex' 
                      }}>
                        {getAuthorInitials(post.author)}
                      </div>
                    </div>
                    <div className="author-details">
                      <div className="author-name">
                        {post.author.first_name} {post.author.last_name}
                      </div>
                      <div className="post-meta">
                        <span className="post-time">
                          <FontAwesomeIcon icon={faCalendar} />
                          {formatDate(post.created_at)}
                        </span>
                        <span className="post-location">
                          <FontAwesomeIcon icon={faMapMarkerAlt} />
                          {post.author.city || 'Не указан'}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
                
                <div className="post-content">
                  <h3 className="post-title">
                    <Link to={`/post-detail/${post.slug}`}>
                      {post.title}
                    </Link>
                  </h3>
                  
                  {post.category && (
                    <div className="post-category">
                      {post.category.name}
                    </div>
                  )}
                  
                  {post.short_description && (
                    <p className="post-excerpt">
                      {post.short_description}
                    </p>
                  )}
                  
                  <div className="post-actions">
                    <Link to={`/post-detail/${post.slug}`} className="read-more">
                      Читать далее
                    </Link>
                    <span className="archived-date">
                      Сохранено: {formatDate(post.archived_at)}
                    </span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ArchivePage;
