import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faSearch,
  faPen,
  faUser,
  faFilter,
  faBaby,
  faCalendarAlt,
  faMapMarkerAlt,
  faEdit,
  faCog
} from '@fortawesome/free-solid-svg-icons';
import profileData from '../../data/profileData.json';
import './ProfilePage.css';

const ProfilePage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const [profile, setProfile] = useState(profileData.profile);
  const [activeTab, setActiveTab] = useState('posts');
  const [selectedCategory, setSelectedCategory] = useState('–í—Å–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏');
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    // –ó–¥–µ—Å—å –º–æ–∂–Ω–æ –∑–∞–≥—Ä—É–∑–∏—Ç—å –¥–∞–Ω–Ω—ã–µ –ø—Ä–æ—Ñ–∏–ª—è —Å —Å–µ—Ä–≤–µ—Ä–∞
    // fetch(`/api/users/${userId}/profile`)
    //   .then(response => response.json())
    //   .then(data => setProfile(data));
    
    // –ü–æ–ª—É—á–∞–µ–º –¥–∞–Ω–Ω—ã–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –∏–∑ localStorage (–µ—Å–ª–∏ –µ—Å—Ç—å)
    const userData = localStorage.getItem('userData');
    if (userData) {
      const user = JSON.parse(userData);
      setProfile(prev => ({
        ...prev,
        username: user.firstName || prev.username,
        fullName: `${user.firstName || ''} ${user.lastName || ''}`.trim(),
        avatar: user.firstName ? `${user.firstName[0]}${user.lastName ? user.lastName[0] : ''}` : prev.avatar,
        location: user.city ? `${user.city}, –†–æ—Å—Å–∏—è` : prev.location,
        personalInfo: {
          ...prev.personalInfo,
          firstName: user.firstName || prev.personalInfo.firstName,
          lastName: user.lastName || prev.personalInfo.lastName,
          email: user.email || prev.personalInfo.email,
          status: user.status || prev.personalInfo.status,
          city: user.city || prev.personalInfo.city,
          birthDate: user.birthDate || prev.personalInfo.birthDate,
          avatar: user.avatar || prev.personalInfo.avatar
        }
      }));
    }
  }, [userId]);

  const handleTabChange = (tabId) => {
    setActiveTab(tabId);
    setProfile(prev => ({
      ...prev,
      tabs: prev.tabs.map(tab => ({
        ...tab,
        isActive: tab.id === tabId
      }))
    }));
  };

  const handleWritePost = () => {
    navigate('/write-post');
  };

  const handleEditProfile = () => {
    navigate('/edit-profile');
  };

  const getStatusText = (status) => {
    const statusMap = {
      'mom': '–ú–∞–º–∞',
      'pregnant': '–ë–µ—Ä–µ–º–µ–Ω–Ω–∞—è',
      'planning': '–ü–ª–∞–Ω–∏—Ä—É—é –±–µ—Ä–µ–º–µ–Ω–Ω–æ—Å—Ç—å',
      'other': '–î—Ä—É–≥–æ–µ'
    };
    return statusMap[status] || status;
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('ru-RU', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const renderTabContent = () => {
    switch (activeTab) {
      case 'posts':
        return (
          <div className="tab-content">
            <div className="content-filters">
              <div className="category-filter">
                <FontAwesomeIcon icon={faFilter} />
                <select 
                  value={selectedCategory} 
                  onChange={(e) => setSelectedCategory(e.target.value)}
                >
                  {profile.categories.map(category => (
                    <option key={category} value={category}>{category}</option>
                  ))}
                </select>
              </div>
              <div className="search-filter">
                <FontAwesomeIcon icon={faSearch} />
                <input
                  type="text"
                  placeholder="–ü–æ–∏—Å–∫"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
              </div>
            </div>
            
            {profile.posts.length === 0 ? (
              <div className="empty-state">
                <p>–í—ã –Ω–µ –Ω–∞–ø–∏—Å–∞–ª–∏ –µ—â–µ –Ω–∏ –æ–¥–Ω–æ–π –∑–∞–ø–∏—Å–∏.</p>
                <button className="write-button" onClick={handleWritePost}>
                  –ù–∞–ø–∏—Å–∞—Ç—å
                </button>
              </div>
            ) : (
              <div className="posts-list">
                {/* –ó–¥–µ—Å—å –±—É–¥–µ—Ç —Å–ø–∏—Å–æ–∫ –ø–æ—Å—Ç–æ–≤ */}
              </div>
            )}
          </div>
        );
      
      case 'drafts':
        return (
          <div className="tab-content">
            <div className="empty-state">
              <p>–£ –≤–∞—Å –ø–æ–∫–∞ –Ω–µ—Ç —á–µ—Ä–Ω–æ–≤–∏–∫–æ–≤.</p>
            </div>
          </div>
        );
      
      case 'activity':
        return (
          <div className="tab-content">
            <div className="empty-state">
              <p>–í–∞—à–∞ –∞–∫—Ç–∏–≤–Ω–æ—Å—Ç—å –ø–æ–∫–∞ –Ω–µ –æ—Ç–æ–±—Ä–∞–∂–∞–µ—Ç—Å—è.</p>
            </div>
          </div>
        );
      
      case 'archive':
        return (
          <div className="tab-content">
            <div className="empty-state">
              <p>–ê—Ä—Ö–∏–≤ –ø—É—Å—Ç.</p>
            </div>
          </div>
        );
      
      default:
        return null;
    }
  };

  return (
    <div className="profile-page">
      {/* Header */}
      <header className="profile-header">
        <div className="header-content">
          <div className="logo">MomsBlog</div>
          <div className="search-bar">
            <FontAwesomeIcon icon={faSearch} />
            <input type="text" placeholder="–ü–æ–∏—Å–∫" />
          </div>
          <div className="header-actions">
            <button className="write-btn" onClick={handleWritePost}>
              <FontAwesomeIcon icon={faPen} />
              –ù–∞–ø–∏—Å–∞—Ç—å
            </button>
            <button className="profile-btn">
              <FontAwesomeIcon icon={faUser} />
              –ú–æ–π –ø—Ä–æ—Ñ–∏–ª—å
            </button>
          </div>
        </div>
      </header>

      <div className="profile-container">
        {/* Left Sidebar */}
        <aside className="profile-sidebar">
          <nav className="sidebar-nav">
            <ul>
              <li><a href="#" className="nav-link">–õ–µ–Ω—Ç–∞</a></li>
              <li><a href="#" className="nav-link">–£–≤–µ–¥–æ–º–ª–µ–Ω–∏—è</a></li>
              <li><a href="#" className="nav-link">–°–æ–æ–±—â–µ–Ω–∏—è <span className="badge">4</span></a></li>
              <li><a href="#" className="nav-link">–ú–æ–∏ —Å–æ–æ–±—â–µ—Å—Ç–≤–∞</a></li>
              <li><a href="#" className="nav-link">–ó–∞–∫–ª–∞–¥–∫–∏</a></li>
              <li><a href="#" className="nav-link">–†–∞–∑–¥–µ–ª—ã —Å–∞–π—Ç–∞ ‚ñº</a></li>
            </ul>
          </nav>
          
          <div className="social-links">
            <a href="#" className="social-link vk">VK</a>
            <a href="#" className="social-link telegram">Telegram</a>
            <a href="#" className="social-link other">+</a>
          </div>
          
          <div className="sidebar-footer">
            <a href="#" className="footer-link">–¢–µ—Ö–Ω–∏—á–µ—Å–∫–∞—è –ø–æ–¥–¥–µ—Ä–∂–∫–∞</a>
            <a href="#" className="footer-link">–†–µ–∫–ª–∞–º–∞ –Ω–∞ —Å–∞–π—Ç–µ</a>
          </div>
        </aside>

        {/* Main Content */}
        <main className="profile-main">
          {/* Profile Banner */}
          <div className="profile-banner">
            <div className="profile-avatar">
              {profile.personalInfo.avatar ? (
                <img src={profile.personalInfo.avatar} alt={profile.fullName} />
              ) : (
                <div className="avatar-placeholder">
                  {profile.avatar}
                  {profile.isOnline && <span className="online-indicator"></span>}
                </div>
              )}
            </div>
            
            <div className="profile-info">
              <div className="profile-name">
                <h1>{profile.username}</h1>
                <span className="status-badge">{profile.status}</span>
              </div>
              <div className="profile-location">
                <FontAwesomeIcon icon={faMapMarkerAlt} />
                {profile.location}
              </div>
              <button className="write-diary-btn" onClick={handleWritePost}>
                –ù–∞–ø–∏—Å–∞—Ç—å –≤ –¥–Ω–µ–≤–Ω–∏–∫
              </button>
            </div>
          </div>

          {/* Statistics */}
          <div className="profile-stats">
            <div className="stat-item">
              <span className="stat-number">{profile.statistics.posts}</span>
              <span className="stat-label">–∑–∞–ø–∏—Å–µ–π</span>
            </div>
            <div className="stat-item">
              <span className="stat-number">{profile.statistics.friends}</span>
              <span className="stat-label">–¥—Ä—É–∑–µ–π</span>
            </div>
            <div className="stat-item">
              <span className="stat-number">{profile.statistics.subscribers}</span>
              <span className="stat-label">–ø–æ–¥–ø–∏—Å—á–∏–∫–æ–≤</span>
            </div>
          </div>

          {/* User Status Cards */}
          <div className="status-cards">
            <div className="status-card">
              <FontAwesomeIcon icon={faUser} />
              <div className="status-info">
                <span className="status-name">{profile.username}</span>
                <span className="status-duration">{profile.joinDate}</span>
              </div>
            </div>
            
            {profile.pregnancyInfo && (
              <div className="status-card pregnancy">
                <FontAwesomeIcon icon={faBaby} />
                <div className="status-info">
                  <span className="status-name">{profile.pregnancyInfo.status}</span>
                  <span className="status-duration">{profile.pregnancyInfo.week}</span>
                </div>
              </div>
            )}
          </div>

          {/* Tabs */}
          <div className="profile-tabs">
            {profile.tabs.map(tab => (
              <button
                key={tab.id}
                className={`tab-button ${tab.isActive ? 'active' : ''}`}
                onClick={() => handleTabChange(tab.id)}
              >
                {tab.name}
              </button>
            ))}
          </div>

          {/* Tab Content */}
          {renderTabContent()}
        </main>

        {/* Right Sidebar (Advertisement) */}
        <aside className="profile-ads">
          <div className="ad-banner">
            <div className="ad-content">
              <h3>–ù–ê–°–õ–ê–ñ–î–ê–ô–°–Ø –í–ö–£–°–û–ú</h3>
              <div className="ad-product">ü•§</div>
            </div>
            <span className="ad-info">i</span>
          </div>
        </aside>
      </div>
    </div>
  );
};

export default ProfilePage;
