import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft, 
  faPaperPlane, 
  faEdit, 
  faTrash, 
  faCheck, 
  faTimes,
  faEllipsisV,
  faUser
} from '@fortawesome/free-solid-svg-icons';
import './ChatPage.css';

const ChatPage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const messagesEndRef = useRef(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [editingMessageId, setEditingMessageId] = useState(null);
  const [editText, setEditText] = useState('');
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  // Заглушка для пользователя (в реальном приложении загружали бы с сервера)
  useEffect(() => {
    // Имитация загрузки пользователя
    setTimeout(() => {
      setUser({
        id: userId,
        name: 'Анна Петрова',
        avatar: null,
        isOnline: true
      });
      setIsLoading(false);
    }, 500);

    // Заглушка для сообщений
    setMessages([
      {
        id: 1,
        text: 'Привет! Как дела?',
        sender: 'user',
        timestamp: new Date(Date.now() - 3600000),
        isEdited: false
      },
      {
        id: 2,
        text: 'Привет! Все хорошо, спасибо! А у тебя как?',
        sender: 'other',
        timestamp: new Date(Date.now() - 3000000),
        isEdited: false
      },
      {
        id: 3,
        text: 'Отлично! Хотела спросить про развитие малыша',
        sender: 'user',
        timestamp: new Date(Date.now() - 2400000),
        isEdited: false
      },
      {
        id: 4,
        text: 'Конечно! Что именно тебя интересует?',
        sender: 'other',
        timestamp: new Date(Date.now() - 1800000),
        isEdited: false
      }
    ]);
  }, [userId]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = () => {
    if (newMessage.trim()) {
      const message = {
        id: Date.now(),
        text: newMessage.trim(),
        sender: 'user',
        timestamp: new Date(),
        isEdited: false
      };
      setMessages(prev => [...prev, message]);
      setNewMessage('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const startEditing = (message) => {
    setEditingMessageId(message.id);
    setEditText(message.text);
  };

  const cancelEditing = () => {
    setEditingMessageId(null);
    setEditText('');
  };

  const saveEdit = () => {
    if (editText.trim()) {
      setMessages(prev => prev.map(msg => 
        msg.id === editingMessageId 
          ? { ...msg, text: editText.trim(), isEdited: true }
          : msg
      ));
      setEditingMessageId(null);
      setEditText('');
    }
  };

  const deleteMessage = (messageId) => {
    setMessages(prev => prev.filter(msg => msg.id !== messageId));
  };

  const formatTime = (timestamp) => {
    const now = new Date();
    const messageTime = new Date(timestamp);
    const diffInHours = Math.floor((now - messageTime) / (1000 * 60 * 60));
    
    if (diffInHours < 1) {
      const diffInMinutes = Math.floor((now - messageTime) / (1000 * 60));
      if (diffInMinutes < 1) return 'Только что';
      return `${diffInMinutes} мин назад`;
    }
    if (diffInHours < 24) return `${diffInHours} ч назад`;
    
    return messageTime.toLocaleDateString('ru-RU', { 
      day: 'numeric', 
      month: 'short',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (isLoading) {
    return (
      <div className="chat-page">
        <div className="chat-loading">
          <div className="loading-spinner"></div>
          <p>Загрузка чата...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="chat-page">
      {/* Заголовок чата */}
      <div className="chat-header">
        <button className="back-button" onClick={() => navigate(-1)}>
          <FontAwesomeIcon icon={faArrowLeft} />
        </button>
        
        <div className="chat-user-info">
          <div className="user-avatar">
            {user?.avatar ? (
              <img src={user.avatar} alt={user.name} />
            ) : (
              <div className="avatar-placeholder">
                <FontAwesomeIcon icon={faUser} />
              </div>
            )}
            <div className={`online-indicator ${user?.isOnline ? 'online' : 'offline'}`}></div>
          </div>
          <div className="user-details">
            <h3 className="user-name">{user?.name}</h3>
            <span className="user-status">
              {user?.isOnline ? 'В сети' : 'Не в сети'}
            </span>
          </div>
        </div>
      </div>

      {/* Сообщения */}
      <div className="messages-container">
        <div className="messages-list">
          {messages.map((message) => (
            <div 
              key={message.id} 
              className={`message ${message.sender === 'user' ? 'user-message' : 'other-message'}`}
            >
              {editingMessageId === message.id ? (
                <div className="edit-message-form">
                  <textarea
                    value={editText}
                    onChange={(e) => setEditText(e.target.value)}
                    onKeyPress={handleKeyPress}
                    autoFocus
                    className="edit-textarea"
                  />
                  <div className="edit-actions">
                    <button 
                      className="edit-btn save-btn"
                      onClick={saveEdit}
                    >
                      <FontAwesomeIcon icon={faCheck} />
                    </button>
                    <button 
                      className="edit-btn cancel-btn"
                      onClick={cancelEditing}
                    >
                      <FontAwesomeIcon icon={faTimes} />
                    </button>
                  </div>
                </div>
              ) : (
                <>
                  <div className="message-content">
                    <p className="message-text">
                      {message.text}
                      {message.isEdited && (
                        <span className="edited-indicator"> (ред.)</span>
                      )}
                    </p>
                    <div className="message-meta">
                      <span className="message-time">{formatTime(message.timestamp)}</span>
                    </div>
                  </div>
                  
                  {message.sender === 'user' && (
                    <div className="message-actions">
                      <button 
                        className="action-btn edit-btn"
                        onClick={() => startEditing(message)}
                        title="Редактировать"
                      >
                        <FontAwesomeIcon icon={faEdit} />
                      </button>
                      <button 
                        className="action-btn delete-btn"
                        onClick={() => deleteMessage(message.id)}
                        title="Удалить"
                      >
                        <FontAwesomeIcon icon={faTrash} />
                      </button>
                    </div>
                  )}
                </>
              )}
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>
      </div>

      {/* Форма отправки сообщения */}
      <div className="message-input-container">
        <div className="message-input-wrapper">
          <textarea
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Введите сообщение..."
            className="message-input"
            rows="1"
          />
          <button 
            className="send-button"
            onClick={handleSendMessage}
            disabled={!newMessage.trim()}
          >
            <FontAwesomeIcon icon={faPaperPlane} />
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatPage;
