                      {post.author.avatar ? (
                        <img 
                          src={post.author.avatar} 
                          alt={post.author.first_name}
                          onError={(e) => {
                            e.target.style.display = 'none';
                            e.target.nextSibling.style.display = 'flex';
                          }}
                        />
                      ) : null}
                      <div className="author-initials" style={{ 
                        display: post.author.avatar ? 'none' : 'flex' 
                      }}>
                        {getAuthorInitials(post.author)}
                      </div>
                    </div>
                    <div className="author-details">
                      <div className="author-name">
                        {post.author.first_name} {post.author.last_name}
                      </div>
                      <div className="post-meta">
                        <span className="post-time">
                          <FontAwesomeIcon icon={faCalendar} />
                          {formatDate(post.created_at)}
                        </span>
                        <span className="post-location">
                          <FontAwesomeIcon icon={faMapMarkerAlt} />
                          {post.author.city || 'ÐÐµ ÑƒÐºÐ°Ð·Ð°Ð½'}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
                
                <div className="post-content">
                  <h3 className="post-title">
                    <Link to={`/post-detail/${post.slug}`}>
                      {post.title}
                    </Link>
                  </h3>
                  
                  {post.category && (
                    <div className="post-category">
                      {post.category.name}
                    </div>
                  )}
                  
                  {post.short_description && (
                    <p className="post-excerpt">
                      {post.short_description}
                    </p>
                  )}
                  
                  <div className="post-actions">
                    <Link to={`/post-detail/${post.slug}`} className="read-more">
                      Ð§Ð¸Ñ‚Ð°Ñ‚ÑŒ Ð´Ð°Ð»ÐµÐµ
                    </Link>
                    <span className="archived-date">
                      Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÐµÐ½Ð¾: {formatDate(post.archived_at)}
                    </span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ArchivePage;
