import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft, 
  faPaperPlane, 
  faEdit, 
  faTrash, 
  faCheck, 
  faTimes,
  faUser,
  faMessage,
  faReply,
  faImage,
  faFile,
  faPaperclip,
  faFilePdf,
  faFileWord,
  faFileText,
  faFileArchive
} from '@fortawesome/free-solid-svg-icons';
import { apiClient } from '../../utils/api';
import { wsClient } from '../../utils/websocket';
import './ChatPage.css';

const ChatPage = () => {
  const { chatId } = useParams();
  const navigate = useNavigate();
  const messagesEndRef = useRef(null);
  const fileInputRef = useRef(null);

  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [editingMessageId, setEditingMessageId] = useState(null);
  const [editText, setEditText] = useState('');
  const [currentUser, setCurrentUser] = useState(null);
  const [chatUser, setChatUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isTyping, setIsTyping] = useState(false);
  const [typingUsers, setTypingUsers] = useState(new Set());
  const [contextMenu, setContextMenu] = useState(null);
  const [modalMessage, setModalMessage] = useState(null);
  const [swipeStartX, setSwipeStartX] = useState(null);
  const [swipeStartY, setSwipeStartY] = useState(null);
  const [replyToMessage, setReplyToMessage] = useState(null);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [showMediaOptions, setShowMediaOptions] = useState(false);
  const [sendingMessage, setSendingMessage] = useState(false);

  // –ó–∞–≥—Ä—É–∑–∫–∞ –¥–∞–Ω–Ω—ã—Ö —á–∞—Ç–∞
  useEffect(() => {
    loadChatData();
  }, [chatId]);

  // WebSocket —Å–æ–µ–¥–∏–Ω–µ–Ω–∏–µ
  useEffect(() => {
    if (currentUser && chatId) {
      wsClient.connect(currentUser.id);
      wsClient.joinChat(parseInt(chatId));
      
      // –û–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ WebSocket —Å–æ–æ–±—â–µ–Ω–∏–π
      wsClient.onMessage('new_message', handleNewMessage);
      wsClient.onMessage('message_updated', handleMessageUpdated);
      wsClient.onMessage('message_deleted', handleMessageDeleted);
      wsClient.onMessage('typing', handleTyping);
      
      return () => {
        wsClient.leaveChat(parseInt(chatId));
        wsClient.offMessage('new_message', handleNewMessage);
        wsClient.offMessage('message_updated', handleMessageUpdated);
        wsClient.offMessage('message_deleted', handleMessageDeleted);
        wsClient.offMessage('typing', handleTyping);
      };
    }
  }, [currentUser, chatId]);

  // –ê–≤—Ç–æ—Å–∫—Ä–æ–ª–ª –∫ –ø–æ—Å–ª–µ–¥–Ω–µ–º—É —Å–æ–æ–±—â–µ–Ω–∏—é
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const loadChatData = async () => {
    try {
      setIsLoading(true);
      
      // –ü–æ–ª—É—á–∞–µ–º —Ç–µ–∫—É—â–µ–≥–æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
      const user = await apiClient.getCurrentUser();
      setCurrentUser(user);
      
      // –ü–æ–ª—É—á–∞–µ–º —Å–æ–æ–±—â–µ–Ω–∏—è —á–∞—Ç–∞
      const messagesData = await apiClient.getMessages(chatId);
      setMessages(messagesData);
      
      // –ü–æ–ª—É—á–∞–µ–º –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –æ —Å–æ–±–µ—Å–µ–¥–Ω–∏–∫–µ
      const chats = await apiClient.getChats();
      const currentChat = chats.find(chat => chat.id === parseInt(chatId));
      if (currentChat) {
        const otherUserId = currentChat.user1_id === user.id ? currentChat.user2_id : currentChat.user1_id;
        const users = await apiClient.getUsers();
        const otherUser = users.find(u => u.id === otherUserId);
        setChatUser(otherUser);
      }
      
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —á–∞—Ç–∞:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleNewMessage = (data) => {
    setMessages(prev => [...prev, data.message]);
  };

  const handleMessageUpdated = (data) => {
    setMessages(prev => 
      prev.map(msg => 
        msg.id === data.message.id ? data.message : msg
      )
    );
  };

  const handleMessageDeleted = (data) => {
    setMessages(prev => 
      prev.filter(msg => msg.id !== data.message_id)
    );
  };

  const handleTyping = (data) => {
    if (data.user_id !== currentUser.id) {
      setTypingUsers(prev => {
        const newSet = new Set(prev);
        if (data.is_typing) {
          newSet.add(data.user_id);
        } else {
          newSet.delete(data.user_id);
        }
        return newSet;
      });
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleSendMessage = async () => {
    if ((!newMessage.trim() && selectedFiles.length === 0) || sendingMessage) return;

    try {
      setSendingMessage(true);
      
      // –û—Ç–ø—Ä–∞–≤–ª—è–µ–º —Ç–µ–∫—Å—Ç–æ–≤–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ
      if (newMessage.trim()) {
        const messageData = await apiClient.sendMessage(chatId, newMessage.trim());
        
        // –ó–∞–≥—Ä—É–∂–∞–µ–º —Ñ–∞–π–ª—ã, –µ—Å–ª–∏ –µ—Å—Ç—å
        if (selectedFiles.length > 0) {
          for (const file of selectedFiles) {
            await apiClient.uploadFile(messageData.id, file);
          }
        }
      }
      
      setNewMessage('');
      setSelectedFiles([]);
      setShowMediaOptions(false);
      
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –æ—Ç–ø—Ä–∞–≤–∫–∏ —Å–æ–æ–±—â–µ–Ω–∏—è:', error);
    } finally {
      setSendingMessage(false);
    }
  };

  const handleEditMessage = async () => {
    if (!editText.trim()) return;

    try {
      await apiClient.updateMessage(editingMessageId, editText.trim());
      setEditingMessageId(null);
      setEditText('');
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏—è —Å–æ–æ–±—â–µ–Ω–∏—è:', error);
    }
  };

  const handleDeleteMessage = async (messageId) => {
    try {
      await apiClient.deleteMessage(messageId);
      setContextMenu(null);
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ —É–¥–∞–ª–µ–Ω–∏—è —Å–æ–æ–±—â–µ–Ω–∏—è:', error);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleInputChange = (e) => {
    setNewMessage(e.target.value);
    
    // –ò–Ω–¥–∏–∫–∞—Ç–æ—Ä –Ω–∞–±–æ—Ä–∞ —Ç–µ–∫—Å—Ç–∞
    if (e.target.value.trim()) {
      wsClient.startTyping();
    } else {
      wsClient.stopTyping();
    }
  };

  const handleFileSelect = (e) => {
    const files = Array.from(e.target.files);
    setSelectedFiles(prev => [...prev, ...files]);
    setShowMediaOptions(false);
  };

  const removeFile = (index) => {
    setSelectedFiles(prev => prev.filter((_, i) => i !== index));
  };

  const handleMediaOptionClick = (type) => {
    if (type === 'photo') {
      fileInputRef.current.accept = 'image/*';
    } else if (type === 'document') {
      fileInputRef.current.accept = '.pdf,.doc,.docx,.txt,.zip,.rar';
    }
    fileInputRef.current.click();
  };

  const getFileIcon = (file) => {
    const extension = file.name.split('.').pop().toLowerCase();
    switch (extension) {
      case 'pdf':
        return faFilePdf;
      case 'doc':
      case 'docx':
        return faFileWord;
      case 'txt':
        return faFileText;
      case 'zip':
      case 'rar':
        return faFileArchive;
      default:
        return faFile;
    }
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const handleContextMenu = (e, message) => {
    e.preventDefault();
    if (message.sender_id === currentUser.id) {
      setContextMenu({
        x: e.clientX,
        y: e.clientY,
        message: message
      });
    }
  };

  const closeContextMenu = () => {
    setContextMenu(null);
  };

  const formatTime = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleTimeString('ru-RU', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  if (isLoading) {
    return (
      <div className="chat-page">
        <div className="loading">–ó–∞–≥—Ä—É–∑–∫–∞...</div>
      </div>
    );
  }

  return (
    <div className="chat-page" onClick={closeContextMenu}>
      {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ —á–∞—Ç–∞ */}
      <div className="chat-header">
        <button className="back-button" onClick={() => navigate('/friends')}>
          <FontAwesomeIcon icon={faArrowLeft} />
        </button>
        <div className="chat-user-info">
          <div className="user-avatar">
            <img 
              src={chatUser?.avatar || '/default-avatar.png'} 
              alt={chatUser?.name || '–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å'} 
            />
          </div>
          <div className="user-details">
            <h3 className="user-name">{chatUser?.name || '–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å'}</h3>
            <span className="user-city">üìç {chatUser?.city || '–ì–æ—Ä–æ–¥ –Ω–µ —É–∫–∞–∑–∞–Ω'}</span>
          </div>
        </div>
      </div>

      {/* –ö–æ–Ω—Ç–µ–π–Ω–µ—Ä —Å–æ–æ–±—â–µ–Ω–∏–π */}
      <div className="messages-container">
        <div className="messages-list">
          {messages.map((message) => (
            <div
              key={message.id}
              className={`message ${message.sender_id === currentUser.id ? 'my-message' : 'incoming-message'}`}
              onContextMenu={(e) => handleContextMenu(e, message)}
            >
              <div className="message-content">
                {message.text && (
                  <p className="message-text">{message.text}</p>
                )}
                
                {/* –§–∞–π–ª—ã —Å–æ–æ–±—â–µ–Ω–∏—è */}
                {message.files && message.files.length > 0 && (
                  <div className="message-files">
                    {message.files.map((file) => (
                      <div key={file.id} className="message-file">
                        <FontAwesomeIcon icon={getFileIcon(file)} />
                        <span className="file-name">{file.filename}</span>
                        <span className="file-size">({formatFileSize(file.file_size)})</span>
                      </div>
                    ))}
                  </div>
                )}
                
                <div className="message-meta">
                  <span className="message-time">{formatTime(message.created_at)}</span>
                  {message.edited_at && (
                    <span className="edited-indicator">(—Ä–µ–¥.)</span>
                  )}
                  {message.sender_id === currentUser.id && (
                    <span className="message-status">‚úì</span>
                  )}
                </div>
              </div>
            </div>
          ))}
          
          {/* –ò–Ω–¥–∏–∫–∞—Ç–æ—Ä –Ω–∞–±–æ—Ä–∞ —Ç–µ–∫—Å—Ç–∞ */}
          {typingUsers.size > 0 && (
            <div className="typing-indicator">
              <span>–ü–µ—á–∞—Ç–∞–µ—Ç...</span>
            </div>
          )}
          
          <div ref={messagesEndRef} />
        </div>
      </div>

      {/* –í—ã–±—Ä–∞–Ω–Ω—ã–µ —Ñ–∞–π–ª—ã */}
      {selectedFiles.length > 0 && (
        <div className="selected-files">
          {selectedFiles.map((file, index) => (
            <div key={index} className="selected-file">
              <FontAwesomeIcon icon={getFileIcon(file)} />
              <span className="file-name">{file.name}</span>
              <span className="file-size">({formatFileSize(file.size)})</span>
              <button 
                className="remove-file-btn"
                onClick={() => removeFile(index)}
              >
                <FontAwesomeIcon icon={faTimes} />
              </button>
            </div>
          ))}
        </div>
      )}

      {/* –û–ø—Ü–∏–∏ –º–µ–¥–∏–∞ */}
      {showMediaOptions && (
        <div className="media-options">
          <button 
            className="media-option"
            onClick={() => handleMediaOptionClick('photo')}
          >
            <FontAwesomeIcon icon={faImage} />
            –§–æ—Ç–æ
          </button>
          <button 
            className="media-option"
            onClick={() => handleMediaOptionClick('document')}
          >
            <FontAwesomeIcon icon={faFile} />
            –î–æ–∫—É–º–µ–Ω—Ç
          </button>
        </div>
      )}

      {/* –§–æ—Ä–º–∞ –≤–≤–æ–¥–∞ */}
      <div className="message-input-container">
        <div className="message-input-wrapper">
          <button 
            className="media-button"
            onClick={() => setShowMediaOptions(!showMediaOptions)}
            title="–ü—Ä–∏–∫—Ä–µ–ø–∏—Ç—å —Ñ–∞–π–ª"
          >
            <FontAwesomeIcon icon={faPaperclip} />
          </button>
          
          <textarea
            className="message-input"
            placeholder="–°–æ–æ–±—â–µ–Ω–∏–µ"
            value={newMessage}
            onChange={handleInputChange}
            onKeyPress={handleKeyPress}
            rows={1}
          />
          
          <button 
            className="send-button"
            onClick={handleSendMessage}
            disabled={(!newMessage.trim() && selectedFiles.length === 0) || sendingMessage}
            title="–û—Ç–ø—Ä–∞–≤–∏—Ç—å"
          >
            <FontAwesomeIcon icon={faPaperPlane} />
          </button>
        </div>

        {/* –°–∫—Ä—ã—Ç—ã–π input –¥–ª—è —Ñ–∞–π–ª–æ–≤ */}
        <input
          ref={fileInputRef}
          type="file"
          multiple
          accept="image/*,.pdf,.doc,.docx,.txt,.zip,.rar"
          onChange={handleFileSelect}
          className="file-input"
        />
      </div>

      {/* –ö–æ–Ω—Ç–µ–∫—Å—Ç–Ω–æ–µ –º–µ–Ω—é */}
      {contextMenu && (
        <div
          className="context-menu"
          style={{
            position: 'fixed',
            left: contextMenu.x,
            top: contextMenu.y,
            zIndex: 1000
          }}
        >
          <button
            className="context-menu-item"
            onClick={() => {
              setEditingMessageId(contextMenu.message.id);
              setEditText(contextMenu.message.text || '');
              setContextMenu(null);
            }}
          >
            <FontAwesomeIcon icon={faEdit} />
            –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å
          </button>
          <button
            className="context-menu-item delete"
            onClick={() => handleDeleteMessage(contextMenu.message.id)}
          >
            <FontAwesomeIcon icon={faTrash} />
            –£–¥–∞–ª–∏—Ç—å
          </button>
        </div>
      )}

      {/* –ú–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏—è */}
      {editingMessageId && (
        <div className="modal-overlay" onClick={() => setEditingMessageId(null)}>
          <div className="edit-modal" onClick={(e) => e.stopPropagation()}>
            <h3>–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å —Å–æ–æ–±—â–µ–Ω–∏–µ</h3>
            <textarea
              value={editText}
              onChange={(e) => setEditText(e.target.value)}
              className="edit-textarea"
              rows={3}
            />
            <div className="modal-buttons">
              <button
                className="cancel-button"
                onClick={() => setEditingMessageId(null)}
              >
                <FontAwesomeIcon icon={faTimes} />
                –û—Ç–º–µ–Ω–∞
              </button>
              <button
                className="save-button"
                onClick={handleEditMessage}
                disabled={!editText.trim()}
              >
                <FontAwesomeIcon icon={faCheck} />
                –°–æ—Ö—Ä–∞–Ω–∏—Ç—å
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ChatPage;
